// field of view of camera
#define FOV 0.001
// generate combined distance field
float map(vec3 p)
{
    vec3 q = p;
    q.xyz = mod(p.xyz,vec3(5.0))-2.5;

    // draw rounded box
    float boxSize = 2.;
	float d = box(q,vec3(boxSize), boxSize/3.);
    d = max(d,sphere(p,boxSize*10.));

	return d;
}

// raymarching procedure with lots of output info
void march(vec3 origin, vec3 direction, out vec3 rayHead, out bool hit, out float dist, out float steps)
{
    float distanceTraveled = 0.;
    hit = true;
    for(float step=0.; step<MARCH_STEPS; step++)
    {
        rayHead = origin + (direction*distanceTraveled);
        dist = map(rayHead);
        steps = step;
        if(dist<MARCH_EPSILON||step>=MARCH_STEPS) return;
        distanceTraveled += dist;
    }
    hit = false;
}

// get origin and direction of ray to be cast for camera/screen xy (pixel) coordinate
void cameraRay(vec2 scanLines, vec3 cameraPosition, vec3 target, float fov, out vec3 pos, out vec3 dir)
{
	vec3 forward = normalize(target-cameraPosition);
    vec3 up = vec3(0.,1.,0.);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(forward,right));

    right = right*scanLines.x*fov;
    up = up*scanLines.y*fov;

    pos = cameraPosition;
    dir = (right+up+forward);
}

// get surface normal of point on distance field for shading
vec3 gradient( vec3 pos )
{
	const vec3 dx = vec3( GRADIENT_STEP, 0.0, 0.0 );
	const vec3 dy = vec3( 0.0, GRADIENT_STEP, 0.0 );
	const vec3 dz = vec3( 0.0, 0.0, GRADIENT_STEP );
	return normalize(vec3(
		map( pos + dx ) - map( pos - dx ),
		map( pos + dy ) - map( pos - dy ),
		map( pos + dz ) - map( pos - dz )
	));
}

// calculate diffuse lighting for point on distance field given surface normal
float diffuse(vec3 normal, vec3 lightPosition)
{
	return max(dot(normal,normalize(lightPosition)),0.);
}

// calculate diffuse lighting for point on distance field
float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<int(SHADOW_STEPS); i++ )
    {
		float h = map( ro + rd*t );
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<SHADOW_EPSILON || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

// calculate combined lighting for point on distance field
float shadePoint(vec3 position, vec3 normal, vec3 lightPosition)
{
    float diffuseLight = diffuse(normal, lightPosition);
	// speculative light - it might be specular, but we're not really sure
    float specularLight = pow(diffuseLight,30.);
	float shadow = softshadow(position, normalize(lightPosition), 0.02, 2.25);
    return (diffuseLight+specularLight)*shadow;
}

// generate background color
vec3 background(vec2 uv)
{
	return vec3(0.5,0.5,0.5)*uv.y;
}

// compose image
void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    // scale coordinates to -1 -> +1, adjusted for screen aspect ratio
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv = uv*2.-1.;
    uv.x = uv.x*iResolution.x/iResolution.y;

    // get mouse coords
    vec2 mouse = iMouse.xy/iResolution.xy;

    // get ray representing camera scanline position and direction
    float cameraRotationSpeed = 0.2;
    vec3 cameraPosition = normalize(vec3(sin(iTime*cameraRotationSpeed),
                                         sin(iTime*cameraRotationSpeed),
                                         cos(iTime*cameraRotationSpeed)));
    if(iMouse.z>0.)
        cameraPosition = normalize(vec3(	sin(mouse.x*2.*M_PI),
                                        	(mouse.y*2.-1.),
                                        	cos(mouse.x*2.*M_PI)));
    cameraPosition *= 20000.;
    vec3 cameraOrigin;
    vec3 cameraDirection;
    cameraRay(uv, cameraPosition, vec3(0.,0.,0.), FOV, cameraOrigin, cameraDirection);

    // calculate distance, hit point, etc of distance field via raymarching
    vec3 rayHead;
    bool hit;
    float dist;
    float steps = 40.;
    march(cameraOrigin, cameraDirection, rayHead, hit, dist, steps);

    // calculate normal for point on distance field
    vec3 normal = gradient(rayHead);

    // calculate light for point
	vec3 rotLightPos = vec3(-0.5,-1.,0.);
    vec3 latRotLightPos = vec3(0.,-1.,-1.);
    vec3 lightPos = vec3(cos(iTime),sin(iTime),cos(iTime));
    float light = 0.;
    light += shadePoint(rayHead, normal, rotLightPos);
    light += shadePoint(rayHead, normal, latRotLightPos);
    light += shadePoint(rayHead, normal, lightPos);
    light /= 3.;

    // specify color of entire distance field
    vec3 fieldColor = vec3(1.0,0.4,0.4);

    // final steps and background
    vec3 color = fieldColor*light;
    if(!hit) color = background(uv);

    // set fragment shader color
    fragColor = vec4(color,1.);
}

// bunch of remix codes from iq, Shane, nimitz

const float pi = 3.141592;

float tri(in float x) {
	return abs(fract(x)-.5);
}

vec3 tri33(in vec3 x){return abs(x-floor(x)-.5);} 
float surfFunc(in vec3 p){
	return dot(tri33(p*0.5 + tri33(p*0.25).yzx), vec3(0.666));
}

float sdSphere(vec3 p, float r) {
	return length(p) - r;
}

float sdFloor(vec3 p) {
	return p.y + 0.4 * surfFunc(p + vec3(0.0, 0.0, iTime));
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float ra, float rb) {
	vec3 pa = p - a;
	vec3 ba = b - a;
	float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
	return length(pa - ba * h) - mix(ra, rb, h);
}

vec2 opU( vec2 d1, vec2 d2 ) {
	return (d1.x<d2.x) ? d1 : d2;
}

float smin( float a, float b, float k ) {
	float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
	return mix( b, a, h ) - k*h*(1.0-h);
}

vec2 smin( vec2 a, vec2 b, float k ) {
	float h = clamp( 0.5+0.5*(b.x-a.x)/k, 0.0, 1.0 );
	return mix( b, a, h ) - k*h*(1.0-h);
}

vec2 smaxP(vec2 a, vec2 b, float s){    
	float h = clamp( 0.5 + 0.5*(a.x-b.x)/s, 0., 1.);
	return mix(b, a, h) + h*(1.0-h)*s;
}

vec4 solve(vec2 p1, float r1, vec2 p2, float r2) {
	vec2 p = p2 - p1;
	float d = length(p);
	float k = (d * d + r1 * r1 - r2 * r2) / (2.0 * d);
	float s = sqrt(r1 * r1 - k * k);
	float x1 = p1.x + (p.x * k) / d + (p.y / d) * s;
	float y1 = p1.y + (p.y * k) / d - (p.x / d) * s;
	float x2 = p1.x + (p.x * k) / d - (p.y / d) * s;
	float y2 = p1.y + (p.y * k) / d + (p.x / d) * s;

	return vec4(x1, y1, x2, y2);
}

vec2 map(vec3 p) {
	vec2 d = vec2(100.0, 0.0);
	d = opU(d, vec2(sdFloor(p), 3.0));
	float s = d.x - p.y;
	p.y += s;
	float z = p.z;
	float phase = sin(z) * 3.14;
	float z0 = mod(p.z, 0.25) - 0.125;
    float z1 = mod(z - 0.25, 0.5) - 0.25;
    float z2 = mod(z, 0.5) - 0.25;
    p.z = smin(pow(z1 * z2, 0.6), z0, 0.1);
    p.z = pow(z1 * z2, 0.8);
    p.z = z0;
	float t = iTime * 10.0;
	vec3 center = vec3(0.1 * sin(z + iTime * 4.0), 0.5, 0.0);
	vec3 joint_r = vec3(-0.4, 0.4, 0.0); 
	vec3 joint_l = vec3(0.4, 0.4, 0.0);
	float h = iMouse.y / iResolution.y;
    h = iMouse.y == 0.0 ? 0.5 : h;
	vec3 foot_r = vec3(-0.5, h * (0.5 + 0.5 * sin(t + phase)), 0.0); 
	vec3 foot_l = vec3(0.5, h * (0.5 + 0.5 * sin(t + pi + phase)), 0.0);
	joint_r.xy = solve(center.xy, 0.4, foot_r.xy, 0.4).xy;
	joint_l.xy = solve(center.xy, 0.4, foot_l.xy, 0.4).zw;

	d = opU(d, vec2(sdCapsule(p, center, joint_l, 0.05, 0.02), 2.0));
	d = opU(d, vec2(sdCapsule(p, joint_l, foot_l, 0.02, 0.01), 2.0));
	d = opU(d, vec2(sdCapsule(p, center, joint_r, 0.05, 0.02), 2.0));
	d = opU(d, vec2(sdCapsule(p, joint_r, foot_r, 0.02, 0.01), 2.0));

    float d1 = sdSphere(vec3(p.xy, z1) - center, 0.1);
    float d2 = sdSphere(vec3(p.xy, z2) - center, 0.1);
    float body = smin(d1, d2, 0.2);
	d = smin(d, vec2(body, 2.2), 0.1);

	return d;
}

vec3 calcNormal(vec3 p) {
	vec2 e = vec2(-1.0, 1.0) * 0.001;
	return normalize(
		e.xyy * map(p + e.xyy).x +
		e.yxy * map(p + e.yxy).x + 
		e.yyx * map(p + e.yyx).x + 
		e.xxx * map(p + e.xxx).x
	);
}

float softshadow(vec3 ro, vec3 rd, float mint, float tmax ) {
	float res = 1.0;
	float t = mint;
	for( int i=0; i<16; i++ )
	{
		float h = map( ro + rd*t ).x;
		res = min( res, 32.0*h/t );
		t += clamp( h, 0.02, 0.10 );
		if( h<0.001 || t>tmax ) break;
	}
	return clamp( res, 0.0, 1.0 );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = ( fragCoord.xy - 0.5 * iResolution.xy ) / iResolution.y;
    vec2 mouse = iMouse.xy / iResolution.xy;
	vec3 ro = vec3((mouse.x - 0.5) * 10.0, 2.0, 6.0);
	vec3 ta = vec3(0.0, 0.0, 0.0);
	vec3 cw = normalize(ta - ro);
	vec3 up = vec3(0.0, 1.0, 0.0);
	vec3 cu = normalize(cross(cw, up));
	vec3 cv = normalize(cross(cu, cw));
	vec3 rd = normalize(p.x*cu + p.y*cv + 2.7*cw); 

	float precis = 0.001;
	float t = 0.0;
	float h = precis * 2.0;
	float m = 0.0;
	for(int i = 0; i < 120; i++) {
		if(abs(h) < precis || t > 20.0) continue;
		vec2 o = map(ro + rd * t);
		h = o.x;
		m = o.y;
		t += h;
	}

	vec3 c = vec3(0.0);
	vec3 col = vec3(0.0);
	if(h < precis) {
		vec3 pos = ro + rd * t;
		vec3 ld = vec3((mouse.x - 0.5)*20.0, 10.0, 5.0) - pos;
		vec3 lig = normalize(ld);
		vec3 nor = calcNormal(pos);
		float dif = clamp(dot(lig, nor)*length(ld)*0.1, 0.0, 1.0);
		float spe = pow(clamp(dot(reflect(lig, nor), rd), 0.0, 1.0), 64.0);
		float sh = softshadow(pos, lig, 0.01, 10.0);
		float fre = 1.0 - dot(-rd, nor);
		float fog = 1.0 - clamp(exp(-0.2 * (pos.z + 5.0)), 0.0, 1.0);
		float kk = m == 3.0 ? 0.0 : m;
		col = vec3(0.5 + 0.5 * sin(m * 0.1), 0.5 + 0.5 * sin(m * 2.0), kk * (0.5 + 0.5 * sin(pos.z * 0.5 + iTime)));
		c = 1.5 * col * (dif + spe + fre * 0.5) * (0.5 + 0.5 * sh) * fog;
	}

	fragColor = vec4( c, 1.0 );
}

