// Sinus bars function
vec3 calcSine(vec2 uv, float frequency, float amplitude, float shift, float offset, vec3 color, float width, float exponent)
{
    float y = sin(iTime * frequency + shift + uv.x) * amplitude + offset;
    float d = distance(y, uv.y);
    float scale = smoothstep(width, 0.0, distance(y, uv.y));
    return color * scale;
}

// Render the bars calling 3 CalcSines() and adding rgb componants
vec3 Bars(vec2 f)
{
    vec2 uv = f / iResolution.xy;
    vec3 color = vec3(0.0);
    color += calcSine(uv, 2.0, 0.25, 0.0, 0.5, vec3(0.0, 0.0, 1.0), 0.10, 3.0);
    color += calcSine(uv, 2.6, 0.15, 0.2, 0.5, vec3(0.0, 1.0, 0.0), 0.10, 1.0);
    color += calcSine(uv, 0.9, 0.35, 0.4, 0.5, vec3(1.0, 0.0, 0.0), 0.10, 1.0);
    return color;
}

// Classic iq twist function
vec3 Twist(vec3 p)
{
    float f = sin(iTime/3.)*1.45;
    float c = cos(f*p.y);
    float s = sin(f/2.*p.y);
    mat2  m = mat2(c,-s,s,c);
    return vec3(m*p.xz,p.y);
}

// The distance function which generate a rotating twisted rounded cube 
// and we save its pos into cubevec
float Cube( vec3 p )
{
    p=Twist(p);
    cubevec.x = sin(iTime);
    cubevec.y = cos(iTime);
    mat2 m = mat2( cubevec.y, -cubevec.x, cubevec.x, cubevec.y );
    p.xy *= m;p.xy *= m;p.yz *= m;p.zx *= m;p.zx *= m;p.zx *= m;
    cubevec = p;
    return length(max(abs(p)-vec3(0.4),0.0))-0.08;
}

// Split the face in 4 triangles zones
// return color index 0 or 1 if color1 or color2
float Face( vec2 uv )
{
        uv.y = mod( uv.y, 1.0 );
        return ( ( uv.y < uv.x ) != ( 1.0 - uv.y < uv.x ) ) ? 1.0 : 0.0;
}

//Classic iq normal
vec3 getNormal( in vec3 p )
{
    vec2 e = vec2(0.005, -0.005);
    return normalize(
        e.xyy * Cube(p + e.xyy) +
        e.yyx * Cube(p + e.yyx) +
        e.yxy * Cube(p + e.yxy) +
        e.xxx * Cube(p + e.xxx));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x = fragCoord.x;
    float pat = iTime*5.0;
    float Step = 1.0;
    float Distance = 0.0;
    float Near = -1.0;
    float Far = -1.0;
    vec3 lightPos = vec3(1.5, 0, 0);
    vec2 kp = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0*kp;
    vec4 m = iMouse / iResolution.xxxx;
    float hd=-1.;
    float ay=0.1;

    p.x *= iResolution.x / iResolution.y;
    vec3 ro = vec3( 0.0, 0.0, 2.1 );
    vec3 rd = normalize( vec3( p, -2. ) );
    for( int i = 0; i < 256; i++ )
        {
        	Step = Cube( ro + rd*Distance );
            Distance += Step*.5;

            if( Distance > 4.0 ) break;
            if( Step < 0.001 )
            	{
                 	Far = Face( cubevec.yx ) + Face( -cubevec.yx ) + Face( cubevec.xz ) + Face( -cubevec.xz ) + Face( cubevec.zy ) + Face( -cubevec.zy );
            		if(hd<0.) hd=Distance;
                    if( Near < 0.0 ) Near = Far;
            		if(m.z<=0.0) Distance += 0.05; else break;
                }
        }

    vec3 Color=Bars(fragCoord);

    if( Near > 0.0 )
    	{
            vec3 sp = ro + rd*hd;
        	vec3 ld = lightPos - sp;
            float lDist = max(length(ld), 0.001);
            ld /= lDist;
            float atten = 1./(1. + lDist*.2 + lDist*.1); 
            float ambience = 0.7;
            vec3 sn = getNormal( sp);
            float diff = min(0.3,max( dot(sn, ld), 0.0));
            float spec = pow(max( dot( reflect(-ld, sn), -rd ), 0.0 ), 32.);

            if(m.z<=0.) Color = Color/5. + mix( vec3( 0.2, 0.0, 1.0 ), vec3( 1.0, 1.0, 1.0 ), vec3( ( Near*0.45 + Far*Far*0.04 ) ) );
            else Color = mix( vec3( 0.2, 0.0, 1.0 ), vec3( 1.0, 1.0, 1.0 ), vec3( ( Near*0.45 + Far*Far*0.04 ) ) );
            Color = Color*(diff+ambience)+vec3(0.78,0.5,1.)*spec/1.5;
        }

    vec3 rainbow = vec3(0.5 + 0.5 * sin(kp.x*8. + 3.14 + pat), 0.5 + 0.5 * cos (kp.x*8. + pat), 0.5 + 0.5 * sin (kp.x*8. + pat));
    if (kp.y > ay && kp.y < ay+0.006 || kp.y > (1.-ay) && kp.y < 1.-ay+0.006 ) Color = rainbow;
    if(kp.y>1.-ay+0.006 || kp.y<0.1) Color=vec3(0.20,0.17,0.35);

    if(kp.y<=ay)
    	{
        vec3 cc = Scroller(fragCoord);
        if(cc!=vec3(0.5)) Color=rainbow;
        }


    // Presenting color to the screen
    fragColor = vec4( Color, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y*2.0-iResolution.xy/iResolution.y;

    float l = 0.0;
    vec2 rot = vec2(sin(iTime*0.1),cos(iTime*0.1));

    for(int i = 0; i < 256; i++) {

        uv = 1.1*(uv.x*vec2(rot.x,rot.y)+uv.y*vec2(-rot.y,rot.x));

        if(pow(0.05*uv.x, sin(uv.y)) > 12.0) {
            break;
        }
        l++;
    }

    fragColor = vec4(mod(l, 2.0));
}

vec2 complexExp(vec2 z, vec2 w){
   float l = length(z);
   float zsq = l*l;
   float argz = atan(z.y,z.x);
   float alpha = pow(zsq,0.5*w.x)* exp(-w.y*argz);
   float beta = w.x*argz + 0.5*w.y* log( zsq); 

   return alpha*vec2(cos(beta),sin(beta)) ; 
}


float mandelbrot(vec2 z,vec2 w){

  vec2 c = z;

  int n=1;
  const int   maxitn = 50;                              

    for( int i = 0;i<maxitn;i++){

        z = complexExp(z,w) + c;

        if(length(z) >= 2.0) {
            return float(i)/float(maxitn);
        }
    }

   return 0.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float aspectRatio = iResolution.x/iResolution.y;
    vec2 z = 2.0*uv + vec2(-1.0,-1.3);
    z.x = z.x*aspectRatio;
    z = vec2(z.y,z.x);


    vec2 w = vec2(4.0  + 3.0*cos(iTime),0.0);
    float t = mandelbrot(z,w);

    float a = cos(iTime);
    float b = sin(iTime);        
 	float c = cos(3.27*iTime);

	fragColor = vec4(pow(t,1.0 +0.3*a ),pow(t,1.0+0.3*b),pow(t,0.7+0.3*c),1.0);
}

// "Dux" by dr2 - 2016
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
float PrBox2Df (vec2 p, vec2 b);
float PrEllipsDf (vec3 p, vec3 r);
float PrEllCylDf (vec3 p, vec2 r, float h);
float SmoothBump (float lo, float hi, float w, float x);
float SmoothMin (float a, float b, float r);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);

const float pi = 3.14159;

vec3 qHit, sunDir;
vec2 gId, gIdHit;
float dstFar, tCur, dukHt, dukAyz, dukAxy;
int idObj;
const int idBdy = 1, idWng = 2, idHead = 3, idBk = 4;

vec3 SkyCol (vec3 ro, vec3 rd)
{
  float f;
  ro.xz += 0.5 * tCur;
  f = Fbm2 (0.1 * (ro + rd * (50. - ro.y) / rd.y).xz);
  return mix (vec3 (0.2, 0.3, 0.55) + 0.25 * pow (1. - max (rd.y, 0.), 8.) +
     0.35 * pow (max (dot (rd, sunDir), 0.), 16.),
     vec3 (0.85), clamp (0.8 * f * rd.y + 0.1, 0., 1.));
}

float WaveHt (vec2 p)
{
  vec2 u;
  u = 0.1 * (vec2 (p.y * cos (0.2 * pi)) +
     vec2 (p.x * sin (0.2 * pi)) * vec2 (-1., 1.));
  u = mod (u + vec2 (-0.1, 0.053) * tCur, 1.) - 0.5;
  u = exp (-100. * u * u);
  return 0.5 + (u.x + u.y) / (3. + 0.05 * dot (p, p));
}

float WaveRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, h, s, sLo, sHi;
  s = 0.;
  sLo = 0.;
  dHit = dstFar;
  for (int j = 0; j < 50; j ++) {
    p = ro + s * rd;
    h = p.y - WaveHt (p.xz);
    if (h < 0.) break;
    sLo = s;
    s += max (0.5, h);
    if (s > dstFar) break;
  }
  if (h < 0.) {
    sHi = s;
    for (int j = 0; j < 8; j ++) {
      s = 0.5 * (sLo + sHi);
      p = ro + s * rd;
      h = step (0., p.y - WaveHt (p.xz));
      sLo += h * (s - sLo);
      sHi += (1. - h) * (s - sHi);
    }
    dHit = sHi;
  }
  return dHit;
}

vec3 WaveNf (vec3 p)
{
  vec2 e = vec2 (0.01, 0.);
  float h;
  h = WaveHt (p.xz);
  return normalize (vec3 (h - WaveHt (p.xz + e.xy), e.x, h - WaveHt (p.xz + e.yx)));
}

float DukDf (vec3 p, float dMin)
{
  vec3 q;
  vec2 r, cs;
  float d, h, s;
  q = p;
  r = vec2 (0.04, 0.06 + 0.01 * clamp (q.z, -0.4, 0.4));
  h = 0.1;
  s = (length (q.xz / r) - 1.) * min (r.x, r.y);
  d = min (max (s, abs (q.y) - h), length (vec2 (s, q.y)) - h);
  if (d < dMin) { dMin = d;  idObj = idBdy;  qHit = q;  gIdHit = gId; }
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.1, 0.06, 0.12);
  cs = vec2 (cos (0.3), sin (0.3));
  q.yz = Rot2Cs (q.yz, cs);
  cs.y = - cs.y;
  q.xy = Rot2Cs (q.xy, cs);
  q.xz = Rot2Cs (q.xz, cs);
  q = q.yxz;
  r = vec2 (0.06, 0.1 + 0.016 * clamp (q.z, -0.4, 0.4));
  h = 0.014;
  s = (length (q.xz / r) - 1.) * min (r.x, r.y);
  d = min (max (s, abs (q.y) - h), length (vec2 (s, q.y)) - h);
  d = SmoothMin (d, dMin, 0.01);
  if (d < dMin) { dMin = d;  idObj = idWng;  qHit = q;  gIdHit = gId; }
  q = p;
  q.yz -= vec2 (0.15, -0.08);
  d = PrEllipsDf (q, vec3 (0.08, 0.07, 0.1));
  d = SmoothMin (d, dMin, 0.02);
  if (d < dMin) { dMin = d;  idObj = idHead;  qHit = q;  gIdHit = gId; }
  q = p;
  q.yz -= vec2 (0.14, -0.19);
  r = vec2 (0.03, 0.008);
  h = 0.02;
  d = max (PrEllCylDf (q, r, h),
      - PrEllCylDf (q - vec3 (0., 0., h), r - 0.004, 2. * h));
  if (d < dMin) { dMin = d;  idObj = idBk;  qHit = q;  gIdHit = gId; }
  return dMin;
}

float ObjDf (vec3 p)
{
  float bb = PrBox2Df (p.xz, vec2 (3.6));
  p.xz -= gId;
  p.y -= 0.05 + dukHt;
  p.yz = Rot2D (p.yz, -3. * dukAyz);
  p.xy = Rot2D (p.xy, 5. * dukAxy);
  return max (DukDf (p, dstFar), bb);
}

void EvalDukPa ()
{
  vec2 e = vec2 (0.1, 0.);
  dukHt = WaveHt (gId);
  dukAyz = WaveHt (gId - e.yx) - WaveHt (gId + e.yx);
  dukAxy = WaveHt (gId - e) - WaveHt (gId + e);
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 srd, dda, h, gIdP;
  float dHit, d;
  srd = - sign (rd.xz);
  dda = - srd / (rd.xz + 0.0001);
  gIdP = vec2 (-10.);
  dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    p = ro + dHit * rd;
    h = fract (dda * fract (srd * p.xz));
    gId = floor (p.xz + 0.5);
    if (abs (gId.x) <= 4. && abs (gId.y) <= 4.) {
      if (gId.x != gIdP.x || gId.y != gIdP.y) {
        EvalDukPa ();
        gIdP = gId;
      }
      d = ObjDf (p);
    } else d = dstFar;
    dHit += min (d, 0.2 + max (0., min (h.x, h.y)));
    if (d < 0.001 || dHit > dstFar) break;
  }
  if (d >= 0.001) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy),
     ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * vec3 (v.y, v.z, v.w));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = 0; j < 20; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += max (0.05, 0.1 * d);
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 ObjCol ()
{
  vec3 col, cBdy;
  float s;
  cBdy = (mod (gIdHit.x, 2.) == mod (gIdHit.y, 2.)) ?
     vec3 (1., 0., 0.) : vec3 (1., 1., 0.);
  if (idObj == idBdy) {
    col = cBdy * (1. - smoothstep (0.02, 0.06, qHit.y) *
       smoothstep (0., 0.14, qHit.z) * 0.1 * SmoothBump (0.3, 0.5, 0.05,
       mod (50. * qHit.x, 1.)));
  } else if (idObj == idWng) {
    col = cBdy * (1. - step (0.004, qHit.y) *
       smoothstep (0., 0.04, qHit.z) * 0.2 * SmoothBump (0.3, 0.5, 0.05,
       mod (100. * qHit.x, 1.)));
  } else if (idObj == idHead) {
    s = length (qHit.yz - vec2 (0.02, -0.05));
    if (s > 0.02) col = cBdy;
    else col = (abs (s - 0.01) < 0.003) ? vec3 (1., 1., 1.) : vec3 (0.3, 0.3, 1.);
  } else if (idObj == idBk) col = vec3 (1., 0.5, 0.);
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, colW, vn;
  float dstObj, dstWat, sh;
  bool waterRefl;
  dstWat = WaveRay (ro, rd);
  dstObj = ObjRay (ro, rd);
  waterRefl = (dstWat < min (dstFar, dstObj));
  if (waterRefl) {
    ro += rd * dstWat;
    vn = WaveNf (ro);
    vn = VaryNf (10. * ro, vn, 0.2);
    colW = vec3 (0., 0.2, 0.3) * (0.4 + 0.6 * max (dot (vn, sunDir), 0.));
    rd = reflect (rd, vn);
    dstObj = ObjRay (ro, rd);
  }
  if (dstObj < dstFar) {
    col = ObjCol ();
    ro += rd * dstObj;
    vn = ObjNf (ro);
    sh = ObjSShadow (ro, sunDir);
    col = col * (0.4 + 0.6 * sh * max (dot (vn, sunDir), 0.)) +
       0.1 * sh * pow (max (0., dot (sunDir, reflect (rd, vn))), 64.);
  } else col = SkyCol (ro, rd);
  if (waterRefl) col = mix (colW, col, 0.3);
  col = pow (clamp (col, 0., 1.), vec3 (0.8));
  return col;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float az, el;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  az = -0.03 * pi * tCur;
  el = -0.15 * pi;
  el = clamp (el, -0.45 * pi, -0.02 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
     mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -9.);
  rd = vuMat * normalize (vec3 (uv, 4.));
  dstFar = 20.;
  sunDir = normalize (vec3 (1., 0.8, -0.8));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrEllipsDf (vec3 p, vec3 r)
{
  return (length (p / r) - 1.) * min (r.x, min (r.y, r.z));
}

float PrEllCylDf (vec3 p, vec2 r, float h)
{
  return max ((length (p.xy / r) - 1.) * min (r.x, r.y), abs (p.z) - h);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const vec4 cHashA4 = vec4 (0., 1., 57., 58.);
const vec3 cHashA3 = vec3 (1., 57., 113.);
const float cHashM = 43758.54;

vec4 Hashv4f (float p)
{
  return fract (sin (p + cHashA4) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec4 t;
  vec2 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv4f (dot (ip, cHashA3.xy));
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f;
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  float s;
  const vec3 e = vec3 (0.1, 0., 0.);
  s = Fbmn (p, n);
  g = vec3 (Fbmn (p + e.xyy, n) - s, Fbmn (p + e.yxy, n) - s,
     Fbmn (p + e.yyx, n) - s);
  return normalize (n + f * (g - n * dot (n, g)));
}

// Created by Stephane Cuillerdier - Aiekick/2016 (twitter:@aiekick)
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// Tuned via XShade (http://www.funparadigm.com/xshade/)

mat3 RotZ(float a){return mat3(cos(a),-sin(a),0.,sin(a),cos(a),0.,0.,0.,1.);}

vec3 path(vec3 p)
{
	p *= RotZ(p.z * 0.1);
    p += sin(p.zxy * 0.5) * 0.5;
	p *= RotZ(p.z * 0.2);
   	return sin(p.zxy * 0.2) * 2.;
}
float df(vec3 p)
{
	p += path(p);
	p *= RotZ(p.z * 0.1);
    return 1. - cos(p.z*1.5) * 0.1 - abs(p.y) + dot(texture(iChannel0, p.xz*0.2),vec4(0.05));
}

vec3 nor( vec3 pos, float prec )
{
	vec3 eps = vec3( prec, 0., 0. );
	vec3 nor = vec3(
	    df(pos+eps.xyy) - df(pos-eps.xyy),
	    df(pos+eps.yxy) - df(pos-eps.yxy),
	    df(pos+eps.yyx) - df(pos-eps.yyx) );
	return normalize(nor);
}

// return color from temperature 
//http://www.physics.sfasu.edu/astro/color/blackbody.html
//http://www.vendian.org/mncharity/dir3/blackbody/
//http://www.vendian.org/mncharity/dir3/blackbody/UnstableURLs/bbr_color.html
vec3 blackbody(float Temp)
{
	vec3 col = vec3(255.);
    col.x = 56100000. * pow(Temp,(-3. / 2.)) + 148.;
   	col.y = 100.04 * log(Temp) - 623.6;
   	if (Temp > 6500.) col.y = 35200000. * pow(Temp,(-3. / 2.)) + 184.;
   	col.z = 194.18 * log(Temp) - 1448.6;
   	col = clamp(col, 0., 255.)/255.;
    if (Temp < 1000.) col *= Temp/1000.;
   	return col;
}

// get density of the df at surfPoint
// ratio between constant step and df value
float SubDensity(vec3 surfPoint, float prec, float ms) 
{
	vec3 n;
	float s = 0.;
    const int iter = 10;
	for (int i=0;i<iter;i++)
	{
		n = nor(surfPoint,prec); 
		surfPoint = surfPoint - n * ms; 
		s += df(surfPoint);
	}

	return 1.-s/(ms*float(iter)); // s < 0. => inside df
}

float SubDensity(vec3 p, float s) 
{
	vec3 n = nor(p,s); 							// precise normale at surf point
	return df(p - n * s);						// ratio between df step and constant step
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 g = fragCoord;
	vec2 si = iResolution.xy;
	vec2 uv = (g+g-si)/si.y;
	vec3 ro = vec3(0,0, iTime * 6.); ro -= path(ro);
	vec3 cv = ro + vec3(0,0,4); cv -= path(cv);
	vec3 cu = normalize(vec3(0,1,0));
  	vec3 z = normalize(cv-ro);
    vec3 x = normalize(cross(cu,z));
  	vec3 y = cross(z,x);
  	vec3 rd = normalize(uv.x * x + uv.y * y + z);

	float s = 1., d = 0.;
	for (int i=0; i<30; i++) // 30 iterations yeah :)
	{
		if (log(d/1e6)>0.) break; // due to this special break condition
		d += df(ro+rd*d);
	}

	vec3 p = ro + rd * d;											// surface point
	vec3 ld = normalize(ro-p); 										// light dir
	vec3 n = nor(p, 0.001);											// normal at surface point
	vec3 refl = reflect(rd,n);										// reflected ray dir at surf point 
	float diff = clamp( dot( n, ld ), 0.0, 1.0 ); 					// diffuse
	float fre = pow( clamp( 1. + dot(n,rd),0.0,1.0), 4. ); 			// fresnel
	float spe = pow(clamp( dot( refl, ld ), 0.0, 1.0 ),16.);		// specular
	vec3 col = vec3(.8,.5,.2);

    // here the magix happens
	float sss = df(p - n*0.001)/0.01;								// quick sss 0.001 of subsurface

	float sb = SubDensity(p, 0.01, 0.1);							// deep subdensity from 0.01 to 0.1 (10 iterations)
	vec3 bb = blackbody(200. * sb);									// blackbody color
	float sss2 = 0.8 - SubDensity(p, 3.); 							// one step sub density of df of 3 of subsurface

	vec3 a = (diff + fre + bb * sss2 * .8 + col * sss * .2) * 0.35 + spe; // near
    vec3 b = col * sss;

	fragColor.rgb = mix(a, b, .8-exp(-0.01*d*d));
}

// clamp( I0/dist^2*Bessel(r) ) ; Bessel ~ 1/r^3 
// ->   pix=1 => r = I0^1/3 / dist^2/3
// graph: see https://www.desmos.com/calculator/6m7cebockb
// from star shader https://www.shadertoy.com/view/Xty3zc


#define rnd(x)  fract( 4356.17 * sin( 1e4*x ) )
#define srnd(x) ( 2.* rnd(x) -1. )

void mainImage( out vec4 O, vec2 U )
{
    float r, t = iTime,t1,v;
    vec2 R = iResolution.xy, P;
    U =  (2.*U-R) / R.y;

    O -= O;

  if (abs(U.x)<1.25*cos(1.2*(.5+.5*U.y))) // clamp out of the flame profile
    for(float i=0.; i<3.*256.; i++) {
       t1 = t + 6.28*rnd(i);
        v = 2.+srnd(i+.7);
        r = cos(1.4*fract(t1)) * (.2+rnd(i+.1));    
        P = vec2(   cos(t1*v)*r ,          // particle location
                 .2*sin (t1*v)*r + 2.*pow(fract(t1),1.5)-1.   ), 
        r = length(P-U),
	    O += U.x<0. 
             ? 1e-4 / pow(r,3.)            // pix = clamp ( Emit/dist^2 * Bessel )
             : smoothstep(1.,.8, r*20.);   // pix = disk ( R/dist )
    }

    O *= vec4(1,.5,.2,1);                  // color  

  //O += .3*texture(iChannel0,vec3(-U,-1));
}

float sdPlane( vec3 p )
{
	return p.y;
}
float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0))-r;
}
float sdHexPrism( vec3 p, vec2 h )
{
    vec3 q = abs(p);
    return max(q.z-h.y,max((q.x*0.866025+q.y*0.5),q.y)-h.x);
}
float sdSphere( vec3 p, float s )
{
    return length(p)-s;
}
mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}
vec3 opTwist( vec3 p )
{
    float  c = cos(10.0*p.y+10.0);
    float  s = sin(10.0*p.y+10.0);
    mat2   m = mat2(c,-s,s,c);
    return vec3(m*p.xz,p.y);
}
float blend (float a, float b, float k) {
    float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
    return mix(b,a,h) - k*h*(1.0-h);
}
float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

vec2 opU( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}
vec3 opRep( vec3 p, vec3 c )
{
    return mod(p,c)-0.5*c;
}
float scDir(float p, float s)
{
    vec3 x = vec3(p);
    return length(cross(sin(x), cos(x))-cross(asin(x),acos(x)))-s;
}
float snDir(float p, float s)
{
    vec3 x = normalize(vec3(p));
    vec2 y = vec2(p);
    return scDir(length(x)-s, s);
}
float displacement(vec3 p)
{
    return sin(20.*p.x)*sin(20.*p.y)*sin(20.*p.z);
}
float opDisplace(vec3 p, float d1)
{
    float d2 = displacement(p);
    return d1+d2;
}
vec2 blend(vec2 a, vec2 b, float k)
{
    return max(vec2(blend(a.x,b.x,k), a.y),vec2(blend(a.x,b.x,k), b.y));
}
float sdPlane2( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}
float sdCappedCone( in vec3 p, in vec3 c )
{
    vec2 q = vec2( length(p.xz), p.y );
    vec2 v = vec2( c.z*c.y/c.x, -c.z );
    vec2 w = v - q;
    vec2 vv = vec2( dot(v,v), v.x*v.x );
    vec2 qv = vec2( dot(v,w), v.x*w.x );
    vec2 d = max(qv,0.0)*qv/vv;
    return sqrt( dot(w,w) - max(d.x,d.y) ) * sign(max(q.y*v.x-q.x*v.y,w.y));
}
vec2 map( vec3 pos)
{
    float roundness = .15;
  	float rot = 1.;
	float k = .05;
    float size = .5;

    float scale = 1. / ( 3. - clamp( pos.y, 1., 7. ) );
    scale = 1.;

    vec2 x;
    float h = 3.5;
    vec3 p = vec3(pos.x, pos.y-1., pos.z);;
    p = vec3(pos.x, pos.y, pos.z);
    float d1;
    float pl = sdPlane(p);
    x = vec2(sdPlane2(p, normalize(vec4(-0.9, 1., 1., 1.6))),1.0);
    p = vec3(pos.x, pos.y, pos.z);
    p = vec3(pos.x, pos.y-0.8, pos.z);
    d1 = sdBox(p, vec3(1.9, 0.1, 2.5));
    x = blend(vec2(d1, 1.1),x,0.1);
    p = vec3(pos.x, pos.y-0.4, pos.z+9.5);
    d1 = sdSphere(p, 5.);
    x = blend(vec2(d1, 1.2),x,2.);
    p = vec3(pos.x-12.125, pos.y+1.5, pos.z+3.);
    d1 = sdSphere(p, 9.);
    x = blend(vec2(d1, 1.2),x,2.);
    p = vec3(pos.x, pos.y-0.8, pos.z);
    d1 = sdBox(p, vec3(0.475, clamp(pos.y,-0.6,3.7), 0.625));
    d1 = opU(vec2(opDisplace(p, d1)),vec2(d1)).x;
    x = blend(vec2(d1, 0.9),x,0.1);
    p = vec3(pos.x, pos.y+1.5, pos.z);
    d1 = sdCappedCone(p, vec3(-3., 0.1, 0.0));
    //x = blend(vec2(d1, 1.2),x,2.);
    return x;

}
vec2 castRay( in vec3 ro, in vec3 rd )
{
    float tmin = 1.0;
    float tmax = 20.0;

#if 0
    float tp1 = (0.0-ro.y)/rd.y; if( tp1>0.0 ) tmax = min( tmax, tp1 );
    float tp2 = (1.6-ro.y)/rd.y; if( tp2>0.0 ) { if( ro.y>1.6 ) tmin = max( tmin, tp2 );
                                                 else           tmax = min( tmax, tp2 ); }
#endif

	float precis = 0.002;
    float t = tmin;
    float m = -1.0;
    for( int i=0; i<50; i++ )
    {
	    vec2 res = map( ro+rd*t );
        if( res.x<precis || t>tmax ) break;
        t += res.x;
	    m = res.y;
    }

    if( t>tmax ) m=-1.0;
    return vec2( t, m );
}
float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );

}

vec3 calcNormal( in vec3 pos )
{
	vec3 eps = vec3( 0.001, 0.0, 0. );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

vec3 render( in vec3 ro, in vec3 rd )
{ 
    vec3 col = vec3(0.7, 0.9, 1.0) +rd.y*0.8;
    vec2 res = castRay(ro,rd);
    float t = res.x;
	float m = res.y;
    if( m>-0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos );
        vec3 ref = reflect( rd, nor );

        // material        
		col = 0.45 + 0.3*sin( vec3(0.05,0.08,0.10)*(m-1.0) );

        if( m==1.0 )
        {

            float f = 1.;
            col = 0.4 + 0.1*f*vec3(1.0)+texture(iChannel0, vec2(pos.x,pos.z)).rgb-vec3(1.0,0.0,1.0);
        }
        else if( m==1.1 )
        {
            float f = mod( floor(5.0*pos.z) + floor(5.0*pos.x), 2.0);
            col = 0.4 + 0.1*f*vec3(1.0)+texture(iChannel1, vec2(pos.x,pos.z)).rgb;
        }
        else if( m==1.2 )
        {
            col = 0.4*0.1*vec3(1.)+texture(iChannel1, vec2(pos.x,pos.z)).rgb+vec3(1.);
        }
        else if( m==0.9)
        {
            col = 0.4+0.1*vec3(1.)+texture(iChannel2, vec2(nor.x,nor.z)).rgb;
        }

        // lighitng        
        float occ = calcAO( pos, nor );
		vec3  lig = normalize( vec3(-0.6, 0.7, -0.5) );
		float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
        float dom = smoothstep( -0.1, 0.1, ref.y );
        float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );
		float spe = pow(clamp( dot( ref, lig ), 0.0, 1.0 ),16.0);

        dif *= softshadow( pos, lig, 0.02, 2.5 );
        dom *= softshadow( pos, ref, 0.02, 2.5 );

		vec3 lin = vec3(0.0);
        lin += 1.20*dif*vec3(1.00,0.85,0.55);
		lin += 1.20*spe*vec3(1.00,0.85,0.55)*dif;
        lin += 0.20*amb*vec3(0.50,0.70,1.00)*occ;
        lin += 0.30*dom*vec3(0.50,0.70,1.00)*occ;
        lin += 0.30*bac*vec3(0.25,0.25,0.25)*occ;
        lin += 0.40*fre*vec3(1.00,1.00,1.00)*occ;
		col = col*lin;

    	col = mix( col, vec3(0.8,0.9,1.0), 1.0-exp( -0.002*t*t ) );

    }

	return vec3( clamp(col,0.0,1.0) );
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy/iResolution.xy;
    vec2 p = -1.0+2.0*q;
	p.x *= iResolution.x/iResolution.y;
    vec2 mo = iMouse.xy/iResolution.xy;

	float time = 15.0 + iTime;

	// camera	
	vec3 ro = vec3( 5.*cos(0.1*time + 6.0*mo.x), 1.0 + 2.0*mo.y+5.,5.*sin(0.1*time + 6.0*mo.x) );
	vec3 ta = vec3( 0., 2., 0. );

	// camera-to-world transformation
    mat3 ca = setCamera( ro, ta, sin(0.5*time + 6.0*mo.x) );
    // ray direction
	vec3 rd = ca * normalize( vec3(p.xy,2.0) );
    if(normalize(rd)==vec3(.9))
    {
        rd=vec3(0.);
    }
    // render	
    vec3 col = render( ro, rd );

	col = pow( col, vec3(0.4545) );

    fragColor=vec4( col, 1.0 );
}

