const vec3 point1 = vec3(0.9428090453147888,-0.3333333432674408,0);
const vec3 point2 = vec3(-0.4714045226573944,-0.3333333432674408,-0.8164966106414795);
const vec3 point3 = vec3(-0.4714045226573944,-0.3333333432674408,0.8164966106414795);

float planedist(in vec3 point, in vec3 norm) {

    return dot(point, norm) - norm.y;

}

const float size = 3.0;
float tet(in vec3 point) {

    point = point/size;

    // subtracting the planes from a sphere
    float dist = max4(vec4(
        planedist(point, norm1),
        planedist(point, norm2),
        planedist(point, norm3),
        -(1.0/3.0+point.y)
    ));

    // the sphere has a `size` radius
    dist = max(length(point)-1.0, dist)*size;

    return dist;

}

/***************

Tetrahedron end

***************/

float DE(in vec3 pos) {

    float rot = (iTime*0.25)*PI*2.0;

    pos = rotateX(pos, rot);
    pos = rotateY(pos, rot);
    pos = rotateZ(pos, rot*0.5);

    // folding
    for(int i=0; i<10; i++) {

        if(pos.x+pos.y<0.0){pos.xy=-pos.yx;}

        pos = rotateY(pos,  0.95);

        if(pos.x+pos.z<0.0){pos.xz=-pos.zx;}

        pos = rotateZ(pos, -0.7);

        if(pos.y+pos.z<0.0){pos.yz=-pos.zy;}

    }

    return max(
        tet(pos),
        -(length(pos)-1.7)
    );
}

const vec3 shadowColor = vec3(0.1, 0.0, 0.0);
const vec3 backColor = vec3(0.4, 0.4, 0.5);
const vec3 oColor = vec3(0.2, 0.4, 0.75);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // camera
	vec2 view = fragCoord.xy / iResolution.xy * 2.0 - 1.0;

    float aspect = iResolution.x/iResolution.y;
    float a = tan(FOV * (PI/180.0) / 2.0);
    vec2 ab = vec2(a * aspect, a);

    // the start point, and the direction of the ray
    vec3 dir = normalize(vec3(ab*view, -1.0));
    vec3 point = vec3(0.0, 0.0, 5.0+sin(iTime)*1.0);

    // ray-march
    float closest = infinity;

    for(int i = 0; i<MAX_STEP; i++) {

        vec3 pos = point;

        float dist = DE(pos);

        if(dist < 0.0025) {
            fragColor = vec4(mix(
                oColor,
                shadowColor,
                sqrt(float(i)/MAX_STEP_F)
            ), 1.0);
            return;
        }

        closest = min(closest, dist);

        point += dir*dist;

    }

    // background shading
    fragColor = vec4(mix(
        shadowColor,
        backColor,
        sqrt(clamp(closest/2.0, 0.0, 1.0))
    ), 1.0);

}

