//The actual number of sides each polygon has in the shadertoy
#define SIDE_COUNT 4
//---------------------------------
//CHANGE THE ABOVE VALUE FOR FUN!
//---------------------------------

//The maximum number of sides the polygon can have.
//Necessary because loop counters can't be compared to dynamic numbers as the break condition.
//Weird things happen if this is lower than SIDE_COUNT
#define MAX_POLY_SIDE_COUNT 16

//Pi. Does this really need explanation?
#define PI 3.14159265358979323846264338

//Used to rotate things.
//Returns a matrix that'll rotate a vector2 by angle radians about the origin.
mat2 rotation(float angle){
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s
               ,s,  c);
}

//Returns 1.0 when in the bounding box given
float inBB(vec2 r, vec2 p1, vec2 p2){
    if(((r.x<p1.x && r.x<p2.x)||(r.x>p1.x && r.x>p2.x))
       ||(r.y<p1.y && r.y<p2.y)||(r.y>p1.y && r.y>p2.y))
       return 0.0;
    else
       return 1.0;
}

//Returns 1.0 when in the _rectangle_ given
float smoothProperRect(vec2 r, vec2 topLeft, vec2 botRight, float edgeBlur){
    float ret;
    ret  = smoothstep(topLeft.x-edgeBlur, topLeft.x+edgeBlur, r.x);
    ret *= smoothstep(topLeft.y-edgeBlur, topLeft.y+edgeBlur, r.y);
    ret *= 1.0 - smoothstep(botRight.y-edgeBlur, botRight.y+edgeBlur, r.y);
    ret *= 1.0 - smoothstep(botRight.x-edgeBlur, botRight.x+edgeBlur, r.x);
    return ret;
}//Does the same, but doesn't require that you set the edge blur.
float smoothProperRect(vec2 r, vec2 topLeft, vec2 botRight){
    return smoothProperRect(r,topLeft,botRight,0.005);
}

//Returns 1.0 at the very center of the given circle, dropping linearly to the edge.
float smoothPointBloom(vec2 r, vec2 center, float radius){
    return 1.0-smoothstep(0.0,radius,length(center-r));
}

//Returns 1.0 when on the line given
float line(vec2 r, vec2 p1, vec2 p2, float lineWidth){
    vec2 midpointRSpace = (p1.xy+p2.xy)/2.0;

    float angle = atan((p1.y-p2.y)/(p1.x-p2.x));

    vec2 rotatedSpaceCord = (r-midpointRSpace)*rotation(-angle);

    float lineLength = length(p1-p2);

    return smoothProperRect(
        rotatedSpaceCord,
        vec2(-lineLength/2.0,-lineWidth/2.0),
        vec2(lineLength/2.0,lineWidth/2.0),
        lineWidth
    );
}

//Returns 1.0 when on the described polygon's edge.
float polygonWireframe(vec2 r, vec2 center, float radius, int sideCount, float lineWidth){
    //If we're outside any possible radius, break.
    if(length(r.xy-center.xy)>radius+lineWidth) return 0.0;
    //If the side count makes no sense, break.
    if(sideCount<1) return 0.0;

    float ret = 0.0;

    //Point radius length up from center:
    vec2 p0 = vec2(0.0, radius);
    //Angle of rotation swept by each edge:
    float angle = 2.0*PI/float(sideCount);

    //Loop all possible sides
    for(int i=0;i<MAX_POLY_SIDE_COUNT;i++){
        //Loop all given sides
        if(i>=sideCount) break;
        vec2 p1 = p0*rotation(angle*float(i));
        vec2 p2 = p1*rotation(angle);
        ret += line(r, p1+center, p2+center, lineWidth);
    }
    return ret;
}

//Returns 1.0 if the given point is on one of the lines of the described polygons.
//Used to render polygons at each tip of a given polygon.
float polyPointedPoly(vec2 r, vec2 center, float radius, int sideCount, float lineWidth){
    if(length(r.xy-center.xy)>radius+(radius*2.0/float(sideCount))+lineWidth) return 0.0;

    if(sideCount < 1) return 0.0;

    float ret = 0.0;

    vec2 p0 = vec2(0.0, radius);
    float angle = 2.0*PI/float(sideCount);

    for(int i=0; i<MAX_POLY_SIDE_COUNT;i++){
        if(i>=sideCount) break;
        vec2 p1 = p0*rotation(angle*float(i));
        ret += polygonWireframe(r, center+p1, radius*2.0/float(sideCount), sideCount, lineWidth);
    }
    return ret;
}

//Returns a vector 2 representing the position of this polygon's sideNumth vertex.
vec2 getPolyPointN(vec2 center, float radius, int sideCount, int sideNum){
    vec2 p0 = vec2(0.0, radius);
    float angle = 2.0*PI/float(sideCount);
    return center + p0*rotation(angle*float(sideNum));
}

//Returns a vector 2 representing the position of this polygon's sideNumth point,
//adjusted for time to linearly phase toward the next side number.
//Valid time values are 0.0 to 1.0
vec2 getPolyPointNTimeAdjust(vec2 center, float radius, int sideCount, int sideNum, float time){
    vec2 p0 = getPolyPointN(center, radius, sideCount, sideNum);
    vec2 p1 = getPolyPointN(center, radius, sideCount, sideNum + 1);
    return p0*time+p1*(1.0-time);
}

//Returns a vector2 representing the position of the sideNumth point of the polyNumth polygon,
//adjusted for time to linearly phase toward the next side number.
//Valid time values are 0.0 to 1.0
vec2 getPolyOfPolysPointNTimeAdjust(vec2 center, float radius, int sideCount, int polyNum, int sideNum, float time){
    vec2 p0 = vec2(0.0, radius);
    float angle = 2.0*PI/float(sideCount);
    vec2 p1 = p0*rotation(angle*float(polyNum));
    return center + getPolyPointNTimeAdjust(p1, radius*2.0/float(sideCount), sideCount, sideNum, time);
}

//Main image, GLSL entry point
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Define our grid space
    vec2 p = vec2(fragCoord.xy / iResolution.xy);
    //Make an aspect ratio out of it and align the origin to the center of the screen
    vec2 r = 1.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/(iResolution.y);

    //The current time, in two second loops.
    float time = mod(iTime,2.0)/2.0;

    //A list of colors. Have fun messing with these!
    const vec3 bgColor = vec3(0.25,0.0,0.3);
    const vec3 polyColor = vec3(5.0,0.0,5.0);
    const vec3 lineColor = vec3(1.0,0.99,1.0);
    const vec3 capColor = vec3(1.0);

    const vec2 center = vec2(0.0);
    const float radius = 0.3;

    vec3 ret = bgColor;
    ret = mix(ret,polyColor,polyPointedPoly(r, center, radius, SIDE_COUNT, 0.001));
    for(int side=0; side<MAX_POLY_SIDE_COUNT; side++){
        if(side>=SIDE_COUNT) break;

        for(int poly=0; poly<MAX_POLY_SIDE_COUNT; poly++){
            if(poly>=SIDE_COUNT) break;

            vec2 p0 = getPolyOfPolysPointNTimeAdjust(center, radius, SIDE_COUNT, poly, side, time);
            vec2 p1 = getPolyOfPolysPointNTimeAdjust(center, radius, SIDE_COUNT, poly+1, side+1, time);
            ret = mix(ret, lineColor, line(r, p0, p1, 0.002));
            ret = mix(ret, capColor, smoothPointBloom(r, p0, 0.01));
        }
    }
	fragColor = vec4(ret,1.0);
}

