// Shader inspired by the Numberphile episode "Epic Circles"
// https://www.youtube.com/watch?v=sG_6nlMZ8f4
//
#define ANIMATE			// Funky animation
#define FILL_OUTSIDE	// Fill outside of unit circle

// Here we build the Pappus chain inside the unit circle
// We notice the Pappus chain's circles, once projected using circle inversion based on a unit circle
//	centered in (0,-1) give an infinite series of circles aligned within the band V[-0.5,0.0]
// By projecting each UV within the unit circle and retrieving its position within the band,
//	we can know which circle it belongs to...
//
vec3 ComputeInnerColor( vec2 _uv, float _radius ) {

    // Invert using circle located at the bottom of our unit circle
    #ifdef ANIMATE
		vec2	inversionCircleCenter = vec2( 0.5*cos(iTime), -0.5+0.5*sin(iTime) );
    #else
    	vec2	inversionCircleCenter = vec2( 0.0, -1.0 );
    #endif

    vec2	relativeUV = _uv - inversionCircleCenter;
    float	dist2Center = length( relativeUV );
    vec2	UV = relativeUV / (dist2Center * dist2Center);

    // Isolate main circles of radius 0.25 along the V=0.75 line
    vec2	circlesUV0 = 2.0 * vec2( UV.x, UV.y - 0.75 );
			circlesUV0.x = fract( 0.5 + circlesUV0.x ) - 0.5;
    float	dist2Circles0 = 2.0 * length( circlesUV0 );

    // Isolate secondary circles of radius 0.0625 along the V=1-0.0625 and V=0.5+0.0625 lines
	vec2	circlesUV1 = 2.0 * vec2( UV.x + 0.25, UV.y - 1.0 + 0.0625 );
    float	circleIndex1 = floor( circlesUV1.x );
			circlesUV1.x = fract( 0.5 + circlesUV1.x ) - 0.5;
    float	dist2Circles1 = 8.0 * length( circlesUV1 );

	vec2	circlesUV2 = 2.0 * vec2( UV.x + 0.25, UV.y - 0.5 - 0.0625 );
    float	circleIndex2 = floor( circlesUV2.x );
			circlesUV2.x = fract( 0.5 + circlesUV2.x ) - 0.5;
    float	dist2Circles2 = 8.0 * length( circlesUV2 );


	if ( dist2Circles2 < 1.0 )
    	return vec3( 1, fract( 0.5 + 0.23 * abs(circleIndex2) ), 0.25 );
	else if ( dist2Circles1 < 1.0 )
    	return vec3( 0, 0.1 + fract( 0.4 * abs(circleIndex1) ), 1 );
	else if ( dist2Circles0 < 1.0 )
    	return vec3( dist2Circles0 );

	return vec3( 0.25 * dist2Center );
}

// For the points outside our unit circle, we simply reproject them inside the circle and sample its color there...
// We know that d*D = 1 and we're given D outside of the unit circle
// We simply find d = 1/D, the new distance inside the circle where to sample the inner color...
vec3 ComputeOuterColor( vec2 _uv, float D ) {
    #ifdef FILL_OUTSIDE
	    float	d = 1.0 / D;
		vec2	insideUV = _uv * d*d;
    	return ComputeInnerColor( insideUV, d );
    #else
    	return vec3( 0.0 );
    #endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    #ifdef ANIMATE
        float	rotationAngle = -iTime;
    #else
        float	rotationAngle = 0.0;
    #endif
    vec2	rot = vec2( cos(rotationAngle), sin(rotationAngle) );

	vec2	uv = vec2( (iResolution.x / iResolution.y) * (2.0 * fragCoord.x / iResolution.x - 1.0),
                      									  2.0 * fragCoord.y / iResolution.y - 1.0 );
	   		uv = vec2( uv.x * rot + uv.y * vec2( -rot.y, rot.x ) );

    #ifdef ANIMATE
		uv *= 2.0 + 1.0 * sin( iTime );
    #endif

    float	radius = length( uv );

    fragColor = vec4( radius < 1.0 ? ComputeInnerColor( uv, radius ) : ComputeOuterColor( uv, radius ), 1.0 );
}

