vec3 rayPoint(Ray r,float t) {
 	return r.origin +  t*r.direction;
}

bool checker3D(vec3 p)
{
    //Is this point in a white cube or a black cube in a 3d checkerboard? 
    p = floor(p);

    if (mod(p.x + p.y + p.z, 2.0)>0.0){
        return true;
    }
    else
    {
        return false;
    }
}

MapValue cube( vec3 p, float d , Material m)
{
  MapValue mv;
  mv.material = m;
  mv.signedDistance = length(max(abs(p) -d,0.0));
  return mv; 
}

MapValue torus( vec3 p, vec2 t ,Material m)
{
  MapValue mv;
  mv.material = m;

   vec2 q = vec2(length(p.xz)-t.x,p.y);
  mv.signedDistance =length(q)-t.y;
    return mv;
}
MapValue checkerdCube( vec3 p, float d , Material m1,Material m2)
{
  Material  m;
    if(checker3D(6.0*p)){
        m = m1;
    }
    else {
       m = m2 ;
    }
  return cube(p,d, m); 
}

MapValue xzPlane( vec3 p ,float y, Material m)
{
  MapValue mv;
  mv.material = m;
  mv.signedDistance = p.y - y;
  return mv;
}

MapValue tableTop( vec3 p ,float y, Material m1,Material m2)
{
 	Material  m;
    //Draw a pattern on the table top by taking a 2d slice through a 3d checkerboard.
    float t = iTime;
    float a = sqrt(3.0)/2.0;
   	vec3 u = vec3(1.0,-0.5,-0.5);
   	vec3 v = vec3(0,a,-a);

    vec3 n = vec3(0.5);
   	float scaleFactor = 3.0;
    vec2 q = p.xz*scaleFactor;
    vec3 plane = n + q.x*u +q.y*v;


    if(checker3D(plane + vec3(0.2*t))){
        m = m1;
    }
    else {
       m = m2 ;
    }

  return xzPlane( p ,y, m);
}

MapValue plane(vec3 p, vec3 origin, vec3 normal , Material m ){
  vec3 a = p - origin;
  MapValue mv;
  mv.material = m;
  mv.signedDistance = dot(a,normal);
  return mv;
}

MapValue sphere(vec3 p, vec3 center, float radius, Material m) {
  MapValue mv;
  mv.material = m;
  mv.signedDistance = distance(p, center) - radius;
  return mv;
}

MapValue addObjects(MapValue d1, MapValue d2 )
{
    if (d1.signedDistance<d2.signedDistance) {
    	return    d1 ;
    }
    else {
    	return d2;
    }
}


MapValue subtractObjects( MapValue A, MapValue B )
{
    //A-B
    if (-B.signedDistance>A.signedDistance){
        B.signedDistance *= -1.0;
        B.material = A.material;
    	return    B ;
    }
    else {

    	return A;
    }
}

MapValue intersectObjects( MapValue d1, MapValue d2 )
{
    if (d1.signedDistance>d2.signedDistance){
    	return    d1 ;
    }
    else {
        d2.material = d1.material;
    	return d2;
    }
}

void setMaterials() {
    float t  = iTime;
    float s = 0.4*(1.0+sin(t));
    vec3 specular = vec3(0.3); 
    float shininess = 16.0;
    blackMat = Material(LightColor(vec3(0.0,0.0,0.01),vec3(0.1,0.1,0.1)) ,35.0,0.75,1.0,1.0);
    whiteMat = Material(LightColor(0.95*vec3(1.0,1.0,0.9),0.3*vec3(1.0,1.0,0.9)) ,shininess ,0.75,1.0,1.0);
    bluishMat = Material(LightColor(vec3(0.3,0.3,0.5),vec3(0.3,0.3,0.5)) ,shininess ,0.75,1.0,1.0);
    yellowMat = Material(LightColor(vec3(0.6,0.6,0.4),vec3(0.8,0.8,0.2)) ,shininess ,0.75,1.0,1.0);
    oscMat = Material(LightColor(vec3(1.0-s,1.0-s,0.9-0.7*s),vec3(1.0-s,1.0-s,0.9-0.7*s)) ,shininess ,0.75,1.0,1.0);
    tableMat = Material(LightColor(vec3(0.33,0.33,0.31),vec3(0.33,0.33,0.31)) ,shininess ,0.00,1.0,1.0);
    tableDarkMat = Material(LightColor(vec3(0.3,0.3,0.32),vec3(0.33,0.33,0.31)) ,shininess ,0.75,1.0,1.0);   
}

MapValue map(vec3 p){
   	float t  = iTime;
   	MapValue bigSphere = sphere(p,vec3(0.0),0.5,bluishMat);

    bigSphere = subtractObjects(bigSphere,sphere(p,0.5*vec3(cos(0.9*t),1.0,sin(0.9*t)),0.3,oscMat));
    float ssh = 0.85;
    MapValue obj  = addObjects(sphere(p,0.55*vec3(cos(t),ssh,sin(t)),0.125,blackMat),sphere(p,0.55*vec3(cos(t+ TWO_PI/3.0),ssh,sin(t+TWO_PI/3.0)),0.125,whiteMat));
    obj  = addObjects(obj,sphere(p,0.55*vec3(cos(t+ 2.0*TWO_PI/3.0),ssh,sin(t + 2.0*TWO_PI/3.0)),0.125,yellowMat));                      
    obj  = addObjects(obj,bigSphere);
    obj  = addObjects(obj,torus(p+vec3(0.0,0.15+ 0.2*sin(t),0.0),vec2(0.75,0.1),whiteMat));
   	obj  = addObjects( obj ,tableTop(p,-0.5,tableMat,tableDarkMat));
    obj  = addObjects( obj ,xzPlane(-p,-2.0,yellowMat));
   	return obj;
}

vec3 calculateNormal(vec3 p) {
    float epsilon = 0.001;

    vec3 normal = vec3(
                       map(p +vec3(epsilon,0,0)).signedDistance - map(p - vec3(epsilon,0,0)).signedDistance,
                       map(p +vec3(0,epsilon,0)).signedDistance - map(p - vec3(0,epsilon,0)).signedDistance,
                       map(p +vec3(0,0,epsilon)).signedDistance - map(p - vec3(0,0,epsilon)).signedDistance
                       );

    return normalize(normal);
}

Trace castRay(in Ray ray, float maxDistance){
    float dist = 0.01;
    float presicion = 0.001;
	vec3 p;
    MapValue mv;
    bool hit = false;
    for(int i=0; i<64; i++){
    	p = rayPoint(ray,dist);
       	mv = map(p);
         dist += 0.5*mv.signedDistance;
        if(mv.signedDistance < presicion )
        {
          hit = true; 
            break;
        } 
         if(dist>maxDistance) break;

    }
    return Trace(dist,p,p,ray,ray,mv.material,hit);
}

Trace traceRay(in Ray ray, float maxDistance) {
    Trace trace = castRay(ray,maxDistance);
    trace.normal = calculateNormal(trace.p);
    trace.reflection = Ray(trace.p,reflect(ray.direction, trace.normal));

    return trace;
}

float castShadow(in Ray ray, float dist){
    Trace trace = castRay(ray,dist);
    float maxDist = min(1.0,dist);
    float result = trace.dist/maxDist;

    return clamp(result,0.0,1.0);
}

Ray cameraRay(vec3 viewPoint, vec3 lookAtCenter, vec2 p , float d){ 
	vec3 v = normalize(lookAtCenter -viewPoint);

    vec3 n1 = cross(v,vec3(0.0,1.0,0.0));
    vec3 n2 = cross(n1,v);  

    vec3 lookAtPoint = lookAtCenter + d*(p.y*n2 + p.x*n1);

    Ray ray;

    ray.origin = viewPoint;
   	ray.direction =  normalize(lookAtPoint - viewPoint);

    return ray;
}

vec3 diffuseLighting(in Trace trace, vec3 lightColor,vec3 lightDir){
    float lambertian = max(dot(lightDir,trace.normal), 0.0);
  	return  lambertian * trace.material.color.diffuse * lightColor; 
}



vec3 cookTorranceSpecularLighting(in Trace trace, vec3 lightColor,vec3 L){
    //https://en.wikipedia.org/wiki/Specular_highlight#Cook.E2.80.93Torrance_model
    //https://renderman.pixar.com/view/cook-torrance-shader


    vec3 V = -trace.ray.direction;

    vec3 H = normalize(L + V);

    float NdotH = dot(trace.normal, H);
    float NdotV = dot(trace.normal, V);
    float VdotH = dot(V ,H );
    float NdotL = dot(trace.normal , L);

    float lambda  = 0.25;
    float F = pow(1.0 + NdotV, lambda);

    float G = min(1.0,min((2.0*NdotH*NdotV/VdotH), (2.0*NdotH*NdotL/VdotH)));


   // Beckmann distribution D
    float alpha = 5.0*acos(NdotH);
    float gaussConstant = 1.0;
	float D = gaussConstant*exp(-(alpha*alpha));


    float c = 1.0;
    float specular = c *(F*D*G)/(PI*NdotL*NdotV);


    return specular * trace.material.color.specular * lightColor;
}




vec3 pointLighting(in Trace trace, PointLight light){
    vec3 lightDir = light.position - trace.p;
	float d = length(lightDir);
  	lightDir = normalize(lightDir);

  	vec3 color =  diffuseLighting(trace, light.color.diffuse, lightDir);

     color += cookTorranceSpecularLighting(trace, light.color.specular, lightDir);

    float  attenuation = 1.0 / (1.0 +  0.1 * d * d);
    float shadow = castShadow(Ray(trace.p,lightDir),d);
    color *= attenuation*shadow;
    return  color;
}

vec3 directionalLighting(Trace trace, DirectionalLight light){

    vec3 color =  diffuseLighting(trace, light.color.diffuse, light.direction);

    color += cookTorranceSpecularLighting(trace, light.color.specular, light.direction);

    float shadow = castShadow(Ray(trace.p,light.direction),3.0);
    color *= shadow;
    return  color;
}


void setLights(){
  	float  time = iTime;
    vec3 specular = vec3(0.7);
  	light1 = PointLight(vec3(cos(1.3*time),1.0,sin(1.3*time)),LightColor( vec3(0.7),specular));
  	light2 = PointLight(vec3(0.7*cos(1.6*time),1.1+ 0.35*sin(0.8*time),0.7*sin(1.6*time)),LightColor(vec3(0.6),specular)); 
    light3 = PointLight(vec3(1.5*cos(1.6*time),0.15+ 0.15*sin(2.9*time),1.5*sin(1.6*time)),LightColor(vec3(0.6),specular));
    dirLight = DirectionalLight(normalize(vec3(0.0,1.0,0.0)),LightColor(vec3(0.1),vec3(0.5)));
} 


vec3 lighting(in Trace trace){
    vec3 color = vec3(0.01,0.01,0.1);//ambient color

	color += pointLighting(trace, light1);
	color += pointLighting(trace, light2) ;
    color += pointLighting(trace, light3) ;
	color += directionalLighting(trace, dirLight);

    return color;
}

float rayDistance(Ray r,vec3 p){
    vec3 v = r.origin - p;
    return length(v - dot(v,r.direction)*r.direction);
}

vec3  drawLight(PointLight light, Ray ray, vec3 color, float dist){
    float rayDist = rayDistance(ray,light.position);


    float lightRadius = 0.1;
    if(rayDist < lightRadius && distance(light.position,ray.origin) < dist){
       float a = 1.0-rayDist/lightRadius;
       return  mix(color,vec3(1.0),a*a*a*a);
    }
    return color;
}

vec3 drawLights(Ray ray,vec3 color,float dist){
    vec3 c = drawLight(light1,ray,color,dist);
    c = drawLight(light2,ray,c,dist);
    c = drawLight(light3,ray,c,dist);
    return c;
}

vec3 render(vec2 p){
    vec3 viewpoint = vec3(-1.0,1.7,-2.2);

    vec3 lookAt = vec3(0.0,-0.1,0.0);

  	Ray ray = cameraRay(viewpoint,lookAt,p,2.4);
    vec3 color = vec3(0.0);
    float frac = 1.0;

    float d = 0.0;
    float maxDist = 10.0;
    for(int i = 0; i<10; i++) {
        Trace trace = traceRay(ray,maxDist);

 		if(i==0) d = trace.dist;
        maxDist -= trace.dist;

    	color += lighting(trace)*(1.0 - trace.material.mirror)*frac;
        if(!trace.hit) break;

        frac *= trace.material.mirror;
        if(frac < 0.1 || maxDist < 0.0 ) break;
        ray = trace.reflection;
    }


    //color = drawLights(ray,color,10.0);
   	return color;
}



