void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos     =  fragCoord.xy / iResolution.xy * 2. - 1.;
    pos.x *= iResolution.x / iResolution.y;  

    vec3 camP = rotationCoordY(vec3(0., 0.4, 1.8), time);
    vec3 camUp = vec3(0. , 1., 0.);
    vec3 camDir = normalize(vec3(0.0, 0.3, 0.0)-camP);
    vec3 u = normalize(cross(camUp,camDir));
    vec3 v = cross(camDir,u);
    vec3 rayDir = normalize(2. * camDir + pos.x * u + pos.y * v);  

    vec2 t =  render(camP, rayDir);  
    vec3 color = getColor(camP, rayDir, t); 

    // gamma
	color = pow( clamp( color, 0.0, 1.0 ), vec3(0.45) );

    // vignetting
    pos*=0.35;
    float distSqr = dot(pos, pos);
	float vignette = 1.0 - distSqr;
    color *=  vignette;

    fragColor = vec4(color, 1.0);

}

vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;


     //float pr = min(cos(uv.y* iTime),   sin(uv.x* iTime)) ;
     float pr = 0.5;
	 float r = cos(0.2*iTime)*10.0+5.8;
	 float b = 3.0;
	 float	xn = 1.1;
	 float	yn = 12.0;
	 float	zn = uv.x*iTime;

    //zooooom
    xn =(uv.x - 0.5) *4.0;
    yn =(uv.y-0.5)*65.0;

    float intens = 0.0;
    //iterate at this pixel for a while and solve lorenz oscillator equations
    for(int i = 0; i <40; i++)
    {
        float xnn = xn*yn + (pr * (yn - zn));
		float ynn = yn*zn + (-xn * zn*zn);
		float znn = zn*xn + (xn * zn);
		xn = xnn;
		yn = ynn;
		zn = znn;
        if(length(vec3(xnn,ynn,znn))>200.0)
        {
            intens = float(i)/40.0;
            break;
        }
    }

	fragColor = vec4(hsv2rgb(vec3(intens*0.5,0.8,0.8)),1.0) * pow(intens,0.1);
}

bool squared(out vec2 uv, in vec2 fragCoord) {
