    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;
    uv.x += (1.0 - aspect) / 2.0;
    if (clamp(uv, vec2(0.0), vec2(1.0)) != uv) {
        return false;
    }
    return true;
}

#define GRID_WIDTH 0.05
#define GRID_DIM 10.0
#define GRID_COUNT (GRID_DIM * GRID_DIM)

const vec3 ON_COLOR = vec3(24, 202, 230) / 255.0;
const vec3 OFF_COLOR = vec3(251, 0, 9) / 255.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv;
    if (!squared(uv, fragCoord)) {
        fragColor = vec4(0, 0, 0, 1);
        return;
    } 

    vec2 grid = uv * GRID_DIM;
    vec2 floorGrid = floor(grid);
    vec2 offset = grid - floorGrid;
    if (offset != clamp(offset, vec2(GRID_WIDTH), vec2(1.0 - GRID_WIDTH))) {
        fragColor = vec4(vec3(0.5), 1.0);
        return;
    }


    float square = floorGrid.x + GRID_DIM * floorGrid.y;
    float f = mod(float(iTime * 1000.0), GRID_COUNT);

    fragColor = vec4(square > f ? ON_COLOR : OFF_COLOR, 1.0);
}

#define stripe(t, origin, width, s)   smoothstep(0., s, width/2. - abs(t-(origin)))

#define h2rgb(c)   clamp( abs(fract(c + vec4(3,2,1,0)/3.) * 6. - 3.) -1., 0., 1.)

#define NUM_STRIPES 7.


void mainImage( out vec4 O, vec2 U )
{
	U /= iResolution.xy;
    float s = .05, T = iTime, t;

    O -= O;

    for(float i = 1.; i <= NUM_STRIPES; i++)
        t = stripe( U.x,
                    .5 + .25* sin( .4* cos(T*i) * (i-4.+ 2.*U.y + .04*sin(T) -.5) ) ,
                    .1, s),
        O = mix(O, h2rgb( (i-4.) / NUM_STRIPES ), t);
}

