// ray marching
const int max_iterations = 100;
const float stop_threshold = 0.02;
const float grad_step = 0.1;
const float clip_far = 1000.0;

// math
const float PI = 3.14159265359;
const float DEG_TO_RAD = PI / 180.0;

const vec3 sunDir = normalize(vec3(1.0,-1.0,1.0));


vec3 rotate( vec3 p, vec3 rot ){
    rot.z = -rot.z;
    mat3 ry = mat3(cos(rot.y), 0.0,-sin(rot.y),
			   0.0, 1.0, 0.0, 
			   sin(rot.y), 0.0, cos(rot.y)  );

	mat3 rz = mat3(cos(rot.z),-sin(rot.z), 0.0,
			   sin(rot.z), cos(rot.z), 0.0,
			   0.0, 0.0, 1.0 );

	mat3 rx = mat3(1.0, 0.0, 0.0,
			   0.0, cos(rot.x), sin(rot.x), 
			   0.0,-sin(rot.x), cos(rot.x) );
    return p*rz*ry*rx;
}


float cubes( vec3 p, vec3 b,float c )
{
  p = rotate(p,vec3(-PI*0.3,0.0,0.0));
  vec3 initialp = p;
  p.xz = mod(p.xz,c) - 0.5 * c;
  vec2 index = floor(initialp.xz/c);

  float incidence = length(index)*0.1;
  incidence += sin((index.x + index.y)*0.1 + iTime*0.1)*0.03;

  p = rotate(p,vec3(iTime*incidence,iTime*incidence,0.0));

  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

//Map
vec2 mapCubeSkin(vec3 p){
    float c = 1.0;
    vec2 index = floor(p.xz/c);
    float minDist = cubes(p,vec3(0.5,0.5,.5),c);
   	float color = (1.0+sin(length(index.xy)*0.2+iTime))*0.5;
	return vec2(minDist, color);
}
vec2 map( vec3 p) {
    p.y += sin((p.x+p.z)*0.3+iTime*3.0);
    return mapCubeSkin(p);
}

vec2 rayMarching( vec3 origin, vec3 dir, float start, float end ) {

    float depth = start;
	for ( int i = 0; i < max_iterations; i++ ) {
        vec2 distResult = map( origin + dir * depth );
		float dist = distResult.x;
		if ( dist < stop_threshold ) {
			return vec2(depth,distResult.y);
		}
		depth += dist;
		if ( depth >= end) {
			return vec2(end,-1.0);
		}
	}
	return vec2(end,-1.0);
}

vec3 ray_dir( float fov, vec2 size, vec2 pos ) {
	vec2 xy = pos - size * 0.5;

	float cot_half_fov = tan( ( 90.0 - fov * 0.5 ) * DEG_TO_RAD );	
	float z = size.y * 0.5 * cot_half_fov;	
	return normalize( vec3( -xy, -z ) );
}

vec3 normal( vec3 pos ) {
	const vec3 dx = vec3( grad_step, 0.0, 0.0 );
	const vec3 dy = vec3( 0.0, grad_step, 0.0 );
	const vec3 dz = vec3( 0.0, 0.0, grad_step );
	return normalize (
		vec3(
			map( pos + dx ).x - map( pos - dx ).x,
			map( pos + dy ).x - map( pos - dy ).x,
			map( pos + dz ).x - map( pos - dz ).x			
		)
	);
}

mat3 rotationXY( vec2 angle ) {
	vec2 c = cos( angle );
	vec2 s = sin( angle );

	return mat3(
		c.y      ,  0.0, -s.y,
		s.y * s.x,  c.x,  c.y * s.x, 
		s.y * c.x, -s.x,  c.y * c.x
	);
}

float fresnel(vec3 n, vec3 d, float exp ){
    return pow(1.0-dot(d,n),exp);
}

vec3 material( vec3 v, vec3 n, vec3 eye,float randomIndex ) {    
    //Texturing 
    vec2 uv = v.xz;    

    //vec3 albedo = texture(iChannel0,vec2(randomIndex,0.0)).xyz;//vec3(0.5,0.0,0.0);
    vec3 albedo = mix(vec3(0.5,0.1,0.4),vec3(0.1,0.1,0.6),randomIndex);

    vec3 viewDir = normalize(eye-v);    
    vec3 fresnelColor = vec3(0.4,0.3,0.7) * fresnel(n,viewDir,2.0);    

    float diffuse = dot(sunDir,n)*0.3;
    vec3 ambient = vec3(0.1,0.05,0.005);

    return albedo+diffuse+ambient;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// default ray dir
	vec3 dir = ray_dir( 45.0, iResolution.xy, fragCoord.xy );

	// default ray origin
	vec3 eye = vec3( 0.0, -6.0, 45.0 );

	// rotate camera
	mat3 rot = rotationXY( vec2(0,2.0*PI));
	dir = rot * dir;
	eye = rot * eye;

	// ray marching
    vec2 rayResult = rayMarching( eye, dir, 2.0, clip_far );
	float depth = rayResult.x;
	if ( depth >= clip_far ) {        
		fragColor = vec4(0.3,0.3,0.3,1.0);//Background color
        return;
	}

	// shading
	vec3 pos = eye + dir * depth;
	vec3 n = normal( pos );
    vec3 fogColor = vec3(0.3,0.3,0.3);

   	fragColor = vec4(material( pos, n, eye,rayResult.y ), 1);

}

// all noise from iq!

#define time iTime

// INNER ATMOS PROPIETIES:
    // inner atmos inner strenght
    #define in_inner 0.2
    // inner atmos outer strenght
    #define in_outer 0.2

// OUTER ATMOS PROPIETIES:
    // inner atmos inner strenght
    #define out_inner 0.2 
    // inner atmos outer strenght
    #define out_outer 0.4 // 0.01 is nice too

float noise3D(vec3 p)
{
	return fract(sin(dot(p ,vec3(12.9898,78.233,128.852))) * 43758.5453)*2.0-1.0;
}

float simplex3D(vec3 p)
{
	float f3 = 1.0/3.0;
	float s = (p.x+p.y+p.z)*f3;
	int i = int(floor(p.x+s));
	int j = int(floor(p.y+s));
	int k = int(floor(p.z+s));

	float g3 = 1.0/6.0;
	float t = float((i+j+k))*g3;
	float x0 = float(i)-t;
	float y0 = float(j)-t;
	float z0 = float(k)-t;
	x0 = p.x-x0;
	y0 = p.y-y0;
	z0 = p.z-z0;
	int i1,j1,k1;
	int i2,j2,k2;
	if(x0>=y0)
	{
		if		(y0>=z0){ i1=1; j1=0; k1=0; i2=1; j2=1; k2=0; } // X Y Z order
		else if	(x0>=z0){ i1=1; j1=0; k1=0; i2=1; j2=0; k2=1; } // X Z Y order
		else 			{ i1=0; j1=0; k1=1; i2=1; j2=0; k2=1; } // Z X Z order
	}
	else 
	{ 
		if		(y0<z0) { i1=0; j1=0; k1=1; i2=0; j2=1; k2=1; } // Z Y X order
		else if	(x0<z0) { i1=0; j1=1; k1=0; i2=0; j2=1; k2=1; } // Y Z X order
		else 			{ i1=0; j1=1; k1=0; i2=1; j2=1; k2=0; } // Y X Z order
	}
	float x1 = x0 - float(i1) + g3; 
	float y1 = y0 - float(j1) + g3;
	float z1 = z0 - float(k1) + g3;
	float x2 = x0 - float(i2) + 2.0*g3; 
	float y2 = y0 - float(j2) + 2.0*g3;
	float z2 = z0 - float(k2) + 2.0*g3;
	float x3 = x0 - 1.0 + 3.0*g3; 
	float y3 = y0 - 1.0 + 3.0*g3;
	float z3 = z0 - 1.0 + 3.0*g3;			 
	vec3 ijk0 = vec3(i,j,k);
	vec3 ijk1 = vec3(i+i1,j+j1,k+k1);	
	vec3 ijk2 = vec3(i+i2,j+j2,k+k2);
	vec3 ijk3 = vec3(i+1,j+1,k+1);	     
	vec3 gr0 = normalize(vec3(noise3D(ijk0),noise3D(ijk0*2.01),noise3D(ijk0*2.02)));
	vec3 gr1 = normalize(vec3(noise3D(ijk1),noise3D(ijk1*2.01),noise3D(ijk1*2.02)));
	vec3 gr2 = normalize(vec3(noise3D(ijk2),noise3D(ijk2*2.01),noise3D(ijk2*2.02)));
	vec3 gr3 = normalize(vec3(noise3D(ijk3),noise3D(ijk3*2.01),noise3D(ijk3*2.02)));
	float n0 = 0.0;
	float n1 = 0.0;
	float n2 = 0.0;
	float n3 = 0.0;
	float t0 = 0.5 - x0*x0 - y0*y0 - z0*z0;
	if(t0>=0.0)
	{
		t0*=t0;
		n0 = t0 * t0 * dot(gr0, vec3(x0, y0, z0));
	}
	float t1 = 0.5 - x1*x1 - y1*y1 - z1*z1;
	if(t1>=0.0)
	{
		t1*=t1;
		n1 = t1 * t1 * dot(gr1, vec3(x1, y1, z1));
	}
	float t2 = 0.5 - x2*x2 - y2*y2 - z2*z2;
	if(t2>=0.0)
	{
		t2 *= t2;
		n2 = t2 * t2 * dot(gr2, vec3(x2, y2, z2));
	}
	float t3 = 0.5 - x3*x3 - y3*y3 - z3*z3;
	if(t3>=0.0)
	{
		t3 *= t3;
		n3 = t3 * t3 * dot(gr3, vec3(x3, y3, z3));
	}
	return 96.0*(n0+n1+n2+n3);
}

float fbm(vec3 p)
{
	float f;
    f  = 0.50000*(simplex3D( p )); p = p*2.01;
    f += 0.25000*(simplex3D( p )); p = p*2.02;
    f += 0.12500*(simplex3D( p )); p = p*2.03;
    f += 0.06250*(simplex3D( p )); p = p*2.04;
    f += 0.03125*(simplex3D( p )); p = p*2.05;
    f += 0.015625*(simplex3D( p ));
	return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec3 pos = vec3(gl_FragCoord.xy - iResolution.xy/2.0,0.0); // planet center

    // LIGHT
    vec3 l = normalize(vec3(sin(time), sin(time*0.5), (cos(time))));

    // PLANET
    float r = iResolution.y/3.0; // radius
    float z_in = sqrt(r*r - pos.x*pos.x - pos.y*pos.y);
    float z_out = sqrt(-r*r + pos.x*pos.x + pos.y*pos.y);

    // NORMALS
    vec3 norm = normalize(vec3(pos.x, pos.y, z_in)); // normals from sphere
    vec3 norm_out = normalize(vec3(pos.x, pos.y, z_out)); // normals from outside sphere
    float e = 0.05; // planet rugosity
    float nx = fbm(vec3(norm.x+e, norm.y,   norm.z  ))*0.5+0.5; // x normal displacement
    float ny = fbm(vec3(norm.x,   norm.y+e, norm.z  ))*0.5+0.5; // y normal displacement
    float nz = fbm(vec3(norm.x,   norm.y,   norm.z+e))*0.5+0.5; // z normal displacement
    norm = normalize(vec3(norm.x*nx, norm.y*ny, norm.z*nz));
    //norm = (norm+1.)/2.; // for normals visualization

    // TEXTURE
    float n = 1.0-(fbm(vec3(norm.x, norm.y, norm.z))*0.5+0.5); // noise for every pixel in planet

    // ATMOS
    float z_in_atm  = (r * in_outer)  / z_in - in_inner;   // inner atmos
    float z_out_atm = (r * out_inner) / z_out - out_outer; // outer atmos
    z_in_atm = max(0.0, z_in_atm);
    z_out_atm = max(0.0, z_out_atm);

    // DIFFUSE LIGHT
    float diffuse = max(0.0, dot(norm, l));
    float diffuse_out = max(0.0, dot(norm_out, l)+0.3); // +0.3 because outer atmosphere stills when inner doesn't

	//fragColor = vec4(vec3(n * diffuse),1.0);
    //fragColor = vec4(vec3(z_in_atm * diffuse),1.0);
    //fragColor = vec4(vec3(z_out_atm * diffuse_out),1.0);*/

    fragColor = vec4(vec3(n * diffuse + z_in_atm * diffuse + z_out_atm * diffuse_out),1.0);
}

#define _ParticlesAmount 10

float rnd(float x)
{
    return fract(sin(dot(vec2(x + 47.49,38.2467 / (x + 2.3)), vec2(12.9898, 78.233))) * (43758.5453));
}

float drawLeaf(vec2 uv, float scale, float d) {
	float ret;
    vec2 root = uv - vec2(0.0, scale);
    float r = length(root) / scale;
    float t = abs(atan(root.x, root.y) / 3.1415);
    float edge = (3.0 * t - 8.0 * t*t*t*t + 6.0 * t*t*t*t*t) / (4.0 - 3.0 * t);
    //float edge = (3.0 * t - 8.0 * t*t*t*t*t + 6.0 * t*t*t*t*t*t) / (3.0 - 2.0 * t*t);
    //float edge = (3.0 * t + 2.0 * t*t - 2.0 * t*t*t - 9.0 * t*t*t*t*t*t + 7.0 * t*t*t*t*t*t*t) / (5.0 - 4.0 * t*t);
	ret = smoothstep(edge - d, edge + d, r);
	return ret;
}

mat2 rotate(float t) {
    return mat2(cos(t), -sin(t), sin(t), cos(t));
}

float drawClover(vec2 uv, float scale, float d) {
    float ret = drawLeaf(uv, scale, d);
    uv = rotate(1.5707) * uv;
    ret *= drawLeaf(uv, scale, d);
    uv = rotate(1.5707) * uv;
    ret *= drawLeaf(uv, scale, d);
    uv = rotate(1.5707) * uv;
    ret *= drawLeaf(uv, scale, d);
    return 1.0 - ret;
}

vec4 alphaBlend(vec4 base, vec4 blend)
{
    return vec4(base.rgb * base.a * (1.0 - blend.a) + blend.rgb * blend.a, blend.a + base.a * (1.0 - blend.a));   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / min(iResolution.x, iResolution.y);
    float t = smoothstep(1.5, 0.0, length(uv));
    fragColor = vec4(t * vec3(1, 0.8784, 0.9333) + (1.0 - t) * vec3(0.9568, 0.7451, 0.8118), 1.0);

    float j;
    float move_max = 1.0;
    vec2 spawn_center = vec2(0.0, 0.0);
    float spawn_length = 0.5;
    float _ParticlesAmount_f = float(_ParticlesAmount);

    for (int i = 1; i < _ParticlesAmount; i++)
    {
        j = float(i);
        float rnd1 = rnd(cos(j));
        float delayedTime = (0.2 + 0.2 * rnd1) * iTime;
        float d = floor(delayedTime / move_max);
        float rnd2 = rnd(j * d);
        float rnd3 = rnd(j * j * d);
        float r = delayedTime / move_max - d;
        float x_wave = 0.15 * sin(delayedTime * 7.0 + 6.282 / j);
        vec2 spawn = vec2(0.0, rnd3 * spawn_length);
        float ease = pow(2.0, 5.0 * (r - 1.0));
        float y_move = move_max * ease;
        float opacity = 1.0 - ease - pow(2.0, -30.0 * r);
        float scale = 1.0 - 0.65 * rnd1 + 0.15 * sin(1.8 * iTime * j / _ParticlesAmount_f + 6.282 / j);
        float rot_wave = 2.0 * sin(delayedTime * 3.0 * j / _ParticlesAmount_f * 2.0 + 6.282 / j);
    	vec2 center = rotate(rot_wave) * (rotate(6.282 * rnd2) * (uv + spawn_center) + spawn + vec2(x_wave, y_move)) * scale;
        vec3 cloverColor = vec3(0.3 + 0.3 * rnd2, 0.98, 0.3) * (1.0 - 0.3 * rnd3);
    	vec3 cloverCenterColor = cloverColor + (vec3(1.0) - cloverColor) * 0.5;
    	vec3 cloverBgColor = vec3(1.0, 0.98, 0.7);
    	fragColor = alphaBlend(fragColor, vec4(cloverBgColor, opacity * drawClover(center, 0.1, 0.3)));
   		fragColor = alphaBlend(fragColor, vec4(cloverColor, opacity * drawClover(center, 0.1, 0.01)));
    	fragColor = alphaBlend(fragColor, vec4(cloverCenterColor, opacity * drawClover(center, 0.05, 0.3)));
    }
}

#define white vec4(1, 1, 1, 1)
#define black vec4(0, 0, 0, 0)

float dist(vec2 c, vec2 v) {
    vec2 d = c - v;
	return sqrt(d.x * d.x + d.y * d.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = vec2(iResolution.x / 2., iResolution.y / 2.);

    float speed = 3.;
    float baseRadius = 350.;
    float range = 25.;

	float radius = baseRadius + range * sin(iTime * speed);
    float x = fragCoord.x - center.x;
    float y = fragCoord.y - center.y;
    if(x * x + y * y > radius * radius) fragColor = black;
    else fragColor = white - white * (dist(center, fragCoord) / radius);
}

