float bounce;

// rotation
void pR(inout vec2 p,float a) 
{
	p=cos(a)*p+sin(a)*vec2(p.y,-p.x);
}


// 3D noise function (IQ)
float noise(vec3 p)
{
	vec3 ip=floor(p);
    p-=ip; 
    vec3 s=vec3(7,157,113);
    vec4 h=vec4(0.,s.yz,s.y+s.z)+dot(ip,s);
    p=p*p*(3.-2.*p); 
    h=mix(fract(sin(h)*43758.5),fract(sin(h+s.x)*43758.5),p.x);
    h.xy=mix(h.xz,h.yw,p.y);
    return mix(h.x,h.y,p.z); 
}


float map(vec3 p)
{	
// 	tunnel    
	p.z+=(3.-sin(0.314*iTime+1.1));
	pR(p.zy,1.57);
	return  mix(length(p.xz)-.2,length(vec3(p.x,abs(p.y)-1.3,p.z))-.2,step(1.3,abs(p.y)))-0.1*noise(8.*p+0.4*bounce);
}

//	normal calculation
vec3 calcNormal(vec3 pos)
{
    float eps=0.0001;
	float d=map(pos);
	return normalize(vec3(map(pos+vec3(eps,0,0))-d,map(pos+vec3(0,eps,0))-d,map(pos+vec3(0,0,eps))-d));
}

// 	standard sphere tracing inside and outside
float castRayx(vec3 ro,vec3 rd) 
{
    float function_sign=(map(ro)<0.)?-1.:1.;
    float precis=.0001;
    float h=precis*2.;
    float t=0.;
	for(int i=0;i<120;i++) 
	{
        if(abs(h)<precis||t>12.)break;
		h=function_sign*map(ro+rd*t);
        t+=h;
	}
    return t;
}

// 	refraction
float refr(vec3 pos,vec3 lig,vec3 dir,vec3 nor,float angle,out float t2, out vec3 nor2)
{
    float h=0.;
    t2=2.;
	vec3 dir2=refract(dir,nor,angle);  
 	for(int i=0;i<50;i++) 
	{
		if(abs(h)>3.) break;
		h=map(pos+dir2*t2);
		t2-=h;
	}
    nor2=calcNormal(pos+dir2*t2);
    return(.5*clamp(dot(-lig,nor2),0.,1.)+pow(max(dot(reflect(dir2,nor2),lig),0.),8.));
}

//	softshadow 
float softshadow(vec3 ro,vec3 rd) 
{
    float sh=1.;
    float t=.02;
    float h=.0;
    for(int i=0;i<22;i++)  
	{
        if(t>20.)continue;
        h=map(ro+rd*t);
        sh=min(sh,4.*h/t);
        t+=h;
    }
    return sh;
}

//	main function
void mainImage(out vec4 fragColor,in vec2 fragCoord)
{    
    float bounce=abs(fract(0.05*iTime)-.5)*20.; // triangle function


	vec2 uv=gl_FragCoord.xy/iResolution.xy; 
    vec2 p=uv*2.-1.;

// 	bouncy cam every 10 seconds
    float wobble=(fract(.1*(iTime-1.))>=0.9)?fract(-iTime)*0.1*sin(30.*iTime):0.;
    wobble*=0.3;

//  camera    
    vec3 dir = normalize(vec3(2.*gl_FragCoord.xy -iResolution.xy, iResolution.y));
    vec3 org = vec3(0,2.*wobble,-3.);  
   	dir = normalize(vec3(dir.xy, sqrt(max(dir.z*dir.z - dot(dir.xy, dir.xy)*.2, 0.))));//barrel
  	vec2 m = sin(vec2(0, 1.57) + iTime/8.);
   	dir.xy = mat2(m.y, -m.x, m)*dir.xy;
   	dir.xz = mat2(m.y, -m.x, m)*dir.xz;


// 	standard sphere tracing:
    vec3 color=vec3(0.);
    vec3 color2=vec3(0.);
    float t=castRayx(org,dir);
	vec3 pos=org+dir*t;
	vec3 nor=calcNormal(pos);

// 	lighting:
    vec3 lig=normalize(-pos);//:vec3(.2,6.,.5));
//	scene depth    
    float depth=clamp((1.-0.09*t),0.,1.);

    vec3 pos2,nor2 =  vec3(0.);
    if(t<12.0)
    {
    	color2 = vec3(max(dot(lig,nor),0.)  +  pow(max(dot(reflect(dir,nor),lig),0.),16.));
    	color2 *=clamp(softshadow(pos,lig),0.,1.);  // shadow            	
       	float t2;
		color2.r +=refr(pos,lig,dir,nor,0.91, t2, nor2)*depth;
  		color2.g +=refr(pos,lig,dir,nor,0.90, t2, nor2)*depth;
   		color2.b +=refr(pos,lig,dir,nor,0.89, t2, nor2)*depth;
		color2-=clamp(.1*t2,0.,1.);				// inner intensity loss
	}      
    float tmp = 0.;
    float T = 1.;

//	animation of glow intensity    
    float intensity = 0.1*-sin(.209*iTime+1.)+0.1; 
	for(int i=0; i<128; i++)
	{
        float density = 0.; float nebula = noise(org+bounce);
        density=intensity-map(org+.5*nor2)*nebula;
		if(density>0.)
		{
			tmp = density / 128.;
            T *= 1. -tmp * 100.;
			if( T <= 0.) break;
		}
		org += dir*0.078;
    }    
	vec3 basecol=vec3(1./1. ,  1./4. , 1./16.);
    T=clamp(T,0.,1.5); 
    color += basecol* exp(4.*(0.5-T) - 0.8);
    color2*=depth*depth;
    color2+= (1.-depth)*noise(6.*dir+0.3*iTime)*.1;	// subtle mist



    fragColor = vec4(vec3(1.*color+0.8*color2)*1.3,0.);
}

// This shows the multiplication table modulo 200 represented as lines connecting points
//	on the unit circle, as explained in the Mathologer video https://www.youtube.com/watch?v=qhbuKbxJsk8
//
// I also inverted the positions outside the unit disk to reflect the inside so the look
//	is even more interesting :D
//
// EDIT: Fixed smoothstep not working with singularity when start and end angles are both 0 (obviously gave a 0/0 NaN)
//
const float	TWOPI = 6.283185307179586476925286766559;

const float	MODULO = 200.0;	// Amount of subdivisions of the circle

vec3 ComputeInnerColor( vec2 _uv, float _mul ) {

    float	isOnLine = 0.0;

	vec2	scStart, scEnd;

	// Compute start and end anglse based on multiplier
	float	dStartAngle = TWOPI / MODULO;
	float	dEndAngle = _mul * dStartAngle;
	float	startAngle = dStartAngle;
	float	endAngle = dEndAngle;

    for ( float i=1.0; i < MODULO; i++ ) {
        // Compute start and end position on the unit circle, forming a line
        scStart.x = sin( startAngle );
        scStart.y = cos( startAngle );
        scEnd.x = sin( endAngle );
        scEnd.y = cos( endAngle );

        // Compute line normal
        vec2	normal = normalize( vec2( scEnd.y - scStart.y, scStart.x - scEnd.x ) );

        // Compute distance to line
        vec2	delta = _uv - scStart;
        float	orthoDistance = abs( dot( delta, normal ) );

        // Check if the current position is on the line or not
        isOnLine += smoothstep( 0.005, 0.0, orthoDistance );

        startAngle += dStartAngle;
        endAngle += dEndAngle;
    }

    return vec3( 1.0 - clamp( isOnLine, 0.0, 1.0 ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2	R = iResolution.xy;
    vec2	uv = ( 2. * fragCoord - R ) / R.y;

uv *= 2.0 + sin( iTime );

    float	mul = 1.0 + 99.0 * (1.0 - cos( 0.02 * iTime ));

    float	radius = length( uv );
//	float	D = radius < 1. ? 1. :  1. / (radius * radius);
    float	D = mix( 1.0, 1.0 / (radius*radius), smoothstep( 0.95, 1.0, radius ) );	// Smoother joint between regular and reciprocal space
	fragColor = vec4( ComputeInnerColor( D*uv, mul ), 1.0 );
}

