float Hash(vec2 p)
{
    float h = dot(p, vec2(17.1, 311.7));
    return 1.0 - 2.0 * fract(sin(h) * 4358.5453);
}

float Noise(vec2 p, float x)
{
    vec2 i = sin(floor(p))*x;
    i += floor(p)*(1.0-x);
    vec2 f = fract(p);
    vec2 u = (f * f * (3.0 - 2.0 * f));

    return mix(mix(Hash(i + vec2(0.0, 0.0)),
                   Hash(i + vec2(1.0, 0.0)), u.x),
               mix(Hash(i + vec2(0.0, 1.0)),
                   Hash(i + vec2(1.0, 1.0)), u.x), u.y);

}

vec2 VecNoise(vec2 point)
{
    vec2 res;
    res.x = Noise(point,0.0);
    res.y = Noise(point + vec2(iTime),0.0);
    return res;
}


float DistortionNoise(vec2 point, float distortion)
{
    vec2 offset = point + vec2(0.5);
    offset = VecNoise(point);
    offset *= distortion;
    point += offset;
    return Noise(point,0.0);
}


float FbmNoise(vec2 p)
{
  const float octaves = 8.0;
  const float lacunarity = 2.0;
  const float H = 0.5;

  float value = 0.0, k = 0.0;
  for (float i = 0.0; i < octaves; ++ i) {
    value += Noise(p,0.0) * pow(lacunarity, -H * i);
    p *= lacunarity;
    ++k;
  }

  float remainder = fract(octaves);
  if (remainder >= 0.0) {
    value -= remainder * Noise(p,0.0) - pow(lacunarity, -H * k);
  }
  return value;
}

float DistFbmNoise(vec2 p)
{
  const float octaves = 8.0;
  const float lacunarity = 2.0;
  const float H = 0.5;

  float value = 0.0, k = 0.0;
  for (float i = 0.0; i < octaves; ++ i) {
    value += DistortionNoise(p, 1.0) * pow(lacunarity, -H * i);
    p *= lacunarity;
    ++k;
  }

  float remainder = fract(octaves);
  if (remainder != 0.0) {
    value += remainder * Noise(p,0.0) * pow(lacunarity, -H * k);
  }
  return value;
}

