                sin(_angle),cos(_angle));
}
mat3 rotate3d(float a)
{
    return mat3(cos(a),-sin(a),sin(a),
                cos(a),cos(a),-sin(a),
                sin(a),cos(a),cos(a));
}
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
