vec3 getNoiseP(vec3 p, float zoom)
{
    float z = FbmNoise(p.xy/zoom);
    float x = FbmNoise(p.zy/zoom);
    float y = Noise(p.xz/zoom,4.23);
    float f = length(z);
    float s = length(x);
    float l = length(y);
    vec3 new = vec3(f, s, l);
    vec3 squared = sqrt(abs(1.0/exp(-0.002*new)));
    vec3 other = new*log(squared)+sqrt(abs(squared));
    return (maxComp(other)+maxComp(squared))*new;

}
