vec2 map( vec3 pos)
{
    float roundness = .15;
  	float rot = 1.;
	float k = .05;
    float size = .5;

    float scale = 1. / ( 3. - clamp( pos.y, 1., 7. ) );
    scale = 1.;

    vec2 x;
    float iGlobalTim = iTime;
    vec3 p = vec3(pos.x, pos.y, pos.z);
    float d1;
    //Putting An Object:
    p = vec3(pos.x, pos.y-VoxelSize+(clamp(iGlobalTim, 15.96, 19.71)-15.96), pos.z);
    p.xz*=rotate2d(clamp(iGlobalTim*7., 30.*7., 31.*7.)-30.*7.);
    d1 = sdBox(opRep(p,vec3(clamp(iGlobalTim,0.0,10.0))-
                     vec3((clamp(iGlobalTim,20.0,25.0)-20.0))+
                     vec3(clamp(iGlobalTim,44.0, 64.0)-44.0)), vec3(VoxelSize));
    x = vec2(d1, 2.0);
    p = vec3(pos.x, pos.y-VoxelSize+(clamp(iGlobalTim, 15.96, 19.71)-15.96), pos.z);
    p.xz*=rotate2d(clamp(iGlobalTim*7., 31.*7., 32.*7.)-31.*7.);
    d1 = sdBox(opRep(p,vec3(clamp(iGlobalTim,1.0,10.0)-1.0)-
                     vec3((clamp(iGlobalTim,21.0,26.0)-21.0))+
                     vec3(clamp(iGlobalTim,44.0, 64.0)-44.0)), vec3(VoxelSize));
    x = opU(vec2(d1, 2.0), x);
    p = vec3(pos.x, pos.y-VoxelSize+(clamp(iGlobalTim, 15.96, 19.71)-15.96), pos.z);
    p.xz*=rotate2d(clamp(iGlobalTim*7., 32.*7., 33.*7.)-32.*7.);
    d1 = sdBox(opRep(p,vec3(clamp(iGlobalTim,2.0,10.0)-2.0)-
                     vec3((clamp(iGlobalTim,22.0,27.0)-22.0))+
                    vec3(clamp(iGlobalTim,44.0, 64.0)-44.0)), vec3(VoxelSize));
    x = opU(vec2(d1, 2.0), x);

    p = vec3(pos.x, pos.y-(clamp(iGlobalTim,22.0,27.0)-22.0)
             +(clamp(iGlobalTim,35.,43.0)-35.0)
             +(clamp(iGlobalTim,45.0,65.0)-45.0), pos.z);
    d1 = sdSphere(opRep(p, vec3((
        clamp(iGlobalTim,10., 14.5)-10.)+
        (clamp(iGlobalTim,45.0, 65.0)-45.)
                               )),0.5);
    x = opU(vec2(d1, 0.6), x);

    p = vec3(pos.x, pos.y+
             (clamp(iTime, 52.0, 60.0)-52.0)-
             (clamp(iTime, 34., 42.)-34.),
             pos.z);
    d1 = sdSphere(opRep(p, vec3(
        (clamp(iTime, 9.0, 14.0)-9.0)
        )//E
        )
                        , 0.5);
    x = opU(vec2(d1, 1.0), x);

    if(iGlobalTim > 57.5)
    {
        float m = iTime-57.5;
        p = vec3(pos.x, pos.y, pos.z);
        d1 = sdBox(p, atan(vec3(m*4.)));
        x = opU(vec2(d1, 1.0), x);
    }

    float fx = 1.;
    if(iTime > 210.*fx)
    {
        x -= x*((clamp(iTime, 210., 220.)-210.)/10.);
    }
    return x;

}
