//return raydirection and rayorigin from camera input.
//[s]screenspace, [e]Eyepos, [t]target, [u]up, out Ray [o]origin, out Ray [d]direction , [f]fow
void camera(vec2 s,vec3 e,vec3 t,vec3 u,out vec3 o,out vec3 d,float f){	
 vec3 z=normalize(t-e),x=normalize(cross(z,u)),y=normalize(cross(x,z))*s.y;x*=s.x;//vec3 x,y,z==vec3 u,v,w
 float w=acos(dot(z,normalize(x))),//fow.xy
 a=(10./(2.*tan(abs(w)*.5)));o=e+(x+y)*(.7+.2*f)*a;d=normalize((e+z*2.+(x+y)*a)-o);}

//q=fragment screenspace, f= fow, h=cameraHeight
vec3 pixel(vec2 q,float f,float h){float dist=4.;
 vec3 t=vec3(0,0,1),u=vec3(0,0,1),//target,up
 e=t+vec3(cos(iTime/2.0)*dist,sin(iTime/2.)*dist,0.25+h*2.),//EyePos
 o,d;//origin,direction
 camera(q,e,t,u,o,d,f);//set o and d
 vec3 p=o+d*raymarch(o,d);//P=intersection of ray with surface by raymarching.
 vec3 light0=normalize(vec3(1,2,3)*9.-p);
 vec3 n=normal(p);
 vec3 c=n*.5+.5;
 //c=mix(c-vec3(.1),c,shadow(p,vec3(1,2,3)));
 //c*=max(dot(n,normalize(vec3(1,2,3))),0.)*.5+.5;//diffuse light
 if(dot(n,light0)>0.){float s=min(1.,pow(max(0.,dot(reflect(-light0,n),normalize(o-p))), 50.0));
  return mix(c,vec3(1),s*.75);}
 return c;}

