float stripe_t( float t, float origin, float width, float smoothness ) {
    // t for a simple stripe
    // origin
    // width
    float hw = width / 2.0;
    float blend = smoothstep(origin - hw, 
                             origin+smoothness - hw, 
                             t) * 
        smoothstep(origin+hw, origin+hw-smoothness, t);
    return blend;
}

