    // fragColor = vec4(mix(bgcolor, color, stripe_t(uv.x, 0.5, 0.1, smoothness)));   

    // Make some wiggly stripes
    vec4 col = vec4(bgcolor);
    for(int i = 1; i < NUM_STRIPES+1; i++) {        
