#define dx (iMouse.x/iResolution.x)
#define dy (iMouse.y/iResolution.y)
#define TWOPI (6.28318530718)

vec3 segment(  vec2  p,
               vec2  a,
               vec2  b,
               float d ){
    float len = length(b-a);

    // avoids vertex overlap
    // doesn't work :-( 
    /*
    float dd = d/(sqrt(2.0)*0.2);
    //float D = d;
    //float D = dd*exp(0.5-dd*dd);
    float dhalf = 0.1;
    float D = d < dhalf ? d*1.3 : smoothstep(0.,dhalf,2.*dhalf - d);
    //float D = d < 0.1 ? d : 0.0;
    //float D = d/dhalf*exp(- (1.0-d/dhalf)*(1.0-d/dhalf));
    if( D < len ){
    	b = a+( ((b-a)/len)*max(len-D,0.) );
    	len = length(b-a);
    }
    */

    float sx = (b.x - a.x);
    float sy = (b.y - a.y);
    vec2 r;

    if( sx == 0. && sy == 0. ){
        r = vec2(length(p-a));
        return vec3(1. - clamp(r.y/d,0.,1.),0.,0.0);
    } else {
    	r = (mat2(sx,-sy,sy,sx))*(p-a)/len;
    }

    r.y = (1.0 - smoothstep(0.0,1.0,abs(r.y)/(d)) )
        * (smoothstep(0.,1., (r.x+d)/(d)) )
        * (1.0 - smoothstep(0.,1., (r.x-len)/(d)) );

    r.x = clamp(r.x,0.0,len);

    return vec3( r.y
                , r.x , r.x / len );
}

vec4 colorSegment(vec2  p,
              	  vec2  a,
                  vec2  b,
                  float d,
                  vec4  color1,
                  vec4  color2){
    vec3 s = segment(p,a,b,d);
    return pow(s.x,0.5)*mix(color1*color1.a,color2*color2.a,s.z);
}

