    float lineThickness = 8./max(iResolution.x,iResolution.y) + dx*0.3*(1. + cos(iTime/3.14/0.40));
    const int pts = 100;

	vec2 uv = viewport(fragCoord);

	o = vec4(0.0,0.0,0.0,0.0);

