    vec2 p2 = viewport(vec2(0.5,0.1)*iResolution.xy);

    vec4 c1;
    vec4 c2;
    float di;

    for( int i = 0 ; i < pts ; i++ ){
        di = float(i)/float(pts-1);

        // curve
        p2 = (vec2(
            sin(di*8.0*TWOPI+iTime)+sin(di*3.0*TWOPI),
            cos(di*3.0*TWOPI+sqrt(3.0)*iTime)
        ));
        // color scheme
        c2 = vec4(
            0.5+0.5*cos(di*4.0*TWOPI),
            0.5+0.5*cos(di*9.0*TWOPI),
            0.5+0.5*cos(di*2.0*TWOPI),
            0.5+0.5*cos(di*13.0*TWOPI+iTime*4.0)
        );

        if( i > 0 ){
            o += colorSegment(uv,p1,p2,lineThickness,c1,c2);
        }

        c1 = c2;
        p1 = p2;
    }
}

