        for (float x=-1.; x<=1.; x+=1./N)
            for (float y=-1.; y<=1.; y+=1./N)

                Us = Ur + vec2(x,y)*z/R.y,
                r = length( fract(Us)-.5 + floor(Us)-floor(U) ),
	            O += U0.x<0. 
                   ? 6e-4/( pow(r/z,3.) * z*z)     // pix = clamp ( Emit/dist^2 * Bessel )
                   : smoothstep(1.,.8, r*8.);      // pix = disk ( R/dist )
                 //? 1./( pow(r,3.) * z*z)         
                 //: smoothstep(1.,.8, r/1.5*z);   

    O *= vec4(1,.5,.2,1) / pow(N+N+1.,2.);         // color and sum normalization       
}

