float sdPlane( vec3 p, bool water, bool sand )
{
    if(water == true)
    {
    float f = mod( sin(1.0*p.z-iTime)+
                           tan(1.0*p.x-iTime), 0.25/max(abs(p.z), 1.25));
    float g = mod( sin(1.0*p.x-iTime)+
                           atan(1.0*p.z-iTime), 1.25/max(abs(p.x), 2.25));
    float h = mod( sin(2.5*p.z-iTime)+
                           tan(0.5*p.x-iTime), 0.5/max(abs(p.z), 1.5));
    float i = mod( sin(2.5*p.x-iTime)+
                           atan(0.5*p.z-iTime), 0.7/max(abs(p.x), 1.7));
	return p.y+(f*g+h*i);
    }
    else
    {
        return p.y;
    }
}
