//-----------------CONSTANTS MACROS-----------------

#define PI 3.14159265359
#define E 2.7182818284
#define GR 1.61803398875

//-----------------UTILITY MACROS-----------------

#define time ((.125-.25*saw(float(__LINE__)*PI*GR*E)/PI/GR/E)*iTime+1000.0)
#define sphereN(uv) (clamp(1.0-length(uv*2.0-1.0), 0.0, 1.0))
#define clip(x) (smoothstep(0.25, .75, x))
#define TIMES_DETAILED (1.0)
#define angle(uv) (atan(uv.y, uv.x))
#define angle_percent(uv) ((angle(uv)/PI+1.0)/2.0)

#define flux(x) (vec3(cos(x),cos(4.0*PI/3.0+x),cos(2.0*PI/3.0+x))*.5+.5)
#define circle(x) (vec2(cos((x)*2.0*PI),sin(2.0*PI*(x))))

#define rotatePoint(p,n,theta) (p*cos(theta)+cross(n,p)*sin(theta)+n*dot(p,n) *(1.0-cos(theta)))


float saw(float x)
{
    float f = mod(floor(abs(x)), 2.0);
    float m = mod(abs(x), 1.0);
    return f*(1.0-m)+(1.0-f)*m;
}
vec2 saw(vec2 x)
{
    return vec2(saw(x.x), saw(x.y));
}

vec3 saw(vec3 x)
{
    return vec3(saw(x.x), saw(x.y), saw(x.z));
}

vec4 saw(vec4 x)
{
    return vec4(saw(x.x), saw(x.y), saw(x.z), saw(x.w));
}


//-----------------ITERATED FUNCTION SYSTEM-----------------

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float gradient = uv.y;

        float scale = E;
    uv = uv*scale-scale/2.0;

    float aspect = iResolution.x/iResolution.y;

    uv.x *= aspect;

    float rotation = -time*PI*GR*E;

    vec2 uv0 = uv;

    int max_iterations = 4;//-int(saw(spounge)*float(max_iterations)/2.0);

    float antispeckle = 1.0; 
    float magnification = 1.0;

	vec4 color = vec4(0.0);
    float map = 0.0;
    float border = 1.0;
    vec4 final = vec4(0.0);
    float c1 = 0.0;

    for(int i = 0; i < max_iterations; i++)
    {
        float iteration = float(i)/float(max_iterations);

        float d = -cos(float(i)*PI);

        vec2 o = circle(time*d)*(1.0-iteration)/2.0;


        float c = smoothstep(0.0, 1.0/E/E, 1.0-length(uv));
        if(i == 0) c1 = c;



        vec2 uv2 = (uv+o)*(E+iteration);
        float c2 = smoothstep(1.0/E, 1.0/GR, 1.0-length(uv2)*2.0/PI)*c;
        vec2 uv3 = (uv-o)*(E+iteration);
        float c3 = smoothstep(1.0/E, 1.0/GR, 1.0-length(uv3)*2.0/PI)*c;

    	uv = rotatePoint(vec3(uv, 0.0), vec3(0.0, 0.0, 1.0), d*rotation*2.0-PI/8.0).xy;
        uv = ((uv)*(1.0-c2)*(1.0-c3)+
            (uv2)*(c2)*(1.0-c3)+
            (uv3)*(1.0-c2)*(c3))*(antispeckle)+uv*(1.0-antispeckle)*c;


        border *= saw(1.0+c2*2.0)*saw(1.0+c3*2.0);

        float b = pow(border, iteration);
        float a = atan(uv.y, uv.x)/PI*.5+.5;
        float l = d*length(uv)*.5;
        map += smoothstep( 0.0, border*GR, GR*saw(c+c2+c3)*saw(border*saw(saw(2.0*(a+l))*saw(b*(d-c2)*(d-c3))*antispeckle)*(iteration+1.0)));

        antispeckle *= (clamp((c2+c3), 0.0, 1.0))*(1.0-saw(c2*2.0-c3)*saw(c3*2.0-c2));
        final += vec4(flux(map*PI+time*GR*E), 1.0);
    }
    fragColor = vec4(uv, 0.0, 1.0);

    float w = smoothstep(.8, 1.0, saw(map));



    map = smoothstep(0.0, 1.0, map);
    fragColor = ((w+final*(1.0-w)))*map*c1+(1.0-c1)*gradient;
}

#define MIRROR

bool mirrortouch = false;
float PI = 3.1415926535;
vec3 col = vec3(1.0, 1.0, 1.0);
vec3 mirror = vec3(0.0);
bool ismired = false;
vec3 stoppos = vec3(0.0);
float map(vec3 p){
    float mirrordis = dot(p,normalize(vec3(-2.0, 1.0, -1.0)));
    #ifdef MIRROR
    if(mirrordis < 0.01 && !ismired){
    	mirror = normalize(vec3(-2.0, 1.0, -1.0));
        mirrortouch = true;
    }
    float mirrordis1 = dot(p,normalize(vec3(2.0, 1.0, -1.0)));
    if(mirrordis1 < 0.01 && !ismired){
    	mirror = normalize(vec3(2.0, 1.0, -1.0));
		mirrortouch = true;
    }
    #endif
	vec3 q = fract(p) * 2.0 - 1.0;
	float ret = length(q) - 0.25;
    vec3 r = fract(p) * 2.0 - 1.0;
    float xpole = length(vec2(r.y, r.z));
    float ypole = length(vec2(r.x, r.z));
    float zpole = length(vec2(r.y, r.x));
    ret = min(ret, xpole - 0.05);
    ret = min(ret, ypole - 0.05);
    ret = min(ret, zpole - 0.05);
    if(!ismired){
    	ret = min(ret, mirrordis - 0.001);
        ret = min(ret, mirrordis1 - 0.001);
    }
	if(xpole <= 0.051){
    	col = vec3(1.0, 0.0, 0.0);
    }
	if(ypole <= 0.051){
    	col = vec3(0.0, 1.0, 0.0);
    }
	if(zpole <= 0.051){
    	col = vec3(0.0, 0.0, 1.0);
    }
    float yellowball = length(q - 0.5 * vec3(sin(iTime), sin(iTime + 1.0), sin(iTime + 2.0))) - 0.05;
    if(yellowball <= 0.001){
    	col = vec3(1.0, 1.0, 0.0);
    }
    ret = min(ret, yellowball);
    yellowball = length(q - 0.5 * vec3(sin(iTime+ PI * 1.2), sin(iTime + 1.0 + PI * 1.2), sin(iTime + 2.0 + PI * 1.2))) - 0.05;
    if(yellowball <= 0.001){
    	col = vec3(1.0, 1.0, 0.0);
    }
    ret = min(ret, yellowball);
    yellowball = length(q - 0.5 * vec3(sin(iTime+ PI * 0.4), sin(iTime + 1.0 + PI * 0.4), sin(iTime + 2.0 + PI * 0.4))) - 0.05;
    if(yellowball <= 0.001){
    	col = vec3(1.0, 1.0, 0.0);
    }
    ret = min(ret, yellowball);
    yellowball = length(q - 0.5 * vec3(sin(iTime+ PI * 1.6), sin(iTime + 1.0 + PI * 1.6), sin(iTime + 2.0 + PI * 1.6))) - 0.05;
    if(yellowball <= 0.001){
    	col = vec3(1.0, 1.0, 0.0);
    }
    ret = min(ret, yellowball);
    yellowball = length(q - 0.5 * vec3(sin(iTime+ PI * 0.8), sin(iTime + 1.0 + PI * 0.8), sin(iTime + 2.0 + PI * 0.8))) - 0.05;
    if(yellowball <= 0.001){
    	col = vec3(1.0, 1.0, 0.0);
    }
    ret = min(ret, yellowball);
    return ret;
}
float trace(vec3 o, vec3 r){
	float t = 0.0;
    float t1 = 0.0;
    vec3 p = vec3(0.0);
    p = o + r * t;
    for(int i = 0; i < 256; ++i){
    	p += r * t1; 
        float d = map(p);
        /*if(mirror != vec3(0.0) && !ismired){
        	r = reflect(r,mirror);
            mirror = vec3(0.0);
            ismired = true;
        }*/
        t1 = d * 0.5;
        t += t1;
        if(d < 0.001){
        	break;
        }
    }
    stoppos = p;
    return t;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 r = normalize(vec3(uv, 1.0));

    float the = iTime * 0.5;
    //r.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));

    vec3 o = vec3(0.0,2.5 + iTime, -3.0 + iTime);
    float t = trace(o, r);
    if(mirrortouch){
    	r = reflect(r, mirror);
        ismired = true;
        t += trace(stoppos, r);
    }

    float fog = 1.0 / (1.0 + t * t * 0.1);
    vec3 fc = vec3(fog) * col + vec3(0.1);
    fragColor = vec4(fc,1.0);
}

#define PADDING_X (0.1 * iResolution.x)
#define PADDING_Y PADDING_X

#define PADDING_X (0.1 * iResolution.x)
#define PADDING_Y PADDING_X



float integrate(vec2 start, vec2 end, vec2 pt) {
    // start = start of segment, end = end of segment, point = point in space.
    /* compute line segment to point light path integral... generated with
 		wolfram alpha ... good luck understanding this :) */
	float r = start.x;
	float s = end.x;
	float p = start.y;
	float q = end.y;

	float t = pt.x;
	float u = pt.y;
	float x = 1.0;
	float v1 = (log(sqrt(p*p - 2.0*p*q + q*q + (r - s)*(r - s)) * sqrt(p*p *x*x + 2.0* q * (x - 1.0)*(u - p*x) - 2.0*p*u*x + q*q*((x - 1.0)*(x - 1.0))+ r*r*x*x + 2.0*s*(x - 1.0)*(t - r*x) - 2.0*r*t*x + s*s*(x - 1.0)*(x - 1.0) + t*t + u*u) + p*p*x - 2.0*p*q*x + p*q - p*u + q*q*x - q*q + q*u + r*r*x - 2.0*r*s*x + r*s - r*t + s*s*x - s*s + s*t))/sqrt(p*p - 2.0*p*q + q*q + ((r - s)*(r - s)));
	x = 0.0;
	float v2 = (log(sqrt(p*p - 2.0*p*q + q*q + (r - s)*(r - s)) * sqrt(p*p *x*x + 2.0* q * (x - 1.0)*(u - p*x) - 2.0*p*u*x + q*q*((x - 1.0)*(x - 1.0))+ r*r*x*x + 2.0*s*(x - 1.0)*(t - r*x) - 2.0*r*t*x + s*s*(x - 1.0)*(x - 1.0) + t*t + u*u) + p*p*x - 2.0*p*q*x + p*q - p*u + q*q*x - q*q + q*u + r*r*x - 2.0*r*s*x + r*s - r*t + s*s*x - s*s + s*t))/sqrt(p*p - 2.0*p*q + q*q + ((r - s)*(r - s)));
	return 1000.0*(v1 - v2);
}


float compute_light_from_segment(vec2 start, vec2 end, vec2 pt) {
    return integrate(start, end, pt);
}    


void mainImage( out vec4 fragColor, in vec2 pos )
{

    vec2 outerMin = vec2(PADDING_X, PADDING_Y);
    vec2 outerMax = vec2(250, 250);

   	float d = 0.0;
    float sz = 1.3;
    // compute the light contribution from each fluorescent segment
    d += compute_light_from_segment(outerMin - vec2(sz, 0.0), vec2(outerMin.x, outerMax.y)  - vec2(sz, 0.0), pos);
    d += compute_light_from_segment(vec2(outerMax.x, outerMin.y) + vec2(0.0, -sz), outerMin + vec2(0.0, -sz), pos);
    d += compute_light_from_segment(vec2(outerMin.x, outerMax.y) + vec2(0.0, sz), outerMax+ vec2(0.0, sz), pos);
    d += compute_light_from_segment(vec2(outerMax.x, outerMin.y)+ vec2(sz, 0.0), outerMax+ vec2(sz, 0.0), pos);


    vec4 mixColor =  vec4(111.0/255.0, 112.0/255.0, 106.0/255.0, 1.0);
    if (pos.x > outerMin.x && pos.x < outerMax.x && pos.y > outerMin.y && pos.y < outerMax.y) {
        mixColor = vec4(104.0/255.0, 109.0/255.0, 113.0/255.0, 1.0);
    }

    vec4 mixColor2 = vec4(98.0/255.0, 94.0/255.0, 91.0/255.0, 1.0);

    float alpha = (sin(iTime) + 1.0) / 2.0;

    mixColor = alpha * mixColor + (1.0 - alpha) * mixColor2;

    fragColor = mix(max(0.04, 0.01*sin(iTime*0.2))* d * mixColor, mixColor, 0.4);
}

#define PI 3.14159265

/**
 *   Original: https://www.shadertoy.com/view/MdX3zr
 *   Попытка разобраться как работает шейдер пламени
 */


/**
 * Функция генерации шума. Алгоритм:
 * 1. делим пространство на кубическую (т.к. 3D-шейдер) сетку
 * 2. находим некий псевдо-случайный угол для каждой вершины куба
 * 3. находим косинус псевдо-случайного угла для каждой вершины
 * 4. находим псевдо-случайное значеие для точки в кубе: 
 *    - находим проекцию на горизонтальные ребра куба (интерполяция по x)
 *    - находим проекцию на перднюю и заднюю грань куба (интерполяция по y)
 *    - находим точку между передней и задней гранью (интерполяция по z)
 */
float hash(float angle) {
    return sin(cos(angle) * angle);
}

float valueNoise(vec3 currentRayPosition)
{
    // найдем координаты куба в котором находится currentRayPosition

    // вначале находим дальнюю левую нижнюю точку сетки куба:
    vec3 integer = floor(currentRayPosition); 

    // находим локальную позицию точки внутри куба
    // можно было бы записать так:
    // vec3 f = currentRayPosition - integer
    // но можно проще, что равносильно:
    vec3 f = fract(currentRayPosition);

    // в пределах куба f изменяется линейно, от 0 до 1
    // мы можем использовать f в качестве функции для интерполяции 
    // то есть по f будем находить псевдо-рандомное значение для каждой точки куба

    // в оригинале - косинусная интерполяция, 
	// vec3 f = cos(fractional * PI)*(-.5)+.5;    
    // которая дает более сглаженый шум в сравнении с линейным:
    // https://www.desmos.com/calculator/8zudfzbmyd
    // но на данном шейдере разницы практически не заметно
    // подробнее про функции интерполяции:
    // http://paulbourke.net/miscellaneous/interpolation/

    float dx = 1.;   // изменение (дельта) угла по оси x
    float dy = 57.;  // изменение (дельта) угла по оси y
    float dz = 21.;  // изменение (дельта) угла по оси z

    // угол = скалярному произведению вектора i и вектора значений изменения угла
    float angle = dot(integer, vec3(dx, dy, dz));

    // находим косинус для каждого угла по формуле y = sin(cos(x) * x), график функции 
    // https://www.desmos.com/calculator/nhkq8uwxqm
    // для дальней стороны куба
    float fbl = angle; // нижний левый угол
    float fbr = angle + dx; // нижний правый
    float ftl = angle + dy; // верхний левый
    float ftr = angle + dy + dx; // верхний правый

    // и для ближней стороны куба
    float nbl = angle + dz; // нижний левый угол
    float nbr = angle + dz + dx; // нижний правый
    float ntl = angle + dz + dy; // верхний левый
    float ntr = angle + dz + dy + dx; // верхний правый  

    // находим проекцию точки на горизонтальные грани  интерполируя от левого к правому углу 
    // для дальней стороны
    float ft = mix(hash(fbl), hash(fbr), f.x);
    float fb = mix(hash(ftl), hash(ftr), f.x);
    // и ближней
    float nt = mix(hash(nbl), hash(nbr), f.x);
    float nb = mix(hash(ntl), hash(ntr), f.x);

    // находим проекцию точки на сторону куба интерполируя от проекции на верхней грани к нижней
    // для дальней стороны
    float far = mix(ft, fb, f.y);
    // и ближней     
    float near = mix(nt, nb, f.y); 

    // находим значение для точки интерполируя от дальней стороне куба к ближней     
    float result = mix(far, near, f.z);
    return result;
}

vec2 rotate(vec2 p, float theta) {
	return vec2(
		p.x*cos(theta)+p.y*sin(theta),
		p.x*sin(theta)-p.y*cos(theta)
	);
}


float makeNoise(vec3 currentRayPosition) {
    vec3 currentRayPositionForFlame = currentRayPosition;
    currentRayPositionForFlame.y += iTime*2.;

    // первая октава шума Перлина
    // http://libnoise.sourceforge.net/glossary/#octave
    float distanceToFlame = valueNoise(currentRayPositionForFlame);

    // вторая октава
    // 3 - частота (чем больше - тем больше зернистость, или деформация)
    // 0.5 - сокращает амплитуду на 2 (чем меньше - тем больше скученность) 
    distanceToFlame += valueNoise(currentRayPosition*3.) * 0.5;

    // придаем форму пламени
    // с увеличением Y, увеличивается площадь по xz
    distanceToFlame *= currentRayPosition.y * .25;
	return distanceToFlame;
}


// signed-distance function - функция расстояния до сферы по признаку
// currentRayPosition - это текущая позиция конкретного луча
float sdSphere(vec3 currentRayPosition)
{
    float sphereRadius = 1.; // радиус сферы
    vec3 spherePosition = vec3(0.0, 1., 0.0); //координаты сферы    
    vec3 sphereModificator = vec3(1.0, 0.5, 1.0); //вытягиваем сферу по y (также влияет на пламя)

    float distanceToSphere = length(currentRayPosition * sphereModificator + spherePosition) - sphereRadius;

	return distanceToSphere; // внутри сферы < 0, на границе = 0, снаружи > 0
}

// signed distance function
// функция расстояния до сферы с эффектом пламени
float flame(vec3 currentRayPosition)
{
    // поворачиваем пламя по оси Y
	//currentRayPosition.xz = rotate(currentRayPosition.xz, iTime);

    float flameDistance = 0.;
	flameDistance += sdSphere(currentRayPosition);
    flameDistance += makeNoise(currentRayPosition);

    // в итоге учитываем расстояние до сферы и до noise
    // причем сфера и noise считаются как один объект
    return flameDistance;
}


// signed distance function
// сцена - то есть граница, за которой лучи перестают быть видимыми
float scene(vec3 currentRayPosition)
{
    // abs - модуль, flameDistance всегда положительно
    // значит пламя находится всегда в пределах сцены 
    float flameDistance = abs(flame(currentRayPosition)); 
    float rayMaxLength =  100.; // максимальная длина луча

    float cutoff = rayMaxLength - length(currentRayPosition);
	// если длина луча больше максимального значения (cutoff отрицательный) - 
    // отсекаем дальнейший рендеринг в raymarch условием if( distanceToScene > epsilon )
    return min(cutoff, flameDistance); 
}


vec4 raymarch(vec3 cameraOrigin, vec3 rayDirection)
{
    /**
	 * Эта функция итеративно анализирует сцену 
     * чтобы приблизить ближайший момент достижения лучом поверхности
	 */
	float distanceToScene = 0.0; 
    float color = 0.0; // если луч не достиг какого-либо объекта - возвращаем черный цвет
    float epsilon = 0.02;

    vec3 currentRayPosition = cameraOrigin; // луч начинает движение от камеры

    bool glowed = false;

    int maxRaySteps = 64; // максимальное число шагов луча

    // с каждой итерацией двигаем луч дальше от камеры на расстояние = мин. дистанции до ближайшего объекта 
	for(int i=0; i<maxRaySteps; i++)
	{

		// получаем расстояние от текущей позиции луча до сцены + погрешность epsilon 
        distanceToScene = scene(currentRayPosition) + epsilon;

        // двигаем луч в соответствии с направлением direction и шагом distanceToSphere (rayDirection направляет к текущему uv.xy)
		currentRayPosition += distanceToScene * rayDirection;

        // Если луч еще не преодалел максимальное значение
		if( distanceToScene > epsilon )
		{
            float distanceToFlame = flame(currentRayPosition);
            if(distanceToFlame < .0) {
                // Луч достиг объекта пламени
                // Ставим флаг что луч прошел сквозь пламя  
				glowed=true;
            }
            if (glowed) {
                // Если наш луч начал проходить/прошел сквозь пламя
                // увеличиваем интенсивность цвета от 0 до 63/64
                // Чем дальше луч - тем ярче цвет
            	color = float(i) / float(maxRaySteps);
            }
		}
	}
	return vec4(currentRayPosition, color); // vec4(xyz позиция пикселя с формой, цвет)
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0; //[0,0] - центр, [-1,-1] < [uv.x,uv.y] < [1,1]
	uv.x *= iResolution.x/iResolution.y; //пропорции

    // Чтобы было 3D - нам нужно "отодвинуть" источник векторов (или лучей) от плоскости по оси z.
	// Для этого создаем камеру. От нее будем запускать лучи к нашим объектам.
    // x - это сдвиг камеры по горизонтали, y - по вертикали, z - отдаляет или приближает камеру
    // положительный z - право-сторонняя система координат, отрицательный - левосторонняя
    vec3 cameraOrigin = vec3(0., -2. , 4.);

    // Далее, нам нужно задать направление движения лучей от камеры к объектам
    // Для этого используем нормализованный вектор rayDirection
    // xy - это текущие координаты точки в uv
    // коэффициент при x - сплющили поле зрения на 1.6
    // -1 при y - инверсия, перевернули всю картинку кверх ногами
    // z - направление движения луча, должно быть от камеры, то есть у cameraOrigin мы будем уменьшать z
    // число z также влияет на поле зрения (как зум у фотокамеры)
    // чем меньше z, тем больше поле зрения
    // вспоминаем свойство нормализации - xyz изменяются пропорционально
    // чем больше значение какого-либо компонента - тем меньше значение других компонентов
    // соответственно uv.x, uv.y будут меньше чем на самом деле и вся сцена будет казаться меньше
    vec3 rayDirection = normalize(vec3(uv.x*1.6, -uv.y, -1.5));

    vec3 currentRayPosition = vec3(0.);
    float shape = 0.;
    float limits = 0.5;
    // сужаем область raymarch до ~1/3 экрана (от -0.5 до 0.5)
    if (uv.x > -limits && uv.x < limits) {
	    vec4 result = raymarch(cameraOrigin, rayDirection);

	    currentRayPosition = result.xyz;
        shape = result.w;
    }

    vec3 orange = vec3(1.,.5,.1); // оранжевый цвет
    vec3 blue = vec3(0.1,.5,1.); // голубой цвет
    float colorChange = currentRayPosition.y*.02+.4;

    vec3 blueToOrange = mix(orange, blue, colorChange); // 3 аргументом задаем формулу перехода из голубого цвета в ораньжевый

    vec3 black = vec3(0.); //цвет фона - черный
	vec3 result = mix(black, blueToOrange, pow(shape*2., 4.)); //добавляем плавный переход в черный    

    fragColor = vec4(result, 1.0);    
}

void mainImage( out vec4 o, in vec2 p ){
	const float TAU = atan(0.,-1.) * 2.;
    const float FACES = 4.;
    p = ((p.xy/iResolution.y)-(iResolution.xy/iResolution.y)/2.)*2.;
    o = vec4( .5 );
    float twist = iTime + sin(p.y) * sin(iTime);
    for( float i = 0.; i< FACES; i+=1.){
        vec2 edge = vec2(i, i+1.);
        edge = sin((edge/FACES) * TAU + twist );
        if(p.x >= edge.x && p.x <= edge.y){
            o = vec4( mod( i, 2.) );
            return;
        }
    }
}

const float M_PI6 = 3.14159265359/6.0;
const float M_PI3 = 3.14159265359/3.0;
const float sqrt32 = 2.0/sqrt(3.0);
const float sr = sqrt(3.0)/2.0;
const vec2 rat = vec2(1.0,sr);

float hash(vec2 v) { return dot(v,vec2(1., -1178.65)); }

vec4 HexagonWithId(vec2 uv){
    vec2 uv2 = (uv)/rat;
    float dmin = 1e9, d2min=1e9, nmin=-1., nmin2 = -1.;
    for (int i=0; i<7; i++)
    {
        float r = float(i)*M_PI3-M_PI6;
        vec2 uv3 = uv2-vec2(0.5,0.75)+((i!=6)?vec2(sin(r),cos(r)*sqrt32):vec2(0.0,0.0));
        vec2 hexas = uv3+vec2(floor(uv3.y)*0.5,1.0);      
        vec2 c = uv3+vec2(1.0,1.0)-fract(hexas);
        float n = hash(floor(hexas));
        vec2 dv = (c-uv2)*rat;
        float d = length(dv);
        if (d<dmin) { d2min=dmin; dmin=d; nmin2=nmin; nmin=n; }
        else if (d<d2min) { d2min=d; nmin2=n; }
    }
    return vec4(dmin,d2min,nmin,nmin2);
}

vec4 TriangularWithId(vec2 uv){
  	vec2 uv2 = (uv)/rat;
  	float dmin = 1e9, d2min=1e9, nmin=-1., nmin2 = -1.;
    vec2 c =  (uv2+uv2.y*vec2(-0.5,1.0))*vec2(1.0,0.5);  
    vec2 fr =fract(c);
    float even = floor(fr.x+ fr.y);
    float fix = floor(fract(uv2.y*0.5)*2.0)*0.5;
    vec2 c2 = uv2-mod(c,vec2(1.0,1.0));
    vec2 p = floor(c2)+vec2(0.5+fix,1.0/3.0);
    vec2 dv = (p-uv2)*rat;

    float d =length(dv);
    dmin =d;
    nmin= hash(floor(p+20.0));
    p = (floor(c2)+vec2(1.0+fix,2.0/3.0));
    dv = (p-uv2)*rat;
    d =length(dv);
    if(dmin>d) {
        d2min=dmin;
        dmin=d;
        nmin2 = nmin;
        nmin = hash(floor(p));
    }
    else {
        d2min=d;
        nmin2 = hash(floor(p));
    }
    for(int i=0; i<2;i++)
    {
        p = (floor(c2)+vec2(even+fix+0.5*float(i),2.0/3.0-float(i)*1.0+2.0/3.0*even));
        dv = (p-uv2)*rat;
        d =length(dv);
        if (d<d2min) { d2min=d; nmin2=hash(floor(p+even*20.0));; }
    }
  return vec4(dmin,d2min,nmin,nmin2);
}

float rnd(float x) { return fract(1000.*sin(234.56*x)); }
vec3 rnd3(float x) { return vec3(rnd(x),rnd(x+.1),rnd(x+.2)); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float dz = (sin(iTime*0.057843));
    float dw = (sin(iTime*0.084532)+1.0);
    float dv = (sin(iTime*0.07328834));
	vec2 uv = vec2(fragCoord.xy / iResolution.xy)*
      vec2(iResolution.x/iResolution.y,1.0); //scale to normal width
    float r = iTime*0.054387;
    float sr= sin(r);
    float cr= cos(r);
    float zx= 8.0-6.0*dz;
    uv= uv- vec2(iResolution.x/iResolution.y, 1.0)*0.5;
    uv= uv* mat2(sr,cr,-cr,sr)*zx;
    uv= uv + vec2(iResolution.x/iResolution.y,1.0)*0.5;
    vec3 color= vec3(0.0,0.0,0.0);
    float bw = 0.0;
    vec4 he=(((fragCoord.y/iResolution.y)-fragCoord.x/iResolution.x)<dv)?TriangularWithId(uv+vec2(0.5,0.0)):HexagonWithId(uv);
    float dx = (sin(iTime)+1.0)*0.5;
    float dy = (sin(0.52344*iTime)+1.0)*0.5;
    if(fragCoord.x>iResolution.x*dy){
        if(fragCoord.y>iResolution.y*dx) {
          bw =he.z;
    	  color = rnd3(bw)*vec3(0.75,1.0,0.75);
        }
        else
        {
           bw =he.w;
    	   color = rnd3(bw)*vec3(0.75,1.0,0.75);
        }
    }
    else{
        if(fragCoord.y>iResolution.y*(1.0-dx)) {
        	bw = he.x;
        	color = vec3(bw);
        }else{
           bw =he.y;
    	   color = vec3(bw);
        }
    }
    color = (abs(he.x-he.y) > 0.02 ||  (fragCoord.y/iResolution.y+fragCoord.x/iResolution.x)<dw)?color:vec3(0.0,0.0,0.0);
	fragColor = vec4(color,1.0);
}

