//Extra function - Maps values
float map(float x, float in_min, float in_max, float out_min, float out_max)
{
	return (x - in_min) * (out_max - out_min) / (in_max - in_min) + out_min;
}

//Function 1 - Creates Poligons
float createPolygon(vec2 uv, float ratio, float sides, float size, vec2 pos)
{
    uv -= vec2(pos.x*ratio, pos.y);
	float a = atan(uv.x, uv.y)+PI;
    float r = TWO_PI/sides;
	float dist = cos(floor(.5+a/r)*r-a)*length(uv);
    dist = 1.-step(size, dist);
    return dist;
}

//Function 2 - Rotates by Angle
mat2 rotate2d(float angle)
{
	return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}

//Function 3 - Scales by given value
mat2 scale2d(vec2 value)
{
    return mat2(value.x, 0, 0, value.y);
}

//Function 4 - Creates circles
float createCircle(vec2 uv, float ratio, float size, vec2 pos)
{
    vec2 center = vec2(pos.x*ratio, pos.y);
	float unr = abs(sin(size));
	float r = map(unr, 0.0, 1.0, 0.1, 0.3);

    float dist = distance(center, uv);

    float value = step(dist, r);
    return value;
}

//Function 5 - Creates Rectangles (modifiable lenght and width)
float createRect(vec2 uv, float ratio, vec2 size, vec2 pos)
{
    float value = step(pos.x - size.x, uv.x) - step(pos.x + size.x, uv.x);
    float value2 = step(pos.y - size.y, uv.y) - step(pos.y + size.y, uv.y);
    value *= value2;
    return value;
}
