float amplitude = 5.0;
float moveSpeed = 150.0;
float waveSpeed = 2.0;
float width = 40.0;

vec2 distort(in vec2 point) {
    float shift = iTime * 4.0;

    // gl_FragColor = texture2D(uSampler, pixelCoord * pixelSize);

    return vec2(
        point.x + sin(point.y * 0.0125 + shift) * width / 4.0,
        point.y + cos(point.x * 0.0125 + shift) * width / 8.0
    );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Current position in pixels
    float shift = sin(iTime * 4.0);

//    float border = (iResolution.y - amplitude) - sin((fragCoord.x + iTime) / (speed * width)) * amplitude * shift;

    fragCoord = distort(fragCoord);
    // fragColor = vec4(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y, 0.0, 1.0);
    // return;

    float border = iResolution.y / 2.0;
    border = border + sin((fragCoord.x + iTime * moveSpeed) / width) * amplitude * sin(iTime * waveSpeed);

    if (fragCoord.y < border) {
        //fragColor = vec4(0.0, 0.25, max(0.0, border / (fragCoord.y * 2.0)), 0.5);
        fragColor = vec4(0.0, (fragCoord.y / border), 0.75, 0.0);
    } else {
        // discard;
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
        // gl_FragColor = vec4(abs(sin(vTextureCoord.x + time)), abs(sin(vTextureCoord.y + time)), 0, 1);
    }
}

vec3 interpolate(vec3 x1, vec3 x2, float a) {
    float k = pow((1.0 - cos(a*3.14))/2.0, 1.0);
    return x1*(1.0 - k) + k*x2;
}

vec4 colormap(float x) {
    vec3 c1 = vec3(0, 7, 100)/255.0;
    vec3 c2 = vec3(32, 107, 203)/255.0;
    vec3 c3 = vec3(237, 255, 255)/255.0;
    vec3 c4 = vec3(255, 170, 0)/255.0;
    vec3 c5 = vec3(0, 2, 0)/255.0;

    if(x < 0.16) {
        float a = x/0.16;
        return vec4(interpolate(c1, c2, a), 1.0); 
    }
    else if(x < 0.42) {
        float a = (x - 0.16)/(0.42 - 0.16);
        return vec4(interpolate(c2, c3, a), 1.0);
    }
    else if(x < 0.6425) {
        float a = (x - 0.42)/(0.6425 - 0.42);
        return vec4(interpolate(c3, c4, a), 1.0);
    }
    else if(x < 0.8575) {
        float a = (x - 0.6425)/(0.8575 - 0.6425);
        return vec4(interpolate(c4, c5, a), 1.0);
    }
    else {
        float a = (x - 0.8575)/(1.0 - 0.8575);
        return vec4(interpolate(c5, c1, a), 1.0);
    }
}


//math
vec2 cmult(vec2 z1, vec2 z2) {
    return vec2(z1.x*z2.x - z1.y*z2.y, z1.x*z2.y + z1.y*z2.x);
}


const int maxIter = 300;
float mandelbrot(vec2 c) {
    vec2 z = vec2(0.0, 0.0);
    vec2 zd = vec2(0.0, 0.0);
    for(int i = 0; i < maxIter; i++) {
        if(length(z) > 20.0) break;
        zd = 2.0*cmult(z, zd) + vec2(1.0, 0.0);
        z = cmult(z, z) + c;
    }

    float d = 0.5*length(z)*log(length(z))/length(zd);
    return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 offset = vec2(-0.749, 0.101);
    float scale = 1.0/pow((0.01*float(iFrame)), 3.0);
	vec2 uv = scale*(2.0*fragCoord.xy - iResolution.xy) / iResolution.y + offset;

    float d = mandelbrot(uv);
    vec3 color = vec3(0);
    if(d > 0.0) {
        d = clamp(8.0*d/scale, 0.0, 1.0);
    	d = pow(d, 0.05);
        color = colormap(1.0 - d).xyz;
    }
    else {
        color = vec3(0);
    }

    fragColor = vec4(color, 1.0);
}

vec2 r(vec2 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return vec2(c * p.x - s * p.y, s * p.x + c * p.y);
}

float sdSquare(vec2 p, float s) {
	vec2 d = abs(p) - s;
	return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

#define TWO_PI 6.2832

vec2 opRepeatRadial(vec2 p, int sectorCount) {
    float fSectors = float(sectorCount);
    float segmentAngle = (floor((atan(p.y, p.x) / TWO_PI - 0.5) * fSectors) + 0.5) * TWO_PI / fSectors;
    return -r(p, -segmentAngle);
}

float evaluate(vec2 uv, float time, float timeOffset) {
	float timeOffsetMultiplier = (1. - .8*length(uv)/0.5) * 0.913;
	uv *= (1. + .02 * sin(1.13 * time + (timeOffset * timeOffsetMultiplier)));
	const float ringWidth = 0.07;
	float ringIndex = floor(length(uv) / ringWidth - 0.5) + 0.5;
	float centerX = (ringIndex + 0.5) * ringWidth;
	float centerness = 1. - ringWidth * ringIndex;
	float ringRotation = time * 0.6 * pow(centerness, 4.);
	vec2 repeatedUV = opRepeatRadial(r(uv, ringRotation), (int(ringIndex) + 1) * 6);
	float squareSize = 0.013 + 0.01 * sin(length(uv) * 11.1 + time * 0.6);
	float d = sdSquare(r(repeatedUV - vec2(centerX, 0.), sin(time * centerness * 2.3 + uv.y * 3.1 - uv.x * 2.3)), squareSize);
	float value = smoothstep(0., 0.001, d);
	value = max(max(value, float(ringIndex > 6.)), float(ringIndex < 1.));
	return 1. - value;
}

vec4 aberrate(vec2 uv, float time) {
	float aberrationAmount = 0.2 * pow(max(0., 1. - length(uv) * 2.), 1.3) + 0.1 * sin(time * 0.73 + length(uv) * 1.1);
	return (vec4(1.0) - evaluate(uv, time, 0.) * vec4(1,0,0,0) - evaluate(uv, time + aberrationAmount, 1.) * vec4(0,1,0,0) - evaluate(uv, time + 2. * aberrationAmount, 2.) * vec4(0,0,1,0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float aspect = iResolution.y / iResolution.x;

	uv -= 0.5;
	uv.y *= aspect;
	//uv *= 1.1;

	fragColor = aberrate(uv, iTime) * aberrate(uv*(1. + .12 * sin(iTime * 0.331)), 4.11 - iTime * 0.96);
}

#define PI 3.14159265359
#define TWO_PI 6.28318530718
// Select the pattern
#define patternSelect 1

float createPolygon(vec2 uv, int sides)
{
	// Angle and radius from the current pixel
	float a = atan(uv.x,uv.y)+PI;
	float r = TWO_PI/float(sides);

	// Shaping function that modulate the distance
	float dist = cos(floor(.5+a/r)*r-a)*length(uv);

    float value = 1. - smoothstep(0.4,0.41, dist * 3.);
    return value;
}

float createPolygon2(vec2 uv, int sides)
{

	// Angle and radius from the current pixel
	float a = atan(uv.x,uv.y)+PI;
	float r = TWO_PI/float(sides);

	// Shaping function that modulate the distance
	float dist = cos(floor(3.+a/r)*r-a)*length(uv);


    float value = 1. - smoothstep(0.4,0.41, dist * 3.);
    return value;
}

float createCircle(vec2 circle, vec2 uv, float radius, float parts)
{
    float dist = distance(circle,uv)*2.;
    uv -= circle;
    float angle = atan(uv.y, uv.x);

   	angle += sin(cos(iTime)) * 3.;
    radius = cos(parts*angle);

    float value = 1. - step(radius,dist);

    return value;
}

float createCircle2(vec2 circle, vec2 uv, float radius, float parts)
{
    float dist = distance(circle,uv);
    uv -= circle;
    float angle = atan(uv.y, uv.x);

    radius = cos(parts*angle);
    radius += sin(iTime);
    radius -= cos((iTime)*3.);

    float value = 1. - step(radius,dist);

    return value;
}

float createCircle3(vec2 circle, vec2 uv, float radius, float parts)
{
	float dist = distance(circle, uv);
    uv -= circle;
    float angle = atan(uv.y, uv.x);

    radius += cos(tan(iTime));

    float value = 1. - step(radius,dist);

    return value;
}

float createCircle4(vec2 circle, vec2 uv, float radius, float parts)
{
	float dist = distance(circle, uv);
    uv -= circle;
    float angle = atan(uv.y, uv.x);

	angle += abs(sin(iTime));
    radius -= floor(sin(parts*angle));

    float value = 1. - step(radius,dist);

    return value;
}

mat2 rotate2d(float angle)
{
	return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}

mat2 scale2d(vec2 value)
{
	return mat2(value.x, 0, 0, value.y);
}

