float createCircle(float rad, float dist){
  float val = 1.0 - step(rad,dist);
  //vec3 color = vec3(1.0);
  //color = val * col;
  return val;  

}

float createRectangle(vec2 size,vec2 pos, vec2 uv){
   float value = step(pos.x,uv.x) - step(pos.x+size.x,uv.x);
   value *= step(pos.y,uv.y) - step(pos.y+size.y,uv.y);
   return value;
}

float createStar(float locationX, float locationY,vec2 uv, float ratio,float angleToAdd, float smoothStepX, float smoothStepY, float smoothStepScale,float triangleScale,float globalTime) {
	vec2 shapePos = vec2(locationX*ratio, locationY);
    uv -= shapePos;


    float angle = atan(uv.x,uv.y)+PI;
    angle += 0.65;
    angle += angleToAdd;
    angle += globalTime;
    float radius = TWO_PI/float(5);
    float dists =cos(floor(.5 +angle/radius)*radius-angle)*length(uv);


	float value = 1. - smoothstep(smoothStepX,smoothStepY,dists * smoothStepScale);
    value += createTriangle(0.0,.15,uv,ratio,0.0,.4,.41, triangleScale,globalTime);
    value += createTriangle(- 0.08,0.05,uv,ratio,1.2,.4,.41,triangleScale,globalTime);
    value += createTriangle(-.055,-0.11,uv,ratio,0.4,.4,.41,triangleScale,globalTime);
    value += createTriangle(.08,.05,uv,ratio,0.90,.4,.41,triangleScale,globalTime);
    value += createTriangle(.055,-0.11,uv,ratio,-0.4,.4,.41,triangleScale,globalTime);  


    return value;

}

float createTriangle(float locationX, float locationY,vec2 uv, float ratio,float angleToAdd, float smoothStepX, float smoothStepY, float smoothStepScale,float globalTime){
	vec2 shapePos = vec2(locationX*ratio, locationY);
    uv -= shapePos;


    float angle = atan(uv.x,uv.y)+PI;
    angle += angleToAdd;
    angle += globalTime;
    float radius = TWO_PI/float(3);
    float dists =cos(floor(.5+angle/radius)*radius-angle)*length(uv);


	float value = 1. - smoothstep(smoothStepX,smoothStepY,dists * smoothStepScale);
    return value;
}

float createDiamond(float locationX, float locationY,vec2 uv, float ratio,float angleToAdd, float smoothStepX, float smoothStepY,float globalTime){
    vec2 shapePos = vec2(locationX*ratio, locationY);
    uv -= shapePos;

    float value = createTriangle(0.0,0.04,uv,ratio,0.0,smoothStepX,smoothStepY,11.6,globalTime);
    value += createTriangle(0.0,-0.03,uv,ratio,1.05,smoothStepX,smoothStepY,11.6,globalTime);
    return value;
}

float createPoly(float locationX, float locationY, int corners, vec2 uv, float ratio,float angleToAdd) 
{

    vec2 shapePos = vec2(locationX*ratio, locationY);
    uv -= shapePos;


    float angle = atan(uv.x,uv.y)+PI;
    angle += angleToAdd;
    float radius = TWO_PI/float(corners);
    float dist =cos(floor(.5+angle/radius)*radius-angle)*length(uv);

    return dist;
}

