        color.rgb = diffuse*color.rgb;
    }
     //draw light source
    isHit = hitSphere(camera,dir,lightSource.center,lightSource.radius,intersect);

    if(isHit && intersect.z > mindist)
    {
        mindist = intersect.z;
        color= vec4(1.0,1.0,1.0,1.0);
    }
    //each triangle in scene
    for(int i=0;i<2;i++){
        vec4 a=triangles[i].a;
        vec4 b=triangles[i].b;
        vec4 c=triangles[i].c;
        vec4 n=triangles[i].n;


        vec4 ra = mv*a;
        vec4 rb = mv*b;
        vec4 rc = mv*c;
        vec4 rn = mv*n;

        vec3 uvt;
        bool isHit =hit(camera,dir,ra.xyz,rb.xyz,rc.xyz,uvt);
        if(isHit)
        {
            vec3 intersect = camera + dir*uvt.z;
            float z = intersect.z;
            // depth  buffer test
			if( z > mindist )
			{
				mindist = z;
                //lambertian
                float diffuse = dot(lightdir,rn.xyz);
                vec3 mate = texture( iChannel0, uv ).xyz;
                color = vec4(mate,1.0);
                color.rgb = diffuse*color.rgb;

                //shadow on floor test

                //hit point to light vector
                vec3 shadowray = lightSource.center - intersect;
               	shadowray = normalize(shadowray);

                vec3 uvt;
                //is shadow ray hits sphere?
                bool isOccluded = hitSphere(intersect,shadowray,sphere.center,sphere.radius,uvt);
                if(isOccluded){
                    color.rgb *= 0.5;
                }

			}
        }
    }//end i loop

    fragColor = color;
}
