// Distance to line segment between a and b
float fLineSegment(vec2 p, vec2 a, vec2 b) {
	vec2 ab = b- a;
	float t = saturate(dot(p- a,ab)/ dot(ab,ab));
	return length((ab* t+ a)- p);
}
