//
// scale-invariant distance meter
//
// be less confused by your distances!
//
// (as seen in the screenshots on http://mercury.sexy/hg_sdf/)
//
// tries to paint a plane in useful colors. outputs HDR colors, so can easily
// be integrated into your existing rendering without looking bad.
//
//

// the actual SDF:
float f(vec3 p) {
    return fSvgWorldIcon(p);
}

// tone mapping stolen from zavie's shader
vec3 Uncharted2ToneMapping(vec3 color) {
	float A = 0.15;
	float B = 0.50;
	float C = 0.10;
	float D = 0.20;
	float E = 0.02;
	float F = 0.30;
	float W = 11.2;
	float exposure = 2.;
	color *= exposure;
	color = ((color* (A* color+ C* B)+ D* E)/ (color* (A* color+ B)+ D* F))- E/ F;
	float white = ((W* (A* W+ C* B)+ D* E)/ (W* (A* W+ B)+ D* F))- E/ F;
	color /= white;
	return color;
}

vec3 fusion(float x) {
// the meter uses the "fusion" gradient, which goes from dark magenta (0) to white (1)
// (often seen in heatmaps in papers etc)
	float t = clamp(x,0.0,1.0);
	return clamp(vec3(sqrt(t),t* t* t,max(sin(PI* 1.75* t),pow(t,12.0))),0.0,1.0);
}
vec3 fusionHDR(float x) {
// HDR version
	float t = clamp(x,0.0,1.0);
	return fusion(sqrt(t))* (0.5+ 2.* t);
}

vec3 distanceMeter(float dist, float rayLength, vec3 rayDir, float camHeight) {
// distance meter function. needs a bit more than just the distance
// to estimate the zoom level that it paints at.
//
// if you have real opengl, you can additionally use derivatives (dFdx, dFdy)
// to detect discontinuities, i had to strip that for webgl
//
// visualizing the magnitude of the gradient is also useful
	float idealGridDistance = 20.0/ rayLength* pow(abs(rayDir.y),0.8);
	float nearestBase = floor(log(idealGridDistance)/ log(10.));
	float relativeDist = abs(dist/ camHeight);

	float largerDistance = pow(10.0,nearestBase+ 1.);
	float smallerDistance = pow(10.0,nearestBase);

	vec3 col = fusionHDR(log(1.+ relativeDist));
	col = max(vec3(0.),col);
	if((dist)< 0.) {
		col = col.grb* 3.;
	}

	float l0 = (pow(0.5+ 0.5* cos(dist* PI* 2.* smallerDistance),10.0));
	float l1 = (pow(0.5+ 0.5* cos(dist* PI* 2.* largerDistance),10.0));

	float x = fract(log(idealGridDistance)/ log(10.));
	l0 = mix(l0,0.,smoothstep(0.5,1.0,x));
	l1 = mix(0.,l1,smoothstep(0.0,0.5,x));

	col.rgb *= 0.1+ 0.9* (1.- l0)* (1.- l1);
	return col;
}
vec4 mainImage_meter(vec2 fragCoord) {
	vec2 reso = iResolution.xy;
	vec2 screenCoord = (((2.0* fragCoord)- reso)/ iResolution.y);
	float camDolly = iMouse.z> 0.? iMouse.y/ iResolution.y: (.5)+ (.5)* sin(iTime* .5);
	float camRotation = iMouse.z> 0.? iMouse.x/ iResolution.x* PI* (2.)+ PI: (.2)* iTime;
	vec3 cam_pos = (vec3(0.0,1.0,1.4)* pow(10.,camDolly* 8.- 8.0));
	vec3 cam_dir = normalize(vec3(0.0,-0.8,-1.0));
	vec3 cam_up = normalize(vec3(0.0,1.0,-0.8));
	cam_up.xz = (pR(cam_up.xz,camRotation));
	cam_dir.xz = (pR(cam_dir.xz,camRotation));
	cam_pos.xz = (pR(cam_pos.xz,camRotation));
	float cam_fov = (45.);
	vec3 u = cross(cam_dir,cam_up);
	vec3 v = cam_up;
	vec3 w = -cam_dir;
	vec3 o = cam_pos;
	vec3 d = vec3(screenCoord,-1.0/ tan(radians(.5*cam_fov)));
	d = normalize((mat3(u,v,w)* d));
	float t = INFINITY;
	if(d.y< 0.) {
		t = (o.y)/ -d.y;
	}
	vec3 p = ((d* t)+ o);
	float dist = f(p);
	vec3 col = distanceMeter(dist,t,d,cam_pos.y);
	col = Uncharted2ToneMapping(col);
	vec4 fragColor = vec4(pow(col,vec3(1./ 2.2)),1);
	return fragColor;
}
void mainImage(out vec4 fragColor, vec2 fragCoord) {
	fragColor = mainImage_meter(fragCoord);
}

//primitives
float sdSphere(vec3 p, float r) {
    return length(p) - r;
}
float sdBox(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

//distance field
float df(vec3 p) {
    float d = sdSphere(p, 1.5);
    d = min(d, sdSphere(p + vec3(3.0*cos(iTime),  0, 3.0*sin(iTime)), 1.0));
    d = min(d, sdBox(p + vec3(0, 2.0, 0), vec3(5.0, 0.1, 5.0)));
    return d;
}

//calculate normal
vec3 calcNormal(vec3 p) {
	float eps = 0.001;
	return normalize(vec3(
		df(p + vec3(eps, 0, 0)) - df(p + vec3(-eps, 0, 0)),
		df(p + vec3(0, eps, 0)) - df(p + vec3(0, -eps, 0)),
		df(p + vec3(0, 0, eps)) - df(p + vec3(0, 0, -eps))
		));
}


struct Ray {
    vec3 position;
    vec3 rayDir;
    int steps;
    float t;
};

//it finds the intersection point and returns various data
const int maxSteps = 100;
Ray trace(vec3 from, vec3 rayDir) {
    vec3 p = vec3(0);
    int steps = 0;
    float t = 0.0;
    for(int i = 0; i < maxSteps; i++) {
        p = from + t*rayDir;
        float d = df(p);
        if(d < 0.001) {
            steps = i;
            break;
        }
        t += d/2.0;
    }
    return Ray(p, rayDir, steps, t);
}


//is point p visible from the light?
bool isVisible(vec3 p, vec3 lightPos) {
    Ray tr = trace(lightPos, normalize(p - lightPos));
    if(distance(tr.position, p) < 0.01) {
        return true;
    }
    else {
        return false;
    }
}

//shading
float kd = 0.7;
vec3 cd = vec3(1.0);

float ks = 0.3;
vec3 cs = vec3(1.0);
float alpha = 12.0;

float ka = 1.0;
vec3 ca = vec3(0.1, 0.2, 0.15);
vec3 shading(Ray  tr, vec3 lightPos) {
    vec3 p = tr.position;
    vec3 normal = calcNormal(p);
    vec3 l = normalize(lightPos - p);
    vec3 v = -tr.rayDir;
    vec3 r = reflect(-l, normal);

    //diffuse
    vec3 Id = kd * max(dot(normal, l), 0.0) * cd;

    //specular
    vec3 Is = ks * pow(max(dot(v, r), 0.0), alpha) * cs;

    //ambient
    vec3 Ia = ka * ca;

    //visibility
    if(isVisible(p, lightPos)) {
        return Id + Is + Ia;
    }
    else {
        return Ia;
    }
}


//main
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //(-1, -1) ~ (1, 1)
	vec2 uv = (2.0*fragCoord.xy - iResolution.xy)/iResolution.y;

    //camera settings
    vec3 camPos = vec3(10.0*cos(iTime), 2, -10.0*sin(iTime));
    vec3 camFront = normalize(-camPos);
    vec3 camUp = vec3(0, 1, 0);
    vec3 camRight = cross(camUp, camFront);
    float focus = 1.0;

    //ray direction
    vec3 rayDir = uv.x*camRight + uv.y*camUp + focus*camFront;

    //trace
    Ray tr = trace(camPos, rayDir);

    //light position
	vec3 lightPos = vec3(5, 5, 5);

    //coloring
    vec3 color = shading(tr, lightPos);

    //clipping with fog effect
    if(tr.t > 20.0) {
        color = color/(tr.t - 20.0);
    }

	fragColor = vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Resolution stuffs
	vec2 uv = fragCoord.xy / min(iResolution.x,iResolution.y);
    uv.x -= 0.4;
    //offset UV's to the centered
    vec2 uvCent = uv * 2.0 - 1.0;
    float Pi = 3.141592;
    //basically a circle (length(uvCent), but with added wave using sin(radians + time)
    //Use different wave for each colour channel to create colour shift effect
    //This could be anything, not just a sinwave, like a scrolling texture for example.
    float circleR = step(length(uvCent + sin(iTime + atan(uvCent.y, uvCent.x) * 19.97) * 0.013), 0.9);
    float circleG = step(length(uvCent + sin(iTime - atan(uvCent.y, uvCent.x) * 26.23) * 0.031), 0.9);
    float circleB = step(length(uvCent + sin(iTime + atan(uvCent.y, uvCent.x) * 22.62) * 0.0552), 0.9);
    //return comped circles and invert it (for black circle on white bg)
    vec4 RGBCircle = vec4(1.0) - vec4(circleR, circleG, circleB, 1.0); 
	fragColor = RGBCircle;
}

#define PI 3.141592654

// ------ FEATURE DEFINITIONS -------
// coloring & movement - hint: try max distance here!
const bool f_coloring_iteration = true;
const bool f_camera_movement 	= true;
const bool f_sphere_audio 		= true;

// eye candies & post processing
const bool f_volumetric_fog 	= true;
const bool f_smooth_merge 		= true;
const bool f_vigneting 			= true;


// ------ CONSTANTS -------
const float focalLength = 1.9; 				// Distance between the eye and the image plane
const int 	rmSteps 	= 64; 				// Max raymarch steps
const float rmEpsilon 	= 0.001; 			// Surface threshold
const vec4 	skyColor 	= vec4(1.0);
const vec3 	eye 		= vec3(0, 0, -2.5); // eye vector - normalised
const vec3 	camUp 		= vec3(0, 1, 0);	// up vector - normalised
const vec3 	camRight 	= vec3(1, 0, 0);	// right vector - normalised
const float fogDistance = 2.7;				// fog fade out distance

// smooth minimum function - credit goes to Iq
float smin( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}

// sphere distance field calculation
float distSphere(vec3 p, float radius)
{
    float freq = (f_sphere_audio) ? texture( iChannel0, vec2( 0.01, 0.2 ) ).x * 1.1 : 1.0;    
  	return length(p) - clamp(0.2, 1.0, radius * freq);
}

// returns the max component of the input vector (x, y or z)
float maxcomp(in vec3 p ) {
    return max(p.x,max(p.y,p.z));
}

// signed box primitive distance field calculation
// b.x = Width
// b.y = Height
// b.z = Depth
// Leave r=0 if radius not needed
float distBox(vec3 p, vec3 b, float r) {
    vec3 d = abs(p) - b;
    return min(maxcomp(d),0.0) - r + length(max(d,0.0));
}

float getDistanceOfNearestObject(vec3 point)
{
	// calculate distances from the objects
    float dSphere = distSphere(point, 0.6);  
    float dBox = distBox(point, vec3(0.47,0.47,0.47), 0.05);    
    float minDist = min(dSphere, dBox);

    // get the minimum distance from our objects   
    return (f_smooth_merge) ? min(smin(dSphere, dBox, 32.0), minDist) : minDist;
}

vec4 getObjectColor(int iterationCount)
{
    float redChannel = (f_coloring_iteration) ? 1.0 / float(iterationCount) * 1.5 : 1.0;
    return vec4(redChannel, 0.1,0.2, 1);
}

vec4 applyFog(vec4 color, float distFromEye)
{
    if(distFromEye > fogDistance && f_volumetric_fog)
    {
        return mix(color, 
                   vec4(1.0,1.0,1.0,1.0), 
                   clamp(0.0,1.0,distFromEye - fogDistance));
    }

    return color;
}

vec4 applyVignette(vec4 color, vec2 fragCoord)
{
    if(f_vigneting)
    {
		vec2 q = fragCoord.xy/iResolution.xy;
    	color *= 0.3 + 0.8 * pow(16.0 * q.x * q.y * (1.0 - q.x) * (1.0 - q.y), 0.2);
    }

    return color;
}


vec4 RayTrace(in vec2 fragCoord)
{    
    // forward vector from camera
    vec3 camForward = cross(camRight, camUp);

    float aspectRatio = iResolution.x / iResolution.y;

    // map the fragment coordinates to the 3D (UV) coordinate system
    float u = fragCoord.x * 2.0 / iResolution.x - 1.0;
    float v = fragCoord.y * 2.0 / iResolution.y - 1.0;   
    float rotX, rotZ = 0.0;

    if(f_camera_movement)
    {
    	float nRotate = iTime * 0.1; // slow rotation  
    	rotX = cos(PI * nRotate);
    	rotZ = sin(PI * nRotate);
    }

    // Cast a ray from the eye through the pixel (using focal length)
    vec3 rayOrigin = vec3(eye.x + rotX, eye.y, eye.z + rotZ);
    vec3 rayDirection = normalize(camForward * focalLength + camRight * u * aspectRatio + camUp * v);;

    // set default color, this will be rendered if the ray doesn't hit anything
    vec4 color = skyColor;

    // Raymarching starts here
    float distFromEye = 0.0;
    for(int i = 0; i < rmSteps; ++i)
    {
        // define current point on the ray
        vec3 p = rayOrigin + rayDirection * distFromEye;      
        float d = getDistanceOfNearestObject(p);     

        // if the point is close _enough_ to the object surface, render the object
        if(d < rmEpsilon)
        {
            // render the object color - we use only one color here
            color = getObjectColor(i);
            color = applyFog(color, distFromEye);
            break;
        }

        distFromEye += d;   
    }

    return color;
}

// the entry point of the shader
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 color = RayTrace(fragCoord);   
    fragColor = applyVignette(color, fragCoord);
}

