// Grab all sky information for a given ray from camera
// from Dave Hoskins // https://www.shadertoy.com/view/Xsf3zX
vec3 GetSky(in vec3 rd, in vec3 sunDir, in vec3 sunCol)
{
	float sunAmount = max( dot( rd, sunDir), 0.0 );
	float v = pow(1.0-max(rd.y,0.0),6.);
	vec3  sky = mix(vec3(.1, .2, .3), vec3(.32, .32, .32), v);
	sky = sky + sunCol * sunAmount * sunAmount * .25;
	sky = sky + sunCol * min(pow(sunAmount, 800.0)*1.5, .3);
	return clamp(sky, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragColor = vec4(1);

	vec2 g = fragCoord.xy;
	vec2 si = iResolution.xy;
	vec2 uv = (2.*g-si)/min(si.x, si.y);

	t = 3.14159*0.5;

	vec2 camp = vec2(643./958.,356./816.) * 5.;

	vec3 rayOrg = vec3(cos(t),sin(camp.y),sin(t)) * camp.x;
	vec3 camUp = vec3(0,1,0);
	vec3 camOrg = vec3(0,1.5,0);

	float fov = .5;
	vec3 axisZ = normalize(camOrg - rayOrg);
	vec3 axisX = normalize(cross(camUp, axisZ));
	vec3 axisY = normalize(cross(axisZ, axisX));
	vec3 rayDir = normalize(axisZ + fov * uv.x * axisX + fov * uv.y * axisY);

	float s = 1.;
	float d = 0.;
	vec3 p = rayOrg + rayDir * d;
	float dMax = 20.;
	float sMin = 0.0001;

	for (float i=0.; i<250.; i++)
	{
		if (abs(s) < d*d*1e-5 || d>dMax) break; // thanks to iq for the simpler form of the condition
		s = df(p).x;
		d += s * 0.3;
		p = rayOrg + rayDir * d;	
	}

    vec3 sky = GetSky(rayDir, ld, vec3(1.5));

	if (d<dMax)
	{
		vec3 n = nor(p, 0.01178);

		// 	iq primitive shader : https://www.shadertoy.com/view/Xds3zN
		float r = mod( floor(5.0*p.z) + floor(5.0*p.x), 2.0);
        fragColor.rgb = 0.4 + 0.1*r*vec3(1.0);

        // iq lighting
		float occ = calcAO( p, n );
        float amb = clamp( 0.5+0.5*n.y, 0.0, 1.0 );
        float dif = clamp( dot( n, ld ), 0.0, 1.0 ) * (df(p+n*1.4).x);
        float spe = pow(clamp( dot( rayDir, ld ), 0.0, 1.0 ),16.0);

        dif *= softshadow( p, ld, 0.1, 10. );

        vec3 brdf = vec3(0.0);
        brdf += 1.20*dif*vec3(1.00,0.90,0.60);
        brdf += 1.20*spe*vec3(1.00,0.90,0.60)*dif;
        brdf += 0.30*amb*vec3(0.50,0.70,1.00)*occ;
        brdf += 0.02;
        fragColor.rgb *= brdf;

        fragColor.rgb = mix( fragColor.rgb, sky, 1.0-exp( -0.01*d*d ) ); 
	}
	else
	{
		fragColor.rgb = sky;
	}
}

// Gradient stolen from https://www.shadertoy.com/view/ltlSRj
vec3 colors [5];
float points [5];

void initia(){
    colors[0]=vec3(0.,0.,0.6);
    colors[1]=vec3(0.,1.,1.);
    colors[2]=vec3(0.0,1.0,0.);
    colors[3]=vec3(1.0,1.0,0.);
    colors[4]=vec3(1.0,0.0,0.);
    points[0]=0.;
    points[1]=0.15;
    points[2]=0.5;
    points[3]=0.65;
    points[4]=1.0;
}
vec3 gradian(vec3 c1,vec3 c2,float a){
    return vec3(c1.x+a*(c2.x-c1.x),
                c1.y+a*(c2.y-c1.y),
                c1.z+a*(c2.z-c1.z));
}

vec3 heat4(float weight){
    if(weight<=points[0]){
        return colors[0];
    }
    if(weight>=points[4]){
        return colors[4];
    }
    for(int i=1;i<5;i++){
        if(weight<points[i]){
           float a=(weight-points[i-1])/(points[i]-points[i-1]);
            return gradian(colors[i-1],colors[i],a);
        }
    }
}


float d(vec2 a, vec2 b) {
   return  pow(max(0.0, 1.0 - distance(a, b) / (0.6)), 2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    initia();
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 4.0 - vec2(2.0);
   uv.x *= iResolution.x / iResolution.y;

    float totalWeight = 0.0;
    for (float i = 0.0; i < 112.0; ++i) {
        //计算每个坐标点的权重
    	totalWeight += 0.5*d(uv, vec2(
            sin(1.0*iTime * 0.3 + float(i))*2. + 2.*sin(i * i), 
            cos(1.0*iTime * 0.4 + float(i *1.5))*2.
        ));
    }


	fragColor = vec4(heat4(totalWeight), 1.0);
}

#define HASHSCALE3 vec3(443.897, 441.423, 437.195)
#define PI 3.14159265
#define OCTAVES 4.0

// Color transform from HSB to RGB
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x * 6.0 + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
    rgb = rgb * rgb * (3.0 - 2.0 * rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

// rotate position around axis
vec2 rotate(vec2 p, float a){
	return vec2(p.x * cos(a) - p.y * sin(a), p.x * sin(a) + p.y * cos(a));
}

// Hash without sine from:
// https://www.shadertoy.com/view/4djSRW
vec2 hash22(vec2 p){
	vec3 p3 = fract(vec3(p.xyx) * HASHSCALE3);
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.xx+p3.yz)*p3.zy);
}

// Cell noise learned from:
// https://thebookofshaders.com/12/
float cell(vec2 x, float t){
	vec2 iPos = floor(x); // Cell integer position
	vec2 fPos = fract(x); // Cell fraction position
	float minDist1 = 2.0; // Stores minimum distance

	// Calculate neighboring cells
	for(int y = -1; y <= 1; y ++){
		for(int x = -1; x <= 1; x ++){
			vec2 neighborCell = vec2(x, y);
			vec2 randPoint = hash22(iPos + neighborCell);

			// Animate point
			randPoint = 0.5 + sin(t + randPoint * 2.0 * PI) * 0.5;

			vec2 fPosToPoint = neighborCell + randPoint - fPos;

			// Pick distance algorithm
			float dist = length(fPosToPoint);	// Euclidean
			// float dist = max(abs(fPosToPoint.x), abs(fPosToPoint.y));	// Chebyshev
			// float dist = abs(fPosToPoint.x) + abs(fPosToPoint.y);		// Manhattan

			// Keep smallest distance
			minDist1 = min(dist, minDist1);
		}
	}

	return minDist1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	// Screen position
	vec2 p = fragCoord.xy / iResolution.xy;
	// Mouse position
	vec2 m = 1.0 - vec2(iMouse.xy / iResolution.xy);

	// Adjust view ratio + rotation
	vec2 pos = vec2(p.x * iResolution.x / iResolution.y, p.y);
	pos = rotate(pos, (m.x + m.y) * 0.1);

	// Store cell noise in brighness
	float brightness = 0.0;

    // Calculate cell noise
	for(float i = 1.0; i <= OCTAVES; i++){
		brightness += cell(pos - (m * 8.0), iTime * 0.5 * i) / i;
		pos *= OCTAVES;
	}

	// Square for contrast
	brightness *= brightness;

	float hue = distance(p, m) * 0.2 + 0.5;
	fragColor = vec4(hsb2rgb(vec3(hue, 0.5, brightness)), 1.0);
}

float point(vec2 base, float x, float y, float grid) {
    float f = mod(base.x + x, grid) * mod(base.y + y, grid);
    return 1.0 - min(1.0,f);
}

vec4 heart(vec2 fragCoord, float time, float spread) {
	vec4 result = vec4(0.0,0.0,0.0,0.0);
	vec2 base = fragCoord.xy;

    float invSpread = 1.0 / spread;
    float halfSpread = spread * 0.5;

    float everyOther = floor(base.y * invSpread);
    base.x += everyOther * halfSpread;

    float t = iTime;
    t += 0.5 * everyOther;
    t += 0.25 * floor((base.x + halfSpread) * invSpread);
    t += 0.25 * floor((base.x + halfSpread) * invSpread);
    float frame = 1.0;

    base.x += floor(0.5 * halfSpread * sin(t) + 0.5);

    frame = mod(t*4.0, 4.0);
    frame = min(1.0, (frame));

    result.r += frame * point(base,  0.0,	0.0,	spread);
    result.r += frame * point(base,  1.0,	-1.0,	spread);
    result.r += frame * point(base, -1.0,	-1.0,	spread);

    frame = mod(t*3.0, 3.0);
    frame = min(1.0, (frame));

    result.r += frame * point(base,  0.0,	-1.0,	spread);
    result.r += frame * point(base,  1.0,	-2.0,	spread);
    result.r += frame * point(base, -1.0,	-2.0,	spread);    
    result.r += frame * point(base,  2.0,	-2.0,	spread);
    result.r += frame * point(base, -2.0,	-2.0,	spread);

    frame = mod(t*2.0, 2.0);
    frame = min(1.0, (frame));

    result.r += frame * point(base,  0.0,	-2.0,	spread);
    result.r += frame * point(base,  1.0,	-3.0,	spread);
    result.r += frame * point(base, -1.0,	-3.0,	spread);    
    result.r += frame * point(base,  2.0,	-3.0,	spread);
    result.r += frame * point(base, -2.0,	-3.0,	spread);

    result.r = min(1.0,result.r);
    result.b = 0.25*result.r;

    frame = mod(t*2.5, 2.5);
    frame = min(1.0, (frame));
    frame *= 0.5;
    result.b += frame * point(base,  0.0,	-1.0,	spread);
    result.b += frame * point(base,  0.0,	-2.0,	spread);
    result.b += frame * point(base,  1.0,	-2.0,	spread);
    result.b += frame * point(base, -1.0,	-2.0,	spread);
    result.a = min(1.0,result.r);

    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = heart(fragCoord, iTime, 20.0);
}

// Z-curve / Morton code
// refs : https://en.wikipedia.org/wiki/Z-order_curve
//        https://fgiesen.wordpress.com/2009/12/13/decoding-morton-codes/

int MASKS[] = int[] (0x55555555, 0x33333333, 0x0F0F0F0F, 0x00FF00FF, 0x0000FFFF);

int xy2z(vec2 U) {       // --- grid location to curve index
    ivec2 I = ivec2(U);
    int n=8;
    for (int i=3; i>=0; i--)
        I =  (I | (I << n)) & MASKS[i],
        n /= 2;
    return I.x | (I.y << 1);
}

ivec2 z2xy(int z) {      // --- curve index to grid location 
    int n=1;
    ivec2 I = ivec2(z,z>>1) & MASKS[0];
    for (int i=1; i<=4; i++)
        I = (I | (I >>  n)) & MASKS[i],
        n *= 2;
    return I;
}



void mainImage( out vec4 O,  vec2 U )
{
    U /= 2.;
    float R = exp2(floor(log2(iResolution.y/2.)));
    O = max(U.x,U.y) < R ? vec4(U/R, .5,0) : vec4(0);

    int Z = xy2z(U);

 // O = vec4( Z ) / R/R;               // display Z-index
 // O = vec4( z2xy(Z),0,0)/ R;         // pos from Z
 // O = vec4(ivec2(U)== z2xy(iFrame)); // browse index on Z-curve.

    if (Z < iFrame) O = vec4(Z)/float(iFrame); // * vec4(U,0,0)/R;

    if (fract(U.y)==.75) return;
    U = iResolution.xy/2.-ceil(U);
    if ( U.x < R) {
        Z = int(U.x+R*U.y);
        if (Z < iFrame ) O = fract(vec4(z2xy(Z),R/2.,0)/R);
    }
}

