vec2 hash2(vec2 p) {
    return fract(sin(vec2(
        dot(p, vec2(127.0, 311.0)),
        dot(p, vec2(269.0, 181.0) * 43759.0))));
}

//Create a voronoi distance field. Distances are to the nearest
//seed point
float voronoi(vec2 uv, float num_tiles) {
    //Tile space
    vec2 cell = floor(num_tiles * uv);
    vec2 cell_uv = fract(num_tiles * uv);

    //Voronoi Level 1
    float min_dist = 10000.0;
    //Look at neighboring boxes only
    for (int x = -1; x <= 1; x++) {
        for (int y = -1; y <= 1; y++) {
            //Find the random point in the neighbor box
            vec2 offset = vec2(float(x), float(y));
            vec2 neighbor_point = hash2(cell + offset);
            neighbor_point = 0.5 + 0.5 * sin(45.0 * neighbor_point + iTime);

            //distance between pixel and neighbor center)
            float dist = distance(offset + neighbor_point, cell_uv);
            min_dist = min(dist, min_dist);
        }
    }
    return min_dist;
}

