vec3 getBaseColor(int i)
{
    if (i == 0) return vec3(1.0, 0.4, 0.2);
    if (i == 1) return vec3(0.4, 1.0, 0.2);
    if (i == 2) return vec3(0.2, 1.0, 0.4);
