    int i = int(mod(colorPerSecond * iTime, 7.));
    int j = int(mod(float(i) + 1., 7.));

    return mix(getBaseColor(i), getBaseColor(j), fract(colorPerSecond * iTime));
}

vec3 linearToneMapping(vec3 color)
{
    float exposure = 1.;
    color = clamp(exposure * color, 0., 1.);
    color = pow(color, vec3(1. / gamma));
    return color;
}

vec3 simpleReinhardToneMapping(vec3 color)
{
    float exposure = 1.5;
    color *= exposure/(1. + color / exposure);
    color = pow(color, vec3(1. / gamma));
    return color;
}

vec3 lumaBasedReinhardToneMapping(vec3 color)
{
    float luma = dot(color, vec3(0.2126, 0.7152, 0.0722));
    float toneMappedLuma = luma / (1. + luma);
    color *= toneMappedLuma / luma;
    color = pow(color, vec3(1. / gamma));
    return color;
}

vec3 whitePreservingLumaBasedReinhardToneMapping(vec3 color)
{
    float white = 2.;
    float luma = dot(color, vec3(0.2126, 0.7152, 0.0722));
    float toneMappedLuma = luma * (1. + luma / (white*white)) / (1. + luma);
    color *= toneMappedLuma / luma;
    color = pow(color, vec3(1. / gamma));
    return color;
}

vec3 RomBinDaHouseToneMapping(vec3 color)
{
    color = exp( -1.0 / ( 2.72*color + 0.15 ) );
    color = pow(color, vec3(1. / gamma));
    return color;
}

vec3 filmicToneMapping(vec3 color)
{
    color = max(vec3(0.), color - vec3(0.004));
    color = (color * (6.2 * color + .5)) / (color * (6.2 * color + 1.7) + 0.06);
    return color;
}

vec3 Uncharted2ToneMapping(vec3 color)
{
    float A = 0.15;
    float B = 0.50;
    float C = 0.10;
    float D = 0.20;
    float E = 0.02;
    float F = 0.30;
    float W = 11.2;
    float exposure = 2.;
    color *= exposure;
    color = ((color * (A * color + C * B) + D * E) / (color * (A * color + B) + D * F)) - E / F;
    float white = ((W * (A * W + C * B) + D * E) / (W * (A * W + B) + D * F)) - E / F;
    color /= white;
    color = pow(color, vec3(1. / gamma));
    return color;
}

vec3 logToneMap(vec3 c){

    // in my experience limit=2.2 and contrast=0.35 gives nice result
    // i prefer to leave a lot of highlights
    // also log curve fades to highlights beautifully
    // however i raised this parameters to make it look
    // somewhat identical to other algorithms here

    // according to my information the dynamic range of
    // human eye's light perception is around
    // 2.5 of what monitor can provide
    // though it's hightly questionable in terms of
    // both the data and my understanding of it

    // P.S.
    // i noticed that applying inverse tonemap transformation to
    // diffuse textures improves the final render a lot

    float limit = 2.5;
    float contrast = 0.3;

    // do the tone mapping

    c = log(c + 1.0) / log(limit + 1.0);

    // clamping for hackyContrast because s_curve
    // does not behave properly outside [0; 1]
    // if you want to keep values outside [0; 1]
    // for example for bloom post-effect
    // you would need comething better than
    // hackyContrast()

    c = clamp(c,0.0,1.0);

    // hacky contrast s_curve
    // btw it's a freatapproximation for 1-cos(x*3.14)*0.5

    c = mix(c, c * c * (3.0 - 2.0 * c), contrast);

    // this creates pleasant hue shifting
    // i amplified the effect to demonstrate it more clearly
    // usually i use it very subtle

    c = pow(c, vec3(1.05,0.9,1));

    // gamma correction

    return pow(c, vec3(1. / gamma));
}

vec3 limitedPerChannelReinhard(vec3 c){
    float limit = 4.0;
    float contrast = 0.6;

    c *= (c / (limit*limit) + 1.) / (c + 1.);

    return pow(c, vec3(1. / gamma));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color = getBaseColor();

    float n = 8.;
    if (uv.x > 0.2)
    {
        if (uv.x < 0.6)
            // blacks
            color *= (2.5 * uv.x - 0.5);
        else
            // whites
            color *= 15.*(2.5 * uv.x - 1.5) + 1.;

        int i = int(n * (1. - uv.y));
        if (i == 0) color = linearToneMapping(color);
        if (i == 1) color = simpleReinhardToneMapping(color);
        if (i == 2) color = whitePreservingLumaBasedReinhardToneMapping(color);
        if (i == 3) color = RomBinDaHouseToneMapping(color);
        if (i == 4) color = filmicToneMapping(color);
        if (i == 5) color = Uncharted2ToneMapping(color);

  //below are the ones i added

     //you can tweak reinhard to look like this
        if (i == 6) color = logToneMap(color); 

        if (i == 7) color = limitedPerChannelReinhard(color);

    }

    if (abs(fract(n * uv.y + 0.5) - 0.5) < 0.02)
        color = vec3(0.);

   	vec3 diffC = vec3(1,1,1) - clamp(color,0.0,1.0);
    float diff = max(diffC.r, max(diffC.g, diffC.g));
    // color = ((diff > 0.0) && (diff < 0.01)) ? vec3(0,0,0) : color;
    // this will show tha point where color fades to white

    fragColor = vec4(color, 1.);
}

