// Grab all sky information for a given ray from camera
// from Dave Hoskins // https://www.shadertoy.com/view/Xsf3zX
vec3 GetSky(in vec3 rd, in vec3 sunDir, in vec3 sunCol)
{
	float sunAmount = max( dot( rd, sunDir), 0.0 );
	float v = pow(1.0-max(rd.y,0.0),6.);
	vec3  sky = mix(vec3(.1, .2, .3), vec3(.32, .32, .32), v);
	sky = sky + sunCol * sunAmount * sunAmount * .25;
	sky = sky + sunCol * min(pow(sunAmount, 800.0)*1.5, .3);
	return clamp(sky, 0.0, 1.0);
}

vec3 nor( vec3 p , vec3 e)
{
	return normalize(vec3(											
	    df(p+e.xyy).x - df(p-e.xyy).x,								
	    df(p+e.yxy).x - df(p-e.yxy).x,								
	    df(p+e.yyx).x - df(p-e.yyx).x ));							
}

float SubDensity(vec3 p, float s) 
{
	vec3 n = nor(p,vec3( 0.0001, 0, 0)); 							
	return df(p - n * s).x/s;										
}

vec2 shade(vec3 ro, vec3 rd, float d, vec3 lp, float li)
{
	vec3 p = ro + rd * d;											
	vec3 ld = normalize(lp-p); 										
	vec3 n = nor(p, vec3( 0.0001, 0, 0));							
	vec3 refl = reflect(rd,n);										
	float amb = 0.132; 												
	float diff = clamp( dot( n, ld ), 0.0, 1.0 ); 					
	float fre = pow( clamp( 1. + dot(n,rd),0.0,1.0), 4. ); 			
	float spe = pow(clamp( dot( refl, ld ), 0.0, 1.0 ),16.);		
	float sss = 1. - SubDensity(p, 1.); 							
	return vec2(
        (diff + fre + spe) * amb * li, 								
        (diff + fre + sss) * amb * li + spe 						
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragColor = vec4(1);

	vec2 g = fragCoord.xy;
	vec2 si = iResolution.xy;
	vec2 uv = (2.*g-si)/min(si.x, si.y);

	t = 1.57;

	vec2 camp = vec2(.74,.55) * 5.;
	vec3 rayOrg = vec3(cos(t),sin(camp.y),sin(t)) * camp.x;
	vec3 lpi = vec3(-cos(t),sin(camp.y),-sin(t)) * camp.x;

	vec3 camUp = vec3(0,1,0);
	vec3 camOrg = vec3(0,1.76,0);

	float fov = .5;
	vec3 axisZ = normalize(camOrg - rayOrg);
	vec3 axisX = normalize(cross(camUp, axisZ));
	vec3 axisY = normalize(cross(axisZ, axisX));
	vec3 rayDir = normalize(axisZ + fov * uv.x * axisX + fov * uv.y * axisY);

	float s = 1.;
	float d = 0.;
	vec3 p = rayOrg + rayDir * d;
	float dMax = 20.;

	for (float i=0.; i<150.; i++)
	{
		if (log(d*d/s/1e5)>s || d>dMax) break;
		s = df(p).x;
		d += s * 0.2;
		p = rayOrg + rayDir * d;	
	}

    if (d<dMax)
	{
		vec3 p = rayOrg + rayDir * d;
		float m = df(p).y;
		if (m < 0.5)
		{
			vec3 n = nor(p, vec3( 0.1, 0, 0));	
			fragColor.rgb = GetSky(reflect(n,rayDir), ld, vec3(50)) * .2 + vec3(73,28,94)/255. * .8;
		}
		else if (m < 1.5)
		{
			fragColor.rgb = mix(
				shade(p, rayDir, 1., lpi, 5.).y * mat, 						
				shade(rayOrg, rayDir, d, rayOrg, 1.).x * (mat+0.5), 				
			.5);
		}
	}
	else
	{
		fragColor.rgb = GetSky(rayDir, ld, vec3(50));
	}
}

#define CLAMP

float distanceBezier(vec2 p, vec2 P0, vec2 P1, vec2 P2, vec2 P3)
{
    // Cubic Bezier curve: 
    //
    //     B(t) = (1-t)^3 P0 + (1-t)^2 t P1 + (1-t) t^2 P2 + t^3 P3
    //
    // or
    //
    //     B(t) = A t^3 + B t^2 + C t + D
    //
    // where
    //
    //     A = -P0 + 3 P1 - 3 P2 + P3
    // 	   B = 3 (P0 - 2 P1 + P2)
    //     C = 3 (P1 - P0)
    //     D = P0

    vec2 A = -P0 + 3.0*P1 - 3.0*P2 + P3;
    vec2 B = 3.0*(P0 - 2.0*P1 + P2);
    vec2 C = 3.0*(P1 - P0);
    vec2 D = P0;

    // Goal:
    //
    //     "find t such that d^2 = ||B(t) - p||^2 is minimized"
    //
    // i.e. the find t such that
    //
    //     f(t) = d/dt dot(B(t) - p, B(t) - p) = 0
    // 
    // and hope that is a global minima

    // Expanding the above gives a 5th degree polynomial:
    //
    //     f(t) = a5 t^5 + a4 t^4 + a3 t^3 + a2 t^2 + a1 t + a0
    //
    // where 
    //
    //     a5 = 6 A A
    //     a4 = 10 A B
    //     a3 = 8 A C + 4 B B
    //     a2 = 6 A D' + 6 B C
    //     a1 = 4 B D' + 2 C C
    //     a0 = 2 C D'
    //
    // where
    // 
    //     D' = D - p

    float a5 = 6.0*dot(A,A);
    float a4 = 10.0*dot(A,B);
    float a3 = 8.0*dot(A,C) + 4.0*dot(B,B);
    float a2 = 6.0*dot(A,D-p) + 6.0*dot(B,C);
    float a1 = 4.0*dot(B,D-p) + 2.0*dot(C,C);
    float a0 = 2.0*dot(C,D-p);

    // calculate distances to the control points
    float d0 = length(p-P0);
    float d1 = length(p-P1);
    float d2 = length(p-P2);
    float d3 = length(p-P3);
    float d = min(d0, min(d1, min(d2,d3)));


    // Use the Newton-Raphson method to find a local minima, i.e. iterate:
    //
    //     t_{n+1} = t_n - f(t_n)/f'(t_n)
    //
    // until convergence is reached

    // Choose initial value of t
    float t;
    if (abs(d3 - d) < 1.0e-5)
        t = 1.0;
    else if (abs(d0 - d) < 1.0e-5)
        t = 0.0;
    else
        t = 0.5;

	// iterate
    for (int i = 0; i < 10; i++) {
        float t2 = t*t;
        float t3 = t2*t;
        float t4 = t3*t;
        float t5 = t4*t;

        float f = a5*t5 + a4*t4 + a3*t3 + a2*t2 + a1*t + a0;
        float df = 5.0*a5*t4 + 4.0*a4*t3 + 3.0*a3*t2 + 2.0*a2*t + a1;

        t = t - f/df;
    }

    // clamp to edge of bezier segment
#ifdef CLAMP
    t = clamp(t, 0.0, 1.0);
#endif

    // get the point on the curve
    vec2 P = A*t*t*t + B*t*t + C*t + D;

    // return distance to the point on the curve
    if (d < 0.1) { 
        // to see the control points
        return -1000.0;
    } else {
        // Newton iteration converge to local minima that is not the global for some values
        // return length(p-P); 

        // Taking the union with the end point distances
        // seem to fix the issue for most "nice" control points
        // (since NR can only find interior points?)
    	return min(length(p-P), min(d0, d3)); 
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 5.0;
    // calculate coordinates of fragment
	vec2 p = zoom*(2.0*fragCoord.xy - iResolution.xy)/iResolution.y;

    // define control points
    float a = 2.0*sin(0.5*0.5*0.5*2.0*3.1415925*iTime);
    vec2 P0 = vec2(-1.00 - 1.00*cos(1.0*a), 1.0*sin(a));
    vec2 P1 = vec2(-0.75 + 0.25*cos(0.5*a), 0.0*sin(a));
    vec2 P2 = vec2( 0.75 - 0.25*cos(0.5*a), 0.0*sin(a));
    vec2 P3 = vec2( 1.00 + 1.00*cos(1.0*a), 1.0*sin(a));

    // get the distance to the bezier curve
    float r = distanceBezier(p, P0, P1, P2, P3);

    if (r < 0.0) {
        // control point
        fragColor = vec4(1.0, 0.0, 1.0, 1.0);
    } else {
        // make the distance field periodic, for viewing contour lines

        // 2 stripes per unit length
        float f = 2.0*3.1416*2.0; 

        // smoothen
        float w = 1.5*fwidth(f*r);
        float s = smoothstep(-w, w, 0.0+sin(f*r+3.1415/2.0));

        //
        fragColor = vec4(vec3(s), 1.0);
    }
}

