float map( in vec3 pos )
{
    vec2 res = opU( vec2( sdPlane(     pos), 1.0 ),
	                vec2( sdSphere(    pos-vec3( 0.0,0.25, 0.0), 0.25 ), 46.9 ) );
    res = opU( res, vec2( sdBox(       pos-vec3( 1.0,0.25, 0.0), vec3(0.25) ), 3.0 ) );
    res = opU( res, vec2( udRoundBox(  pos-vec3( 1.0,0.25, 1.0), vec3(0.15), 0.1 ), 41.0 ) );
	res = opU( res, vec2( sdTorus(     pos-vec3( 0.0,0.25, 1.0), vec2(0.20,0.05) ), 25.0 ) );
    res = opU( res, vec2( sdCapsule(   pos,vec3(-1.3,0.10,-0.1), vec3(-0.8,0.50,0.2), 0.1  ), 31.9 ) );
	res = opU( res, vec2( sdTriPrism(  pos-vec3(-1.0,0.25,-1.0), vec2(0.25,0.05) ),43.5 ) );
	res = opU( res, vec2( sdCylinder(  pos-vec3( 1.0,0.30,-1.0), vec2(0.1,0.2) ), 8.0 ) );
	res = opU( res, vec2( sdCone(      pos-vec3( 0.0,0.50,-1.0), vec3(0.8,0.6,0.3) ), 55.0 ) );
	res = opU( res, vec2( sdTorus82(   pos-vec3( 0.0,0.25, 2.0), vec2(0.20,0.05) ),50.0 ) );
	res = opU( res, vec2( sdTorus88(   pos-vec3(-1.0,0.25, 2.0), vec2(0.20,0.05) ),43.0 ) );
	res = opU( res, vec2( sdCylinder6( pos-vec3( 1.0,0.30, 2.0), vec2(0.1,0.2) ), 12.0 ) );
	res = opU( res, vec2( sdHexPrism(  pos-vec3(-1.0,0.20, 1.0), vec2(0.25,0.05) ),17.0 ) );
	res = opU( res, vec2( sdPryamid4(  pos-vec3(-1.0,0.15,-2.0), vec3(0.8,0.6,0.25) ),37.0 ) );
    res = opU( res, vec2( opS( udRoundBox(  pos-vec3(-2.0,0.2, 1.0), vec3(0.15),0.05),
	                           sdSphere(    pos-vec3(-2.0,0.2, 1.0), 0.25)), 13.0 ) );
    res = opU( res, vec2( opS( sdTorus82(  pos-vec3(-2.0,0.2, 0.0), vec2(0.20,0.1)),
	                           sdCylinder(  opRep( vec3(atan(pos.x+2.0,pos.z)/6.2831, pos.y, 0.02+0.5*length(pos-vec3(-2.0,0.2, 0.0))), vec3(0.05,1.0,0.05)), vec2(0.02,0.6))), 51.0 ) );
	res = opU( res, vec2( 0.5*sdSphere(    pos-vec3(-2.0,0.25,-1.0), 0.2 ) + 0.03*sin(50.0*pos.x)*sin(50.0*pos.y)*sin(50.0*pos.z), 65.0 ) );
	res = opU( res, vec2( 0.5*sdTorus( opTwist(pos-vec3(-2.0,0.25, 2.0)),vec2(0.20,0.05)), 46.7 ) );
    res = opU( res, vec2( sdConeSection( pos-vec3( 0.0,0.35,-2.0), 0.15, 0.2, 0.1 ), 13.67 ) );
    res = opU( res, vec2( sdEllipsoid( pos-vec3( 1.0,0.35,-2.0), vec3(0.15, 0.2, 0.05) ), 43.17 ) );

    return res.x;
}

float castRay( in vec3 ro, in vec3 rd, float tmax)
{
    float tmin = 1.0;

    float t = tmin;
	float precis = 0.0005;
    for( int i=0; i<64; i++ )
    {
	    float res = map( ro+rd*t );
        if( res<precis || t>tmax ) break;
        t += res;
    }
    return t;
}

vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ) + 
					  e.yyx*map( pos + e.yyx ) + 
					  e.yxy*map( pos + e.yxy ) + 
					  e.xxx*map( pos + e.xxx ) );
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<10; i++ )
    {
        float r = 0.01 + 0.1*float(i);
        vec3 aopos =  nor * r + pos;
        float d = map( aopos );
        occ += (r - d)*sca;
       /* if(d < 0.01)
            break;*/
        sca *= 0.85;
    }
    return clamp( 1.0 - occ / 3.14, 0.0, 1.0 );    
}

vec3 render( in vec3 ro, in vec3 rd )
{ 
    vec3 col = vec3(0.7, 0.9, 1.0) +rd.y*0.8;
    float t = castRay(ro,rd, 20.0);

    vec3 pos = ro + t*rd;
    vec3 nor = calcNormal( pos );

    float occ = calcAO( pos, nor );
    return vec3(occ, occ, occ);
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mo = iMouse.xy/iResolution.xy;
	float time = 15.0 + iTime;

    vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

	// camera	
    vec3 ro = vec3( -0.5+3.5*cos(0.1*time + 6.0*mo.x), 1.0 + 2.0*mo.y, 0.5 + 4.0*sin(0.1*time + 6.0*mo.x) );
    vec3 ta = vec3( -0.5, -0.4, 0.5 );

    // camera-to-world transformation
    mat3 ca = setCamera( ro, ta, 0.0 );
    // ray direction
    vec3 rd = ca * normalize( vec3(p.xy,2.0) );

     // render	
     fragColor.rgb = render( ro, rd );
}

vec3 hsv(in float h, in float s, in float v)
{
  return mix(vec3(1),clamp((abs(fract(h+vec3(3,2,1)/3.)*6.-3.)-1.),0.,1.),s)*v;
}

mat2 rotate(float a)
{
	return mat2(cos(a), sin(a), -sin(a), cos(a));	
}

float hash(float n){return fract(sin(n) * 43758.5453123);}

float star(in vec2 p)
{
  float a = radians(72.); 
  return dot(abs(rotate(floor(0.5 + atan(p.x, p.y) /a) *a) * p), vec2(2, 1));
}

vec2 paturn( in vec2 p )
{
    vec2 i = floor(p);    
    float t = iTime + hash(i.x*5. + i.y*9.)*radians(360.);
    float f = sin(t +sin(t*2.+i.x))*0.5+0.5;
    p = fract(p)-0.5;
    float d = mix(length(p)+0.05, star(p)-0.1, f);
    float n= i.x*5. + i.y*9.+floor(iTime);    
    return vec2(d  - 0.3*mix(hash(n),hash(n+1.0),smoothstep(0.0,1.0,fract(iTime))),f);
}

float scene(in float w, in float s)
{
    float time = mod(iTime,30.0);
    return clamp(time - w, 0.0, s) / s;  
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = (2.0*fragCoord.xy-iResolution.xy) / iResolution.y;

    p *= 1.5 * pow(sin(smoothstep(0.0,20.0,mod(time,13.)) * radians(90.0)), 0.3);
    float t = floor(time*0.5);
    p += vec2(cos(2.*t+3.0*cos(3.0*t)),sin(2.*t+3.0*sin(3.*t))) * 1.0;
    p = mix(vec2(inversesqrt(length(p)) * 5., atan(p.x, p.y)*3.), p, step(0.5,fract(time*0.3)));

    float s1 = scene(5.0, 10.0);
    float r1 = mix(0.3, 10.0,s1);
    vec2 n = paturn(p*r1+0.5);
    vec3 col = hsv(n.y+time*0.2,0.7,0.9)*smoothstep(0.03, 0.,n.x);

    col *= smoothstep(1.5, 0.11,length(q*2.-1.));
    fragColor = vec4(col,1.0);
}

