mat3 rotx(float a){return mat3(1.,0.,0.,0.,cos(a),-sin(a),0.,sin(a),cos(a));}
mat3 roty(float a){return mat3(cos(a),0.,sin(a),0.,1.,0.,-sin(a),0.,cos(a));}
mat3 rotz(float a){return mat3(cos(a),-sin(a),0.,sin(a),cos(a),0.,0.,0.,1.);}

vec4 df(vec3 p) // from https://www.shadertoy.com/view/ltcSDr
{
	p *= rotz(p.z * 0.07);
	vec2 uv = p.xz;
	float a = length(uv) - iTime*2.;
	uv *= mat2(cos(a), -sin(a), sin(a), cos(a));
	uv = abs(uv);
	uv = vec2(atan(uv.x, uv.y)/3.14159, length(uv));
	float b = sqrt(uv.x) + sqrt(uv.y);
	float c = sqrt(uv.x + uv.y);
	float s = b-c;
	float d = 6. - abs(p.y)  - smoothstep(0.128,1.-0.128, s*1.32);
	return vec4(d);
}

vec3 nor( in vec3 p, float prec )
{
	vec3 e = vec3( prec, 0., 0. );
	vec3 n = vec3(
	    df(p+e.xyy).x - df(p-e.xyy).x,
	    df(p+e.yxy).x - df(p-e.yxy).x,
	    df(p+e.yyx).x - df(p-e.yyx).x );
	return normalize(n);
}


// return color from temperature 
//http://www.physics.sfasu.edu/astro/color/blackbody.html
//http://www.vendian.org/mncharity/dir3/blackbody/
//http://www.vendian.org/mncharity/dir3/blackbody/UnstableURLs/bbr_color.html
vec3 blackbody(float Temp)
{
	vec3 col = vec3(255.);
    col.x = 56100000. * pow(Temp,(-3. / 2.)) + 148.;
   	col.y = 100.04 * log(Temp) - 623.6;
   	if (Temp > 6500.) col.y = 35200000. * pow(Temp,(-3. / 2.)) + 184.;
   	col.z = 194.18 * log(Temp) - 1448.6;
   	col = clamp(col, 0., 255.)/255.;
    if (Temp < 1000.) col *= Temp/1000.;
    //return col;

    // thansk to public_int_i
    float i1 = sin(iTime)*0.5+0.5,
          i2 = cos(iTime)*0.5+0.5;
   	return col*max(0., 1.0-(i1+i2))+
           col.zxy*i1+
           col.yzx*i2;
}

// get density of the df at surfPoint
// ratio between constant step and df value
float SubDensity(vec3 surfPoint, float prec, float ms) 
{
	vec3 n;
	float s = 0.;
    const int iter = 10;
	for (int i=0;i<iter;i++)
	{
		n = nor(surfPoint,prec); 
		surfPoint = surfPoint - n * ms; 
		s += df(surfPoint).x;
	}

	return 1.-s/(ms*float(iter)); // s < 0. => inside df
}

float SubDensity(vec3 p, float s) 
{
	vec3 n = nor(p,s); 							
	return df(p - n * s).x;						
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);

    vec2 g = fragCoord.xy;
	vec2 si = iResolution.xy;
	vec2 uv = (g+g-si)/si.y;
	float t = -iTime * 0.2;
	vec3 ro = vec3(cos(t),0., sin(t)) * 12.; 
    vec3 cv = vec3(0); 
	vec3 cu = normalize(vec3(0,1,0));
  	vec3 z = normalize(cv-ro);
    vec3 x = normalize(cross(cu,z));
  	vec3 y = cross(z,x);
    float fov = .9;
  	vec3 rd = normalize(fov * (uv.x * x + uv.y * y) + z);

    float s = 1., d = 0.;
	for (int i=0; i<200; i++) 
	{
		if (log(d*d/s/1e5)>0.) break;
		d += (s=df(ro+rd*d).x)*.5;
        fragColor.rgb += d/400.; // new lighting try system
	}

    fragColor.rgb = mix(fragColor.rgb, vec3(d*d/400.), .5);

	vec3 p = ro + rd * d;											// surface point
	vec3 lid = normalize(ro-p); 									// light dir
	vec3 n = nor(p, 0.1);											// normal at surface point
	vec3 refl = reflect(rd,n);										// reflected ray dir at surf point 
	float diff = clamp( dot( n, lid ), 0.0, 1.0 ); 					// diffuse
	float fre = pow( clamp( 1. + dot(n,rd),0.0,1.0), 4. ); 			// fresnel
	float spe = pow(clamp( dot( refl, lid ), 0.0, 1.0 ),16.);		// specular
	vec3 col = vec3(.8,.5,.2);
    float sss = df(p - n*0.001).x/0.1;								// quick sss 0.001 of subsurface
	float sb = SubDensity(p, 1., 0.1);								// deep subdensity from 0.01 to 0.1 (10 iterations)
	vec3 bb = clamp(blackbody(100. * sb),0.,1.);					// blackbody color
	float sss2 = 1. - SubDensity(p, 3.); 							// one step sub density of df of 3 of subsurface

    fragColor.rgb *= (diff + fre + bb * sss2 * .8 + col * sss * .2) * 0.25 + spe * 1.2;

	// vigneting from iq Shader Mike : https://www.shadertoy.com/view/MsXGWr
    vec2 q = g/si;
    fragColor.rgb *= 0.5 + 0.5*pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.55 );

}

// Constants
#define PI 3.1415
#define MOD2 vec2(3.07965, 7.4235)

// Time Scaling
#define time iTime*0.1

// Raymarching
float minPrimStepSize = 0.1 ;
const int primNumSamples = 100 ;

// Colours
const vec3 smokeCol = vec3(30./255., 31./255., 30./255.) ;
const vec3 sunCol = vec3(1.0,1.0,1.0) ;
const vec3 backCol = vec3(206./255., 146./255., 14./255.) ;
const vec3 skyCol = vec3(135.,206.,250.)/255. ;
const vec3 rocketCol = vec3(230.,10.,10.)/255. ;
const vec3 ambientCol = vec3(0.4) ;

// SmokeVals
const int octavesSmoke =  12 ;

vec2 rot2D(vec2 p, float angle) {
    angle = radians(angle);
    float s = sin(angle);
    float c = cos(angle);

    return p * mat2(c,s,-s,c);  
}

float hash(vec3 p)  // replace this by something better
{
    p  = fract( p*0.3183099+.1 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

float noise( in vec3 x ) {
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    return mix(mix(mix( hash(p+vec3(0,0,0)), 
                        hash(p+vec3(1,0,0)),f.x),
                   mix( hash(p+vec3(0,1,0)), 
                        hash(p+vec3(1,1,0)),f.x),f.y),
               mix(mix( hash(p+vec3(0,0,1)), 
                        hash(p+vec3(1,0,1)),f.x),
                   mix( hash(p+vec3(0,1,1)), 
                        hash(p+vec3(1,1,1)),f.x),f.y),f.z);
}

vec3 smokeStart = vec3(5.0,-5.0,5.0) ;
vec3 smokeEnd = vec3(5.0,5.0,5.0) ;
float smokeThickness = 1.0 ;
float offset = 0.1 ;

float sampleSmoke(vec3 position) {
  float noiseVal = 0.0 ;
  float amplitude = 1.0 ;
  float freq = 4.5 ;
  float lac = 2.0 ;
  float scaling = 2.0 ;
  for (int i = 0 ; i < octavesSmoke ; ++i) {
    noiseVal += amplitude * noise(freq*position+vec3(0.0,time*200.0,3.0*time)) ;
    amplitude /= lac ;
    freq *= lac ;
  }

  vec3 smokeDir = normalize(smokeEnd-smokeStart) ;
  float dist = length((smokeStart - position) - (dot((smokeStart - position),smokeDir))*smokeDir) ;
  noiseVal *= exp(-2.5*dist) ;
  noiseVal -= offset ;
  noiseVal *= (1.0 - exp(-0.05 * length(smokeStart-position))) ;
  noiseVal = clamp(noiseVal,0.0,1.0) ;

  return scaling * noiseVal ;
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r ) {
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

void rotate(const float a, inout vec2 v) {
    float cs = cos(a), ss = sin(a);
    vec2 u = v;
    v.x = u.x*cs + u.y*ss;
    v.y = u.x*-ss+ u.y*cs;
}

float pi = 3.1415 ;

float mBox(vec3 p, vec3 b) {
	return max(max(abs(p.x)-b.x,abs(p.y)-b.y),abs(p.z)-b.z);
}

vec2 frot(const float a, in vec2 v) {
    float cs = cos(a), ss = sin(a);
    vec2 u = v;
    v.x = u.x*cs + u.y*ss;
    v.y = u.x*-ss+ u.y*cs;
    return v;
}

void angularRepeat(const float a, inout vec2 v)
{
    float an = atan(v.y,v.x);
    float len = length(v);
    an = mod(an+a*.5,a)-a*.5;
    v = vec2(cos(an),sin(an))*len;
}


float dfRocketBody(vec3 p) {
    float rocketRotation = 1.5*sin(0.9*time) ;
    rotate(rocketRotation,p.xz);
    vec3 p2 = p;
    vec3 pWindow = p;

    angularRepeat(pi*.25,p2.zx);
    float d = p2.z;
    d = max(d, frot(pi*-.125, p2.yz+vec2(-.7,0)).y);
    d = max(d, frot(pi*-.25*.75, p2.yz+vec2(-0.95,0)).y);
    d = max(d, frot(pi*-.125*.5, p2.yz+vec2(-0.4,0)).y);
    d = max(d, frot(pi*.125*.25, p2.yz+vec2(+0.2,0)).y);
    d = max(d, frot(pi*.125*.8, p2.yz+vec2(.55,0)).y);
    d = max(d,-.8-p.x);
    d -= .5;

    vec3 pThruster = p2;
    pThruster -= vec3(-1.46,.0,.0);
    rotate(pi*-.2,pThruster.yz);
    d = min(d,mBox(pThruster,vec3(.1,.4,.27)));
    d = min(d,mBox(pThruster-vec3(-.09,.0,.0),vec3(.1,.3,.07)));


    pWindow -= vec3(.1,.0,.0);
    angularRepeat(pi*.25,pWindow.xy);
    pWindow -= vec3(.17,.0,.0);
    d = min(d,mBox(pWindow,vec3(.03,.2,.55)));

  	return d;
}

float dfRocketFins(vec3 p) {       
    float rocketRotation = 1.5*sin(0.9*time) ;
    rotate(rocketRotation,p.xz);

    vec3 pFins = p;
    angularRepeat(pi*.5,pFins.zx);
    pFins -= vec3(0.0,-1.0+cos(p.y+.2)*.5,.0);
    rotate(pi*.25,pFins.yz);
    float scale = 1.0-pFins.y*.5;
    float d =mBox(pFins,vec3(.17,.03,3.0)*scale)*.5;
    return d;
}

float dfRocket(vec3 p) {
    float proxy = mBox(p,vec3(2.5,.8,.8));
    if (proxy>1.0)
    	return proxy;
    return min(dfRocketBody(p),dfRocketFins(p));
}


float sampleSmokeCap(vec3 position) {
	return sdCapsule(position,smokeStart,smokeEnd,smokeThickness) ;  
}

float sampleRocketCCy(vec3 position) {
    return dfRocket(position-(smokeEnd+vec3(0.0,2.3,0.0))) ;
}

bool isIntersectingSmokeShape(vec3 position, float precis, out float dist) {
    dist = sampleSmokeCap(position) ;
    return dist < precis ;
}

bool isIntersectingRocket(vec3 position, float precis, out float dist) {
    dist = sampleRocketCCy(position) ;
    return dist < precis ;
}

bool rayMarchTrans(vec3 startPos, vec3 direction, out float rayDist) {
    vec3 position = startPos ;
    bool intersected = false ;
    rayDist = 0.0 ;
    float delta = minPrimStepSize ;
    float precis = 0.0005 ;

    for (int i = 0 ; i < primNumSamples ; ++i) {
		if (isIntersectingSmokeShape(position,precis,delta)) {
            return true ;
        } else {
            precis = 0.00005 * rayDist ;
		    rayDist += delta ;
            position = (rayDist)*direction + startPos ;
        }
    }

    return false ;
}

bool rayMarchSolids(vec3 startPos, vec3 direction, out float rayDist) {
    vec3 position = startPos ;
    bool intersected = false ;
    rayDist = 0.0 ;
    float delta = minPrimStepSize ;
    float precis = 0.0005 ;

    for (int i = 0 ; i < primNumSamples ; ++i) {
		if (isIntersectingRocket(position,precis,delta)) {
            return true ;
        } else {
            precis = 0.0005 * rayDist ;
		    rayDist += delta ;
            position = (rayDist)*direction + startPos ;
        }
    }

    return false ;
}


const float extinctionCoeff = 13.0 ;
const float scatteringCoeff = 12.5 ;
const float secSmokeSampleSize = 0.2 ;
const int secSmokeNumSamples = 5 ;

float getIncidentSunlight(vec3 startPos, vec3 lightDir) {
    vec3 position = startPos ;
    vec3 stepVector = lightDir * secSmokeSampleSize ;
    float extinction = 1.0 ;
    float dist = 0.0 ;
    for (int i = 0 ; i < secSmokeNumSamples ; ++i) {
        if (!isIntersectingSmokeShape(position,0.005,dist))
            break ;
	    float density = sampleSmoke(position) ;
        extinction *= exp(-extinctionCoeff*density*secSmokeSampleSize) ;
        position += stepVector ;
    }
    return extinction ;
}

const float primSmokeSampleSize = 0.1 ;
const int primSmokeNumSamples = 50 ;

vec4 primaryRayMarchSmoke(vec3 startPos, vec3 direction, vec3 lightDir) {
    vec3 position = startPos ;
    vec3 stepVector = direction * primSmokeSampleSize ;
    float dist ;
    float extinction = 1.0 ;
    vec3 colour = vec3(0.0) ;
    for (int i = 0 ; i < primSmokeNumSamples ; ++i) {
        if (extinction < 0.05 || !isIntersectingSmokeShape(position,0.005,dist))
            break ;
     	float vertDistFromRocket = abs(position.y - smokeEnd.y) ;
        float deltaYDensityMod = (1.f-(vertDistFromRocket)/(smokeEnd.y-smokeStart.y));
		float density = sampleSmoke(position) * deltaYDensityMod * deltaYDensityMod;
        extinction *= exp(-extinctionCoeff*density*primSmokeSampleSize);
        vec3 scattering = primSmokeSampleSize * density * scatteringCoeff * (ambientCol +  sunCol * getIncidentSunlight(position, lightDir)) ;
        colour += scattering * extinction ;
        position += stepVector ;
    }

    return vec4(colour,extinction) ;    
}

vec3 calcSkyCol(in vec3 direction, in vec3 lightDir) {	
    float sunAmount = max( dot(direction, lightDir), 0.0 );
	float v = pow(1.0-max(direction.y,0.0),5.)*.5;
	vec3  sky = vec3(v*sunCol.x*0.4+skyCol.x, v*sunCol.y*0.4+skyCol.y, v*sunCol.z*0.4+skyCol.z);
	sky = sky + sunCol * pow(sunAmount, 6.5)*.12;
	sky = sky+ sunCol * min(pow(sunAmount, 1200.), .3)*.65;
	return sky;
}

// Taken from IQ : https://www.shadertoy.com/view/Xds3zN
vec3 calcRocketNormal( in vec3 pos ) {
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*sampleRocketCCy( pos + e.xyy ) + 
					  e.yyx*sampleRocketCCy( pos + e.yyx ) + 
					  e.yxy*sampleRocketCCy( pos + e.yxy ) + 
					  e.xxx*sampleRocketCCy( pos + e.xxx ));
}

vec4 calcRocketColour(in vec3 position,in vec3 direction, in vec3 lightDir) {	
	vec3 nor = calcRocketNormal(position) ;
    vec3 ref = reflect( direction, nor);
    vec3 col = vec3(0.0) ;
    // lighting        
	vec3  lig = lightDir ;
    float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
    float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
    float spe = pow(clamp( dot( ref, lig ), 0.0, 1.0 ),16.0);

    if (dfRocketBody(position)>dfRocketFins(position)) {
    	if (position.y<smokeEnd.y-0.85 || position.y>smokeEnd.y+1.34)
        	if (position.y>smokeEnd.y+3.41)
            	col = vec3(.1,.1,.1);
            else
                col = vec3(.9,.9,.9);
        else {
        	col = vec3(.1,.1,.1);
        }
    }

	vec3 lin = vec3(0.0);
    lin += 4.30*dif*vec3(1.00,0.80,0.55);
	lin += 7.00*spe*vec3(1.00,0.90,0.70)*dif;
    lin += 1.00*amb ;
	col = col*lin;

    return vec4(col,0.0) ;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - iResolution.xy * .5) / iResolution.y;
    vec2  m = 2.*((iMouse.xy / iResolution.xy) - 0.5);

    if (iMouse.xy == vec2(0)) {
       m.y = 0.0 ;   
    }

    vec3 dir = vec3(uv, 1.);
    dir.yz = rot2D(dir.yz,  90. * m.y);
    dir.xz = rot2D(dir.xz, 45.);
    dir = normalize(dir) ;

    vec3 cameraPos = vec3(0.0,7.0,3.0*offset) ;


   float lightElev = 10. * 3.14/180. ;
   float lightAzi = 90. * 3.14/180. + time * 20. ;
   vec3 lightDir = vec3(cos(lightAzi)*cos(lightElev),sin(lightElev),sin(lightAzi)*cos(lightElev));


    float rayDistTrans = 0.0 ;
    float rayDistSolid = 0.0 ;
    vec4 colour = vec4(vec3(0.0),1.0) ;
    bool isTransPresent = rayMarchTrans(cameraPos,dir,rayDistTrans) ;
    bool isSolidPresent = rayMarchSolids(cameraPos,dir,rayDistSolid) ;

    if (isTransPresent && isSolidPresent) {
        if (rayDistSolid < rayDistTrans) {
            colour = calcRocketColour(cameraPos+dir*rayDistSolid,dir,lightDir) ; 
        } else {
        	colour = primaryRayMarchSmoke(cameraPos+dir*rayDistTrans,dir,lightDir) ; 
            colour = vec4(mix(colour.rgb,calcRocketColour(cameraPos+dir*rayDistSolid,dir,lightDir).rgb,colour.a),0.0) ;
        }
    } else if (isTransPresent) {
        colour = primaryRayMarchSmoke(cameraPos+dir*rayDistTrans,dir,lightDir) ;
    } else if (isSolidPresent) {
        colour = calcRocketColour(cameraPos+dir*rayDistSolid,dir,lightDir) ;    
    }
    vec3 skyCol = calcSkyCol(dir,lightDir) ;
    colour.rgb = mix(colour.rgb,skyCol,colour.a) ;
    fragColor = vec4(colour.rgb,1.0) ;
}

bool wave(in vec2 coord, float pDistance)
{
    float myTime = iTime * 1.0;
	float WidthSea = cos(coord.x*2.0*1.5*pDistance*3.1415/360.0 - myTime) * 50.0*1.0/pDistance+125.0+25.0*1.0/pDistance;    
    return coord.y <= WidthSea;
}


void mainImage( out vec4 color, in vec2 pixCoord )
{
		float h = 0.0;

	float m = pow( texture( iChannel0, vec2(h*0.5,0.25) ).x, 2.0 )*(1.0+2.0*h);

    //Camera
    float camCenterX = iResolution.x/5.25 + 5.0*cos(m*1000.0);
    float camCenterY = iResolution.y/5.25+ 5.0*sin(m*927.0);
    float zoom = 0.4-0.3*m;

    pixCoord.x = (pixCoord.x - camCenterX) / zoom;
    pixCoord.y = (pixCoord.y - camCenterY) / zoom;

    float xTemp = pixCoord.x * cos(iTime) + pixCoord.y * sin(iTime);
    float yTemp = -pixCoord.x * sin(iTime) + pixCoord.y * cos(iTime);
    pixCoord.x = xTemp;
    pixCoord.y = yTemp;

    //Drawing
    float radius = 50.0;
    float radtodeg = 180.0/3.1415;
    float centerX = iResolution.x/1.25;
    float centerY = iResolution.y/1.25;    
    float angle = atan((centerX - pixCoord.x), (centerY - pixCoord.y))*radtodeg + iTime;
    float distance = sqrt( pow(centerX - pixCoord.x, 2.0) + pow(centerY - pixCoord.y, 2.0));


    if (wave(pixCoord, 1.0))
        color  = vec4(0.0,0.0,1.0,1.0);
    else if (distance < radius)
        color = vec4(1.0, 1.0, 0.0, 1.0);
    else if (wave(pixCoord, 2.0))
        color  = vec4(0.2,0.2,1.0,1.0);
    else if (wave(pixCoord, 3.0))
        color = vec4(0.3,0.3,1.0,1.0);
    else if ((mod(angle, 15.0) >= 0.0 )&& mod(angle, 15.0) < 6.0)
        color = vec4(1.0, 1.0, 0.0, 1.0);
    else
       	color = vec4(0.6, 0.6, 1.0, 1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ((120.0 + iTime) / 3.0) * ((fragCoord.xy - (iResolution.xy / 2.0)) / iResolution.y);

    float r = mod(iTime / 100.0 + acos(-1.0) / 4.0, acos(-1.0)*2.0);
    mat2 rot = mat2(cos(r), -sin(r), sin(r), cos(r));
    uv = uv * rot;

    float value = (uv.x * sign(uv.y) + uv.y * sign(uv.x)) * sin(uv.x) * sin(uv.y);
    float color = sin(value) * 3.0;
    color = color * sign(uv.x) * sign(uv.y); // revision 2

    float low = abs(color);
    float med = abs(color) - 1.0;
    float high = abs(color) - 2.0;
    if(color > 0.0) {
	  fragColor = vec4(med, high, high,1.0);
    } else {
      fragColor = vec4(high, high, med,1.0);
    }
}

vec3 intersectCircle(vec2 target, vec2 origin, vec2 sphereCenter, float r) {
    vec2 d = target - origin;

    float a = dot(d, d);
    float b = 2.0 * dot(d, origin - sphereCenter);
    float c = dot(sphereCenter, sphereCenter) + dot(origin, origin) - 2.0 * dot(sphereCenter, origin) - r*r;
    float test = b*b - 4.0*a*c;

    bool behind = length(origin - target) < length(origin - sphereCenter);

	if (test >= 0.0 && !behind) {
  		float u = (-b - sqrt(test)) / (2.0 * a);
  	    vec2 hitp = origin + u * (target - origin);

        if (length(hitp.xy - target) > length(target - origin)) {
            return vec3(-1.0,0.0,.0); 
        }
        return vec3(1.0, hitp);
    } else {
        return vec3(-1.0,0.0,.0);   
    }
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspectRatio = iResolution.x/iResolution.y;
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= aspectRatio;
    vec2 sphereCenter = vec2(0.5 * aspectRatio, 0.5);
    float sphereRadius = 0.3;
    float orbitRadius = 0.5;

    vec2 lightPos = sphereCenter + vec2(orbitRadius * sin(iTime), orbitRadius * cos(iTime));
    float lightRadius = 0.02;

    if (length(uv-sphereCenter) < sphereRadius) {
        fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    } else if (length(uv-lightPos) < lightRadius) {
        fragColor = vec4(1.0, 1.0, 0.0,1.0);
    } else {
        vec3 hit = intersectCircle(lightPos, uv, sphereCenter, sphereRadius);
        if (hit.x >= 0.0) {
            fragColor = vec4(vec3(0.0),1.0);
        } else {
            fragColor = vec4(0.8, 0.78, 0.85, 1.0);
        }
    }

}

/*

	Smooth Noise Contours
	---------------------

    Using a cheap - but effective - hack to produce antialiased-looking contour lines without the 
	need for supersampling. I had Airtight's elegant "Cartoon Fire" shader in mind when making this, 
	and have provided a link to it below. 

	I've always liked the abstract look of functional contour segments, lines, etc. There's a couple 
	of ways to produce them, depending on the look you're going for. One method involves combining
	your function value (noise, Voronoi, etc) with the "fract" function and the other involves 
	stepping the function values with the "floor" function.

    Each looks all right, except for the aliasing. You could take care of that with supersampling,
	but it's a lot of work for the GPU, so I figured there might be a way to combine the "smoothstep" 
	and "fwidth" functions to produce a smooth "fract" function. Since "x - fract(x)" is "floor(x)," 
	you'd get the "floor" function too.

	After playing around for a while, I came up with something that seems to work. As you can see,
	the partitioned contours look relatively jaggy free, even after the application of border lines 
	and highlighting.

	Anyway, the smooth fract "sFract" and complimentary smooth floor "sFloor" functions are below.
	They haven't undergone extensive testing, so I'd use them cautiously. :)

	The rest is just coloring and highlighting. I went for a simplistic cardboard cutout, vector-graphic
	style.

	Similar examples:

    Cartoon Fire - airtight
	https://www.shadertoy.com/view/lsscWr

    // More sophisticated smoothing method, but I might switch to this one in future.
	Smooth Voronoi Contours - Shane
    https://www.shadertoy.com/view/4sdXDX


*/


// Variable to a keep a copy of the noise value prior to palettization. Used to run a soft gradient 
// over the surface, just to break things up a little.
float ns;


//float sFract(float x, float sm){ float fx = fract(x); return fx - smoothstep(fwidth(x)*sm, 0., 1. - fx); }
//float sFract(float x, float sm){ float fx = fract(x); return min(fx, fx*(1. - fx)/fwidth(x)/sm); }

// Based on Ollj's smooth "fract" formula.
float sFract(float x, float sm){

    // Extra smoothing factor. "1" is the norm.
    const float sf = 1.; 

    // The hardware "fwidth" is cheap, but you could take the expensive route and
    // calculate it by hand if more quality was required.
    vec2 u = vec2(x, fwidth(x)*sf*sm);

    // Ollj's original formula with a transcendental term omitted.
    u.x = fract(u.x);
    u += (1. - 2.*u)*step(u.y, u.x);
    return clamp(1. - u.x/u.y, 0., 1.); // Cos term ommitted.
}



// Only correct for nonnegative values, but in this example, numbers aren't negative.
float sFloor(float x){ return x - sFract(x, 1.); } 

// Standard hue rotation formula with a bit of streamlining. 
vec3 rotHue(vec3 p, float a){

    vec2 cs = sin(vec2(1.570796, 0) + a);

    mat3 hr = mat3(0.299,  0.587,  0.114,  0.299,  0.587,  0.114,  0.299,  0.587,  0.114) +
        	  mat3(0.701, -0.587, -0.114, -0.299,  0.413, -0.114, -0.300, -0.588,  0.886) * cs.x +
        	  mat3(0.168,  0.330, -0.497, -0.328,  0.035,  0.292,  1.250, -1.050, -0.203) * cs.y;

    return clamp(p*hr, 0., 1.);
}


