#define NSTEPS 16
#define pi 3.1415926535

float computeCoverage(vec2 eo, vec2 es)
{
    bool swapped = false;
    if (es.x * es.y > 1.0 || true) {
        es = 1.0 / es;
        eo *= -es;
        swapped = true;
    }

    float area = 0.0;
    float theta = 0.0;
    float thetaStep = 2.0*pi/float(NSTEPS);
	vec2 prev = vec2(es.x * cos(0.0) + eo.x,
                     es.y * sin(0.0) + eo.y);
    float r = length(prev);
    if (r > 1.0) {
        prev /= r;
    }

    for (int i=0; i<NSTEPS; i++) {
        theta += thetaStep;
        float x = es.x * cos(theta) + eo.x;
        float y = es.y * sin(theta) + eo.y;

        float r = length(vec2(x, y));
        if (r > 1.0) {
            x /= r;
            y /= r;
        }

        area += prev.x * y;
        area -= prev.y * x;

        prev = vec2(x, y);
    }

    area *= 0.5;

    if (swapped) {
        return area / (pi * es.x * es.y);
    }

    return area / pi;
}

