float hash(vec3 p) 
{
    p  = fract( p*0.3183099+.1 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    return mix(mix(mix( hash(p+vec3(0,0,0)), 
                        hash(p+vec3(1,0,0)),f.x),
                   mix( hash(p+vec3(0,1,0)), 
                        hash(p+vec3(1,1,0)),f.x),f.y),
               mix(mix( hash(p+vec3(0,0,1)), 
                        hash(p+vec3(1,0,1)),f.x),
                   mix( hash(p+vec3(0,1,1)), 
                        hash(p+vec3(1,1,1)),f.x),f.y),f.z);
}

/*
	Fractal noise 7 iter [0,1]
*/
float fractn(vec3 p)
{
    float f = 0.0;
    p = p * 3.0;
    f += 0.50000 * noise(p); p = 2.0 * p;
    f += 0.25000 * noise(p); p = 2.0 * p;
	f += 0.12500 * noise(p); p = 2.0 * p;
	f += 0.06250 * noise(p); p = 2.0 * p;
    f += 0.03125 * noise(p); p = 2.0 * p;
    f += 0.015625 * noise(p); p = 2.0 * p;
    f += 0.0078125 * noise (p); p = 2.0 * p;

    return f;
}

struct Plane
{
    vec3 Point;
    vec3 Normal;
};

float iPlane( vec3 ro, vec3 rd, Plane plane)
{
    float hit = 0.0;
    float dotP = dot(rd,plane.Normal);
    if(dotP == 0.0)
    {
        return hit;
    }

    float distToHit = dot(plane.Point - ro, plane.Normal) / dotP;
    if(distToHit < 0.0)
    {
        return hit;
    }

    hit = distToHit;
    return hit;
}

/*
	We perform a set of samples from the point that we hit the cloud
	by a distance. The returned value will tell us how "thick" each cloud
	is so we can simulate light absorption.
	With 16 samples we can see some banding.
	If I increase the samples my WebGl just crashes :C
*/
float scattering(vec3 ro,vec3 rd)
{
    const int samples = 16;
    float sampleDist = 1.0;
    float acum = 0.0;
    for(int i = 0; i < samples; i++)
    {
        float idx = float(i) / float(samples);
        acum += fractn(ro + (rd * (idx * sampleDist)));
    }
    return acum / float(samples);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
	vec2 uv = (fragCoord.xy/iResolution.xy) - 0.5;

    // Colors
    vec3 color = vec3(0.0,0.0,0.0); 
    vec3 skyColor = mix(vec3(1.0,1.0,1.0),vec3(0.5,0.8,0.98),sqrt(uv.y + 0.5));
    color = skyColor;

    // Plane clouds
    Plane clouds;
    clouds.Point = vec3(0.0,200.0,0.0);
    clouds.Normal = vec3(0.0,1.0,0.0);

    // Setup camera
    vec3 camUp = vec3(0,1,0);
    vec3 camFront = vec3(0,0.1,1);
    vec3 camRight = vec3(1,0,0);
    vec3 camPos = vec3(0,0,-1) * 0.0;
    vec3 camRay = camUp * uv.y + camRight * uv.x + camFront;

	// Check clouds intersection
    float id = 0.0;
    float dPlane = iPlane(camPos,camRay,clouds);
    vec3 cloudColor = vec3(0.0,0.0,0.0);
    if(dPlane > 0.0)
    {
        // Sample noise and modulate noise
        vec3 pos = camPos + (camRay * dPlane);
        vec2 off = vec2(iTime,iTime) * vec2(-10,0);
        float n = fractn(vec3(pos.x + off.x,pos.y,pos.z + off.y) *0.002);
        n = smoothstep(0.2,1.0,n);
        cloudColor = mix(skyColor,vec3(1.0,1.0,1.0),n);

        // Fade with distance
        float alpha = abs(1.0 - clamp(dPlane/5000.0,0.0,1.0));
        cloudColor = mix(skyColor,cloudColor,alpha);

        // Cloud Scattering
        float scat = scattering(vec3(pos.x + off.x,pos.y,pos.z + off.y) *0.002,camRay);
        scat = smoothstep(0.0,0.7,scat);
        color = mix(skyColor,cloudColor * scat,alpha);

    }

    fragColor = vec4(color,1.0);
}

