const vec4 light = vec4(205.0,214.0,224.0,255.0)/255.0;
const vec4 dark = vec4(32.0,74.0,135.0,255.0)/255.0;
vec2 foo;
bool bar;

vec4 circle(vec2 pos,vec2 fragCoord, float theta){
    vec2 opos = pos; //
    for(float i=0.0;i<4.0;i++){
        for(float j=0.0;j<4.0;j++){

            pos.x = opos.x + j*8.0*d*(abs(sin(iTime)))/3.0;
            pos.y = opos.y + i*sin(iTime)*-8.0*d/3.0;

            if (length(pos-fragCoord)<=d){
                foo = vec2(i,j);
                bar = (sin(iTime)>0.0);
                if(!bar){
                	return ((i==2.0&&j<3.0)||j==1.0||foo==vec2(0,2)||foo==vec2(3,3)) ? dark:light;
                }else{
                    return ((i==3.0&&j>0.0)||(i==2.0&&mod(j,2.0)==1.0)||(i==1.0&&mod(j,2.0)==0.0)) ? dark:light;
                }
            }
        }
    }
    //return vec4(0.5*sin(iTime*4.0)+0.5,0.5*cos(iTime)+0.5,0.5*sin(3.0*iTime + 45.0)+0.5,1.0);
    return vec4(1.0);
}

