// Signed Distance to a polySpline 
//
// Inspired by CPU's shader   https://www.shadertoy.com/view/MtVSRD
// and ajs15822's  https://www.shadertoy.com/view/ltXSDB
// 
//My first objective was to make an anti-aliased version of CPU's polyspline shader,
//but without the distance field seems impossible for me, at least without calculating 4 samples of each pixel,
//so i tried a distance field aproach and mix 3 distance fields.

//Calculating the 3 distance field to the 3 beziers like ajs15822's shader did not work either,
//but using the sign calculation of the first polyspline shader into this second shader did the job.
//Pretty slow to use it on anything real.



// Test if point p crosses line (a, b), returns sign of result
float testCross(vec2 a, vec2 b, vec2 p) {
    return sign((b.y-a.y) * (p.x-a.x) - (b.x-a.x) * (p.y-a.y));
}

// Determine which side we're on (using barycentric parameterization)
float signBezier(vec2 A, vec2 B, vec2 C, vec2 p)
{ 
    vec2 a = C - A, b = B - A, c = p - A;
    vec2 bary = vec2(c.x*b.y-b.x*c.y,a.x*c.y-c.x*a.y) / (a.x*b.y-b.x*a.y);
    vec2 d = vec2(bary.y * 0.5, 0.0) + 1.0 - bary.x - bary.y;
    return mix(sign(d.x * d.x - d.y), mix(-1.0, 1.0, 
        step(testCross(A, B, p) * testCross(B, C, p), 0.0)),
        step((d.x - d.y), 0.0)) * testCross(A, C, B);
}

// Solve cubic equation for roots
vec3 solveCubic(float a, float b, float c)
{
    float p = b - a*a / 3.0, p3 = p*p*p;
    float q = a * (2.0*a*a - 9.0*b) / 27.0 + c;
    float d = q*q + 4.0*p3 / 27.0;
    float offset = -a / 3.0;
    if(d >= 0.0) { 
        float z = sqrt(d);
        vec2 x = (vec2(z, -z) - q) / 2.0;
        vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
        return vec3(offset + uv.x + uv.y);
    }
    float v = acos(-sqrt(-27.0 / p3) * q / 2.0) / 3.0;
    float m = cos(v), n = sin(v)*1.732050808;
    return vec3(m + m, -n - m, n - m) * sqrt(-p / 3.0) + offset;
}

// Find the signed distance from a point to a bezier curve
float sdBezier(vec2 A, vec2 B, vec2 C, vec2 p)
{    
    B = mix(B + vec2(1e-4), B, abs(sign(B * 2.0 - A - C)));
    vec2 a = B - A, b = A - B * 2.0 + C, c = a * 2.0, d = A - p;
    vec3 k = vec3(3.*dot(a,b),2.*dot(a,a)+dot(d,b),dot(d,a)) / dot(b,b);      
    vec3 t = clamp(solveCubic(k.x, k.y, k.z), 0.0, 1.0);
    vec2 pos = A + (c + b*t.x)*t.x;
    float dis = length(pos - p);
    pos = A + (c + b*t.y)*t.y;
    dis = min(dis, length(pos - p));
    pos = A + (c + b*t.z)*t.z;
    dis = min(dis, length(pos - p));
    return dis;    // * signBezier(A, B, C, p);  //No need for this sign
}


#define setX(f) if (f>=0.&& f<1. && mix(mix(a.x,b.x,f),mix(b.x,c.x,f),f)<X) odd=!odd;

	float polySpline(vec2 p,vec2 AA,vec2 BB,vec2 CC,vec2 FF,vec2 GG,vec2 HH) {
		bool odd = false;
		float eps = 1e-7, root, A, B, C, X = p.x, Y = p.y;

		vec2 a = AA.xy;// readCell(np - 1., 0.).xy;

		vec2 b = BB.xy;
		vec2 c = CC.xy;
		A = a.y + c.y - b.y - b.y;
		B = 2.*(b.y - a.y);
		C = a.y - Y;
		if (abs(A)<eps) {
			setX(-C / B);
		}
		else {
			float root = B*B - 4.*A*C;
			if (root>0.) {
				root = sqrt(root);
				setX((-B - root) / (2.*A));
				setX((-B + root) / (2.*A));
			}
		}
		a = c;

		//------
		b = FF.xy;
		c = GG.xy;
		A = a.y + c.y - b.y - b.y;
		B = 2.*(b.y - a.y);
		C = a.y - Y;
		if (abs(A)<eps) {
			setX(-C / B);
		}
		else {
			float root = B*B - 4.*A*C;
			if (root>0.) {
				root = sqrt(root);
				setX((-B - root) / (2.*A));
				setX((-B + root) / (2.*A));
			}
		}
		a = c;

		//------
		b = HH.xy;
		c = AA.xy;
		A = a.y + c.y - b.y - b.y;
		B = 2.*(b.y - a.y);
		C = a.y - Y;
		if (abs(A)<eps) {
			setX(-C / B);
		}
		else {
			float root = B*B - 4.*A*C;
			if (root>0.) {
				root = sqrt(root);
				setX((-B - root) / (2.*A));
				setX((-B + root) / (2.*A));
			}
		}
		a = c;

		return float(odd);
	}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    vec2 m = mix((2.0*iMouse.xy-iResolution.xy)/iResolution.y,
        vec2(cos(iTime * 1.2) * 0.8, 0.0), step(iMouse.z, 0.0));

    // Define the control points of our curve
    vec2 A = vec2(-0.4, -0.6), C = vec2(-0.6, +0.4001), B = (4.0 * m - A - C) / 2.0;
    B= vec2(-1.4,0.0);

    vec2 F=vec2(0.5,0.5);
    vec2 G=vec2(1.25,0.5);
    vec2 H=vec2(0.5,-0.5);

    F.x += cos(iTime * 0.5) * 0.8;
    F.y += cos(iTime * 3.5) * 0.4;

    H.x += cos(iTime * 0.53) * 0.8;
    H.y += cos(iTime * 3.35) * 0.4;

    B.x += 0.7 + cos(iTime * 0.53) * 0.45;
    B.y += cos(iTime * 3.35) * 0.4;

    G.x += cos(iTime * 0.3) * 0.4;
    G.y += cos(iTime * 2.35) * 0.2;

    A.x += cos(iTime * 0.3) * 0.4;
    A.y += cos(iTime * 2.35) * 0.2;

    // Render the control points
    float d = min(distance(p,H),min(distance(p,G), min(distance(p,B),   min(distance(p, A),(min(distance(p, F),distance(p, C)))))) );
    if (d < 0.04) { fragColor = vec4(1.0 - smoothstep(0.025, 0.034, d)); return; }

    B+=(B-((A+C)*0.5));		//Made the curve pass through the points
    F+=(F-((C+G)*0.5));
    H+=(H-((G+A)*0.5));

    // Get the signed distance to bezier curve
    d = sdBezier(A, B, C, p);
    float d2 = sdBezier(C,F,G, p);
    float d3 = sdBezier(G,H,A, p);

    float spSign =polySpline(p,A,B,C,F,G,H);

    d=min(abs(d),min(abs(d2),abs(d3)));
    if (spSign<=0.){
        d=-d;
    }

    // Visualize the distance field using iq's orange/blue scheme
    fragColor = vec4(1.0) - sign(d)*vec4(0.1,0.4,0.7,1.0);
    fragColor *= (1.0 - exp(-4.0*abs(d))) * (0.8 + 0.2*cos(140.*d));
    fragColor = mix(fragColor, vec4(1.0), 1.0-smoothstep(0.0,0.02,abs(d)) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ((130.0 + iTime) / 3.0) * ((fragCoord.xy - (iResolution.xy / 2.0)) / iResolution.y);

    float value = (uv.x * sign(uv.y) + uv.y * sign(uv.x)) * sin(uv.x) * sin(uv.y);
    float color = sin(value) * 3.0;
    color = color * sign(uv.x) * sign(uv.y); // revision 2

    float low = abs(color);
    float med = abs(color) - 1.0;
    float high = abs(color) - 2.0;
    if(color > 0.0) {
	  fragColor = vec4(high, med, high,1.0);
    } else {
      fragColor = vec4(med, high, med,1.0);
    }
}

// from Syntopia http://blog.hvidtfeldts.net/index.php/2015/01/path-tracing-3d-fractals/
vec2 rand2n(vec2 co, float sampleIndex) {
    vec2 seed = co * (sampleIndex + 1.0);
	seed+=vec2(-1,1);
    // implementation based on: lumina.sourceforge.net/Tutorials/Noise.html
    return vec2(fract(sin(dot(seed.xy ,vec2(12.9898,78.233))) * 43758.5453),
                fract(cos(dot(seed.xy ,vec2(4.898,7.23))) * 23421.631));
}

vec2 tp1 = vec2(0.26607724, 0);
vec2 tp2 = vec2(0, 0.14062592);
vec2 cPos = vec2(2.01219217, 3.62584500);
float r = 4.02438434;
const float PI = 3.14159265359;

void calcCircle(float theta, float phi){
	float tanTheta = tan(PI/2. - theta);
    float tanPhi = tan(phi);
    float tanTheta2 = tanTheta * tanTheta;
    float tanPhi2 = tanPhi * tanPhi;

    tp1 = vec2(sqrt((1. + tanTheta2)/(-tanPhi2 + tanTheta2)) - 
               tanTheta * sqrt((1. + tanPhi2)/(-tanPhi2 + tanTheta2))/tanTheta, 0.);
    tp2 = vec2(0., -tanPhi * sqrt(-(1. + tanTheta2)/(tanPhi2 - tanTheta2))+
              tanTheta * sqrt(-(1. + tanPhi2)/(tanPhi2 - tanTheta2)));


    cPos = vec2(sqrt((1. + tanTheta2)/(-tanPhi2 + tanTheta2)),
                 sqrt((1. + tanPhi2)*tanTheta2/(-tanPhi2 + tanTheta2))
               );
    r = sqrt((1. + tanPhi2)*(1. + tanTheta2) /(-tanPhi2 + tanTheta2));
}

vec2 circleInverse(vec2 pos, vec2 circlePos, float circleR){
	return ((pos - circlePos) * circleR * circleR)/(length(pos - circlePos) * length(pos - circlePos) ) + circlePos;
}

const int ITERATIONS = 20;
float colCount = 0.;
int IIS(vec2 pos){
    if(length(pos) > 1.) return 0;

    bool fund = true;
    int invCount = 1;
	for(int i = 0 ; i < ITERATIONS ; i++){
        fund = true;
        if (pos.x < 0.){
            pos *= vec2(-1, 1);
            invCount++;
	       	fund = false;
        }
        if(pos.y < 0.){
            pos *= vec2(1, -1);
            invCount++;
            fund = false;
        }
        if(distance(pos, cPos) < r ){
        	pos = circleInverse(pos, cPos, r);
            invCount++;
            colCount++;
            fund = false;
        }
        if(fund)
        	return invCount;
    }

	return invCount;
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec2 compQuot(const vec2 a, const vec2 b){
	float denom = dot(b, b);
    return vec2((a.x * b.x + a.y * b.y) / denom,
                (a.y * b.x - a.x * b.y) / denom);
}

float circEasingInOut(float t){
	t /= .5;
	if (t < 1.) return -.5 * (sqrt(1. - t*t) - 1.);
	t -= 2.;
	return .5 * (sqrt(1. - t*t) + 1.);
}

float circEasingIn(float t){
	return -  (sqrt(1. - t*t) - 1.);
}

float expEasingIn(float t){
    return pow( 2., 13. * (t - 1.) );
}


float quartEasingInOut(float t) {
	t *= 2.;
	if (t < 1.) return .5*t*t*t*t*t;
	t -= 2.;
	return .5*(t*t*t*t*t + 2.);
}


float scene(in float t, in float w, in float s){
    return clamp(t - w, 0.0, s) / s;  
}

const float sampleNum = 30.;
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    float ratio = iResolution.x / iResolution.y / 2.0;
    vec3 sum = vec3(0);
    calcCircle(PI/4., PI/8.);
	float t = mod(iTime, 4.);    
    float zoomingTime = 2.;
    float stay = 0.;
    const float maxZoomOut = 5.;
    for(float i = 0. ; i < sampleNum ; i++){
        vec2 position = ( (fragCoord.xy + rand2n(fragCoord.xy, i)) / iResolution.yy ) - vec2(ratio, 0.5);
        position.y += 0.3;

        //position = position * 2.;

        if(t < zoomingTime + stay)
        	position *= mix(maxZoomOut, 0.01,
                            quartEasingInOut(scene(t, 0., zoomingTime)));
        else
            position *= mix(0.01, maxZoomOut,
                            quartEasingInOut(scene(t, zoomingTime + stay, zoomingTime)));

        //position += vec2(cos(iTime), 0.3 * sin(iTime));

        if(position.y < 0.) continue;

        // upper half plane -> Poincare disk
        vec2 poincare = compQuot(position - vec2(0, 1), position + vec2(0, 1));

        int d = IIS(poincare);

       	if(d == 0){
           	sum += vec3(0.,0.,0.);
       	}else{
           	if(mod(float(d), 2.) == 0.){
               	sum += hsv2rgb(vec3(0.5 + position.y * 0.05, 1., 1.));
        	}else{
            	sum += hsv2rgb(vec3(1., 1., 1.));
        	}
        }
    }
    fragColor = vec4(sum/sampleNum, 1.);
}

float integrate(vec2 center, float radius, vec2 pt) {
    if (length(pt - center) > 0.5) {
     	return 0.0;   
    }
    float sum = 0.0;
    for(int i  = 0; i < 28; i++) {
     	float theta = (float(i) / 28.0) * 2.0 * 3.14 + 0.1*iTime;
        vec2 curr = center + radius * vec2(cos(theta), sin(theta));
        float d = length(curr - pt);
        sum += 1.0 / (pow(d, 1.44));
    }
    return 0.005*sum;
}    





float compute_light_from_square(vec2 outerMin, vec2 outerMax, vec2 pos) {
   	float d = 0.0;
    // compute the light contribution from each fluorescent segment
    d += integrate(0.5*(outerMin+outerMax), 0.5 * (outerMax.x - outerMin.x), pos);
    return d;
}    


void mainImage( out vec4 fragColor, in vec2 fragCoordinate )
{
    fragColor = vec4(0.0);
    vec2 pos = fragCoordinate/iResolution.xy;
    float aspect = iResolution.x/iResolution.y;
    pos.x *=aspect;
    float PADDING_X = 0.1;
    float PADDING_Y = 0.1;
    int NUM_BOXES = 6;
    float BOX_PADDING = 0.009;
    float boxWidth = (1.0-2.0*PADDING_X-0.5*float(NUM_BOXES-1)*BOX_PADDING)/float(NUM_BOXES);
    float boxHeight = (1.0-2.0*PADDING_X-0.5*float(NUM_BOXES-1)*BOX_PADDING)/float(NUM_BOXES);
    float d = 0.0;
    for(int i = 0; i < 2*NUM_BOXES; i++) {
        for(int j = 0; j < NUM_BOXES; j++) {
			vec2 outerMin = vec2(float(i) * boxWidth + PADDING_X, float(j) * boxHeight + PADDING_Y);
            vec2 outerMax = outerMin + vec2(boxWidth, boxHeight);
            outerMin += vec2(BOX_PADDING, BOX_PADDING);
            outerMax -= vec2(BOX_PADDING, BOX_PADDING);
			d += compute_light_from_square(outerMin, outerMax, pos);
        }
    }
    vec4 mixColor =  vec4(19.0/255.0, 98.0/255.0, 128.0/255.0, 1.0);
    fragColor =  mix(0.00015 *vec4(pow(d, 2.0 + 0.23)), mixColor, 0.4);
}

const float SPEED = 1.5;

void drawSphere( out vec4 fragColor, in vec2 fragCoord, in vec2 center, in vec4 color, in float radius)
{    
    vec2 pos = fragCoord - center;

    vec3 lumVector = vec3(sin(iTime*SPEED),0.39,cos(iTime*SPEED));
    vec3 posVector = vec3(pos.x, pos.y, sqrt(pow(radius, 2.0)-pow(pos.x, 2.0)-pow(pos.y, 2.0)));

    vec3 unitLumVector = normalize(lumVector);
    vec3 unitPosVector = normalize(posVector);

    float lumIntensity = unitLumVector.x*unitPosVector.x+unitLumVector.y*unitPosVector.y+unitLumVector.z*unitPosVector.z;   

    if(sqrt(pow(pos.x,2.0)+pow(pos.y,2.0))<radius){
        fragColor = color * lumIntensity;
    }
}

void drawSphereDiagonal(out vec4 fragColor, in vec2 fragCoord, in vec4 beginColor, in vec4 endColor, in float nbSphere)
{
    vec4 deltaColor = endColor-beginColor;
    vec4 stepColor = deltaColor / (nbSphere-1.0);

    float radius = iResolution.y/(nbSphere+5.0);
    vec2 beginCenter = vec2(iResolution.x/(nbSphere+1.0), iResolution.y/(nbSphere+1.0));

    vec2 center;
    vec4 color;

    for(float i = 1.0; i<=nbSphere; i += 1.0)
    {
        center = beginCenter * i;
        color = beginColor + stepColor*(i-1.0);

        drawSphere(fragColor, fragCoord, center, color, radius);
    }
}

