///point(global time, uv, center of the point, xScale, yScale, )
float point(float time, vec2 _uv, vec2 pos, float xs, float ys, float timeAdd, float size)
{
   float hypo = distance(vec2(pos.x * sin(time+(timeAdd*2.0*PI))*xs,
                              pos.y * cos(time+(timeAdd*2.0*PI))*ys),_uv);
   return size / pow(length (hypo),1.8);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 	
    /*
	uv = -1 -- 0 -- 1
	*/
	vec2 uv = (fragCoord.xy - (iResolution.xy/2.0)) / iResolution.xy;
	uv *= 2.0; 

    vec2 mouse = (iMouse.xy - (iResolution.xy/2.0)) / iResolution.xy;
    mouse *= 2.0;   
    float col = 0.0;

    //Add Points
    col += point(iTime,uv,mouse, 0.1,0.1,0.0,POINT_SIZE);
    col += point(iTime,uv,vec2(0.5,0.5), 0.5,0.3,0.0,POINT_SIZE);
    col += point(iTime,uv,vec2(0.5,0.5), 0.3,0.5,0.1,POINT_SIZE);
    col += point(iTime,uv,vec2(0.5,0.5), 0.5,0.3,0.2,POINT_SIZE);
    col += point(iTime,uv,vec2(0.5,0.5), 0.3,0.5,0.3,POINT_SIZE);
    col += point(iTime,uv,vec2(0.5,0.5), 0.5,0.3,0.4,POINT_SIZE);
    col += point(iTime,uv,vec2(0.5,0.5), 0.3,0.5,0.5,POINT_SIZE);
    col += point(iTime,uv,vec2(0.5,0.5), 0.5,0.3,0.6,POINT_SIZE);
    col += point(iTime,uv,vec2(0.5,0.5), 0.3,0.5,0.7,POINT_SIZE);
    col += point(iTime,uv,vec2(0.5,0.5), 0.5,0.3,0.8,POINT_SIZE);
    col += point(iTime,uv,vec2(0.5,0.5), 0.3,0.5,0.9,POINT_SIZE);

    col += point(iTime,uv,vec2(0.5,0.5), 1.5,1.5,0.0,POINT_SIZE);
    col += point(iTime,uv,vec2(0.5,0.5), 1.5,1.5,0.25,POINT_SIZE);
    col += point(iTime,uv,vec2(0.5,0.5), 1.5,1.5,0.5,POINT_SIZE);
    col += point(iTime,uv,vec2(0.5,0.5), 1.5,1.5,0.75,POINT_SIZE);

    col += point(iTime,uv,vec2(0.5,0.5), 1.0,2.5,0.0,POINT_SIZE);
    col += point(iTime,uv,vec2(0.5,0.5), 2.5,-1.0,0.25,POINT_SIZE);
    col += point(iTime,uv,vec2(0.5,0.5), 1.0,2.5,0.5,POINT_SIZE);
    col += point(iTime,uv,vec2(0.5,0.5), 2.5,-1.0,0.75,POINT_SIZE);

    col -= point(iTime,uv,vec2(0.5,0.5), 0.5,0.4,0.0,POINT_SIZE*0.8);
    col -= point(iTime,uv,vec2(0.5,0.5), 0.5,-0.2,0.25,POINT_SIZE*0.8);
    col -= point(iTime,uv,vec2(0.5,0.5), 0.5,0.2,0.5,POINT_SIZE*0.8);
    col -= point(iTime,uv,vec2(0.5,0.5), 0.5,-0.4,0.75,POINT_SIZE*0.8);

