    col += point(iTime,uv,vec2(0.5,0.5), -5.5,-0.2,0.0,POINT_SIZE*2.0);

    col *= BRIGHTNESS;

	fragColor = vec4(COLOR.r * col, COLOR.g * col, COLOR.b* col,1.0);
}

const float epsilon = 0.0001;
const float speed = 10.0;
vec3 light;

float scene(vec3, inout int);
bool march (out vec3, in vec3, in vec3, inout int);
vec3 calc_gradient(in vec3);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    light = vec3(2.0 * sin(iTime/12.0 * speed), 2.0 * cos(iTime/12.0 * speed), 0.0)/1.5;

    vec3 cameraPosition = vec3(0.0, 0.0, -3.0);
    vec3 cameraFocalPoint = cameraPosition - vec3(0.0, 0.0, 0.3);
    cameraPosition.xy += (fragCoord.xy - iResolution.xy/2.0) / iResolution.xx;

    vec3 norm = cameraPosition - cameraFocalPoint;
    vec3 hitPosition = cameraPosition;
    int id = 1;

    for(int i = 0; i < 5; i++) {
    	cameraPosition = hitPosition;

        bool hit = march(hitPosition, hitPosition + 2.0*norm*epsilon, norm, id);

        norm = calc_gradient(hitPosition);
        vec3 lm = normalize(cameraPosition - hitPosition);
        norm = normalize(2.0 * dot(lm, norm) * norm - lm);

        if(!hit) {
            fragColor*=0.0;
            return;
        }


        if(id == 0){
            fragColor = vec4 (1.0, 1.0, 1.0, 1.0);
            return;
        }
        if(id == 1){ 
            fragColor = vec4 (1.0, 0.0, 0.0, 1.0);
            break;
        }
        if(id == 2){
            fragColor = vec4 (1.0, 1.0, 1.0, 1.0);
            continue;
        }
        if(id == 3){ 
            fragColor = vec4 (0.0, 1.0, 1.0, 1.0);
            break;
        }
        if(id == 4){
            fragColor = vec4 (0.0, 0.0, 1.0, 1.0);
            continue;
        }
        if(id == 5){
        	fragColor = vec4 (0.0, 1.0, 0.0, 1.0);
            break;
        }
    }


    vec3 normal = calc_gradient(hitPosition);
    vec3 lm = normalize(light - hitPosition);
    vec3 rm = normalize(2.0 * dot(lm, normal) * normal - lm);
    vec3 v = normalize(cameraPosition - hitPosition);

    vec3 lightEnd;
    int occId;

    march(lightEnd, hitPosition + 2.0*lm*epsilon + 2.0*normal*epsilon, lm, occId);

    vec3 lightcoefficient = vec3(0.2, 0.2, 0.2);

    if(length(lightEnd-light) < 0.1) {
		lightcoefficient += 
            0.35 * dot(lm, normal) + 
            0.4 * pow(clamp(dot(rm, v),0.0,1.0), 4.0);
    }

    fragColor.xyz *= lightcoefficient;
}

float max3(float a, float b, float c) {
    return max(a, max(b, c));
}

vec3 calc_gradient(vec3 p) {
    vec3 delta = vec3(epsilon, 0.0, 0.0);
    int id;
    float t = scene(p,id);
 	return normalize(vec3 (
        (scene(p + delta.xyz,id) - t), 
        (scene(p + delta.yxz,id) - t), 
        (scene(p + delta.zyx,id) - t)
   	));

}

bool march(out vec3 endPosition, in vec3 startPosition, in vec3 direction, inout int id) {

    vec3 mdir = normalize(direction);
    endPosition = startPosition;

    for(int i = 0; i < 400; i++) {
        float distance = scene(endPosition,id);

        if(distance < epsilon)
            return true;

        if(distance > 1000.0)
            return false;

        endPosition += distance * mdir;
    }

    return false;

}

float scene (vec3 p, inout int id) {
	float s1 = length(p) - 0.5;
    float s2 = length(p - vec3(1.0 * sin(-iTime*0.025 * speed), 2.0*cos(iTime*0.025 * speed), 0.0)) - 0.4;
    float l = length(p-light)-0.05;
    float b = max(abs(p.x - 2.0), max(abs(p.y),abs(p.z))) - 0.5;
    float c = max3(
    	abs(p.x + 2.0) - 0.4,
    	abs(p.y - 0.0) - 3.0,
    	abs(p.z - 0.0) - 4.0
    );
    float d = max3(
    	abs(p.x + 0.0) - 8.0,
    	abs(p.y + 4.5) - 2.0,
    	abs(p.z - 0.0) - 4.0
    );

    if (l < s1 && l < s2 && l < b && l < c && l < d) id = 0;
    else if(s1 < s2 && s1 < b && s1 < c && s1 < d) id = 1;
    else if(s2 < b && s2 < c && s2 < d) id = 2;
    else if(b < c && b < d) id = 3;
    else if(c < d) id = 4;
    else id = 5;

    return min(s1,min(s2,min(l,min(b,min(c,d)))));
}

#define N 10
#define M 5
#define K 24
#define M_PI 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspectRatio = iResolution.x / iResolution.y;

    float h = 1.0;
    float w = aspectRatio;

    vec2 uv = vec2(fragCoord.x / iResolution.x * aspectRatio, fragCoord.y / iResolution.y); 

    float radius = 0.01;
    float orbitR = 0.02;
    float orbiterRadius = 0.005;
    float centerRadius = 0.002;
    float encloseR = 2.0 * orbitR;
    float encloserRadius = 0.002;
    float spacingX = (w / (float(N) + 1.0));
    float spacingY = h / (float(M) + 1.0);
    float x = 0.0;
    float y = 0.0;
    vec4 totalLight = vec4(0.0, 0.0, 0.0, 1.0);
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < M; j++) { 
            // compute the center of the diagram
            vec2 center = vec2(spacingX * (float(i) + 1.0), spacingY * (float(j) + 1.0));
            x =  center.x + orbitR * cos(iTime );
            y =  center.y + orbitR * sin(iTime );
            vec2 bulb = vec2(x,y);
            if (length(uv - center) > encloserRadius + encloseR) {
                continue;
            } else if (length(uv - center) < centerRadius) {
                // frag intersects white center marker                   
                fragColor = vec4(1.0);
                return;               
            } else if (length(uv - bulb) < radius) {
                // intersects rotating "light"
                fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
                return;
            } else {
                // intersects one of the enclosing 24 cylinders
                for(int k = 0; k < K; k++) {
                    float theta = M_PI * 2.0 * float(k)/ float(K);
                    x = center.x + cos(theta) * encloseR;
                    y = center.y + sin(theta) * encloseR;
                    vec2 encloser = vec2(x,y);
                    if (length(uv - encloser) < encloserRadius) {
                        fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
                    	return;
                    }
                }   
            }
        }
    }
	fragColor = vec4(0.0);

}

#define PI 3.14159265359
