float createShape(vec2 position, float numOfCorners)
{
    float a = atan(position.x, position.y) + PI;
	float r = TWO_PI/float(numOfCorners);

    float dist = cos(floor(.5+a/r)*r-a)*length(position);
    return dist;
}

mat2 rotate2d(float angle)
{
    return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}

mat2 scale2d(float value)
{
    return mat2(value, 0, 0,value);
}

float circle(vec2 uv, vec2 center, float radius)
{
    float bilog = step(distance(center,uv), radius);
    return bilog;
}

float rectangle(vec2 uv, vec2 position, vec2 size, float border)
{
    float leftSide = position.x;
    float rightSide = position.x + size.x;
    float bottom = position.y;
    float top = position.y + size.y;

    //Border
    float rect = step(leftSide, uv.x) - step(rightSide, uv.x); 
    float rect2 = step(bottom, uv.y) - step(top, uv.y);

    float rect3 = step(leftSide + border , uv.x) - step(rightSide - border, uv.x); 
    float rect4 = step(bottom + border, uv.y) - step(top - border, uv.y);

    return (rect * rect2) - (rect3 * rect4);
}

float rectangle2(vec2 uv, vec2 position, vec2 size, float border)
{
    float leftSide = position.x;
    float rightSide = position.x + size.x;
    float bottom = position.y;
    float top = position.y + size.y;

    float rect = step(leftSide, uv.x) - step(rightSide, uv.x); 
    float rect2 = step(bottom, uv.y) - step(top, uv.y);

    return (rect * rect2);
}

