// Alexander Lemke, 2017

//////////////////////////////////////////////////
// Settings
#define     NUMBER_OF_STEPS         48
#define 	AA 						4

//////////////////////////////////////////////////
// Constants
const float     MAX_DISTANCE    = 300.0;
const float     EPSILON         = 0.001;
const float     PI              = 3.14159265359;
const float 	CUBE 			= 2.0;
const float 	GROUND 			= 1.0;
const vec3 		BoxCenter 		= vec3(0.0, -2.0, 0.0);
const vec3 		BoxSize 		= vec3(3.5, 10.0, 3.5);

//////////////////////////////////////////////////
// Globals
vec3 gCameraPosition;

//////////////////////////////////////////////////
// Helpers
float Saturate(in float f)
{
    return clamp(f, 0.0, 1.0);
}

mat3 Create3x3RotationMatrix(in vec3 axis, in float angle)
{
    axis = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float oc = 1.0 - c;

    return mat3(oc * axis.x * axis.x + c,          oc * axis.x * axis.y - axis.z * s, oc * axis.z * axis.x + axis.y * s,
                oc * axis.x * axis.y + axis.z * s, oc * axis.y * axis.y + c,          oc * axis.y * axis.z - axis.x * s,
                oc * axis.z * axis.x - axis.y * s, oc * axis.y * axis.z + axis.x * s, oc * axis.z * axis.z + c);
}

float DrawCharacter(inout vec2 p, in int c)
{
    float fC = float(c);
    float color = 0.0;
	if(p.x >= 0.0 && p.x <= 1.0 && p.y >= 0.0 && p.y <= 1.0)
    {
        color = step(texture(iChannel1, p / 16.0 + fract(floor(vec2(fC, 15.99 - fC / 16.0)) / 16.0)).a, 0.5);
    }
    p.x -= 0.5;
    return color;
}

#define DrawNone(c, x) character += DrawCharacter(uv, 78); character += DrawCharacter(uv, 111); character += DrawCharacter(uv, 110); character += DrawCharacter(uv, 101)
#define DrawNormal(c, x) c += DrawCharacter(x, 78); c += DrawCharacter(x, 111); c += DrawCharacter(x, 114); c += DrawCharacter(x, 109); c += DrawCharacter(x, 97); c += DrawCharacter(x, 108)
#define DrawMapping(c, x) c += DrawCharacter(x, 77); c += DrawCharacter(x, 97); c += DrawCharacter(x, 112); c += DrawCharacter(x, 112); c += DrawCharacter(x, 105); c += DrawCharacter(x, 110); c += DrawCharacter(x, 103)
#define DrawParallax(c, x) c += DrawCharacter(x, 80); c += DrawCharacter(x, 97); c += DrawCharacter(x, 114); c += DrawCharacter(x, 97); c += DrawCharacter(x, 108); c += DrawCharacter(x, 108); c += DrawCharacter(x, 97); c += DrawCharacter(x, 120)
#define DrawSteep(c, x) character += DrawCharacter(uv, 83); character += DrawCharacter(uv, 116); character += DrawCharacter(uv, 101); character += DrawCharacter(uv, 101); character += DrawCharacter(uv, 112)

//////////////////////////////////////////////////////
// Intersection Helpers
struct IntersectionData
{
    float       mT;
    float       mMaterial;
};

IntersectionData GetClosestIntersection(in IntersectionData a, in IntersectionData b)
{
    if(a.mT < b.mT)
    {
        return a;
    }
    return b;
}

struct Ray
{
    vec3    mPosition;
    vec3    mDirection;
}; 

//////////////////////////////////////////////////////
// Basic Distance Field Tests
// http://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdfBox(in vec3 p, in vec3 b)
{
  vec3 d = abs(p) - b;
  return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

float sdfPlane(in vec3 p, in vec4 n)
{
	return dot(p, n.xyz) + n.w; // normal must be normalized
}

//////////////////////////////////////////////////////
// Scene Elements
IntersectionData CheckSceneForIntersection(in vec3 p)
{
    IntersectionData planeIntersectionData = IntersectionData(sdfPlane(p, vec4(0.0, 1.0, 0.0, 2.0)), GROUND);  
    IntersectionData cubeIntersectionData = IntersectionData(sdfBox(p + BoxCenter, BoxSize), CUBE);

    return GetClosestIntersection(planeIntersectionData, cubeIntersectionData);
}

IntersectionData Intersect(in Ray initialRay)
{    
    IntersectionData sceneIntersection = IntersectionData(MAX_DISTANCE, -1.0);

    float t = 0.0;   
    for(int i = 0; i < NUMBER_OF_STEPS; ++i)
    {
        // Break out if our step size is too small or we've gone out of range
        if(sceneIntersection.mT < EPSILON || t > MAX_DISTANCE) break;

        Ray currentRay = Ray(initialRay.mPosition + initialRay.mDirection * t, initialRay.mDirection); // Update our ray     
        sceneIntersection = CheckSceneForIntersection(currentRay.mPosition);   
        t += sceneIntersection.mT;
    }   
    sceneIntersection.mT = t;

    return sceneIntersection;
}

//////////////////////////////////////////////////////
// Normals
vec3 GetNormal(in vec3 point) 
{
    IntersectionData d0 = CheckSceneForIntersection(point);
    IntersectionData dX = CheckSceneForIntersection(point - vec3(EPSILON, 0.0, 0.0));
    IntersectionData dY = CheckSceneForIntersection(point - vec3(0.0, EPSILON, 0.0));
    IntersectionData dZ = CheckSceneForIntersection(point - vec3(0.0, 0.0, EPSILON));
    return normalize(vec3(dX.mT - d0.mT, dY.mT - d0.mT, dZ.mT - d0.mT));
}

float SampleTexture(in vec2 uv)
{
    return texture(iChannel0, uv).r;
}

vec3 SampleNormalMap(in vec2 uv, in float height)
{
    const float strength = 40.0;    
    float d0 = SampleTexture(uv.xy);
    float dX = SampleTexture(uv.xy - vec2(EPSILON, 0.0));
    float dY = SampleTexture(uv.xy - vec2(0.0, EPSILON));
    return normalize(vec3((dX - d0) * strength, (dY - d0) * strength, 1.0));
}

vec3 CalculateNormalMapNormal(in vec2 uv, in float height, in vec3 normal, in vec3 tangent, in vec3 binormal)
{   
    vec3 normalMap = SampleNormalMap(uv, height).rgb;
	return normalize((normal * normalMap.b) + (binormal * normalMap.g) + (tangent * normalMap.r));
}

//////////////////////////////////////////////////////
// Lighting
vec3 CalculateLighting(in vec3 point, in vec3 normal, in vec3 eye, in vec3 albedo, in float ambient)
{       
    return (albedo * Saturate(dot(normal, vec3(0.0, -1.0, 0.25)))) + (albedo * ambient);
}

vec3 ParallaxMapping(in vec3 position, inout vec3 normal, in float d, in int toggle)
{
    vec3 toBox = -position - BoxCenter;
    vec3 uvBoxSize = vec3(BoxSize.x, 3.5, BoxSize.z);

    vec2 textureCoords = (abs(normal.x) == 1.0) ? (toBox.zy / uvBoxSize.zy) : vec2(0.0);
    textureCoords += (abs(normal.y) == 1.0) ? (toBox.xz / uvBoxSize.xz) : vec2(0.0);
    textureCoords += (abs(normal.z) == 1.0) ? (toBox.xy / uvBoxSize.xy) : vec2(0.0);
    textureCoords = (textureCoords * 0.5 + 0.5) + vec2(0.0, 0.5);   

    float height = SampleTexture(textureCoords);

    vec3 tangent = normalize(dFdy(textureCoords).y * dFdx(position) - dFdx(textureCoords).y * dFdy(position));
    vec3 temp = cross(normal, tangent);
    tangent = cross(temp, normal);
    tangent = normalize(tangent);
    vec3 binormal = cross(-tangent, normal);

    vec3 viewTangentSpace = normalize(gCameraPosition - position) * mat3(tangent, binormal, normal);

    const float scale = 0.04;
    const float bias = 0.02; 
	vec2 textureOffset = (viewTangentSpace.xy * (height * scale - bias)) / viewTangentSpace.z;

    // Steep Parallax Mapping and POM
    const float numberOfSamples = 10.0;
	const float stepSize = 1.0 / numberOfSamples;
	vec2 deltaOffset = textureOffset / numberOfSamples;
	float currentLayerDepth = 0.0;
    float currentDepth = 0.0;       

    if ((toggle == 3) || (toggle == 4))  // Steep Parallax Mapping or POM
    {   
        for(float i = 0.0; i <= numberOfSamples; ++i)
        {
            currentDepth -= stepSize;           
        	textureCoords += deltaOffset;
            height = SampleTexture(textureCoords);

            if(currentDepth < height) 
            {	
                break;
            }
        }

        if (toggle == 4) // POM
        {
            vec2 previousTextureCoords = textureCoords - deltaOffset;
            float collisionDepth = height - currentDepth;
            float previousDepth = SampleTexture(previousTextureCoords) - currentDepth - stepSize;

            float weight = collisionDepth / (collisionDepth - previousDepth);
            textureCoords = mix(textureCoords, previousTextureCoords, weight);    
            height = SampleTexture(textureCoords);
        }
    }
    else if(toggle == 2) // Parallax Mapping  
    {
        textureCoords = textureCoords + textureOffset; 
        height = SampleTexture(textureCoords);
    }

    // Final Display
    if((toggle >= 1) && (toggle <= 4))
    {
        normal = CalculateNormalMapNormal(textureCoords, height, normal, tangent, binormal);        
    }
    return SampleTexture(textureCoords) * vec3(0.85, 0.85, 1.0);
}

//////////////////////////////////////////////////////
// Implementation
vec4 DisplayScene(in vec3 cameraPosition, in vec3 cameraDirection, in vec2 uv)
{
    int toggle = int(floor(mod(iTime, 5.0)));
    Ray cameraRay = Ray(cameraPosition, cameraDirection);

    vec3 finalColor = vec3(0.0);

    IntersectionData intersection = Intersect(cameraRay);        
    if(intersection.mT < MAX_DISTANCE)
    {   
        vec3 intersectionPoint = (cameraRay.mPosition + cameraRay.mDirection * intersection.mT);
        vec3 normal = GetNormal(intersectionPoint);

        vec3 diffuse = vec3(1.0);
        if(intersection.mMaterial == CUBE)
        {
			diffuse = ParallaxMapping(intersectionPoint, normal, intersection.mT, toggle);
        }
        else if(intersection.mMaterial == GROUND)
        {
            diffuse = vec3(0.5) + mod(floor(0.001 * intersectionPoint.z) + floor(0.001 * intersectionPoint.x), 2.0) * 0.1;
        }
        finalColor = CalculateLighting(intersectionPoint, normal, cameraPosition, diffuse, 0.2);     
    }
    finalColor = mix(finalColor, vec3(0.85, 0.85, 1.0), 1.0 - exp(-intersection.mT * 0.015));


    float character = 0.0;
    if(toggle == 0)
    {
        DrawNone(character, uv);
    }
    else if(toggle == 1)
    {
        DrawNormal(character, uv);
        uv.x -= 0.5;
        DrawMapping(character, uv);
    }
    else if(toggle == 2)
    {
        DrawParallax(character, uv);
        uv.x -= 0.5;
        DrawMapping(character, uv);
    }
    else if(toggle == 3)
    {
        DrawSteep(character, uv);
        uv.x -= 0.5;
        DrawParallax(character, uv);
        uv.x -= 0.5;
        DrawMapping(character, uv);
    }
    else if(toggle == 4)
    {
        character += DrawCharacter(uv, 80); 
        character += DrawCharacter(uv, 79); 
        character += DrawCharacter(uv, 77);
    }
    return vec4(mix(finalColor, vec3(1.0), character), 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{    
    vec4 finalColor = vec4(0.0);

    float xRot = (iMouse.z > 0.0) ? (iMouse.y / iResolution.y - 0.5) * (PI * 0.5) : 0.0;
    mat3 xRotMatrix = Create3x3RotationMatrix(vec3(1.0, 0.0, 0.0), xRot);
    float yRot = (iMouse.z > 0.0) ? (iMouse.x / iResolution.x) * (PI * 2.0) : (iTime * PI) * 0.02;
    mat3 yRotMatrix = Create3x3RotationMatrix(vec3(0.0, -1.0, 0.0), yRot);

    const float originDistance = 8.0;
    gCameraPosition = vec3(originDistance * sin(yRot) * cos(xRot), originDistance * sin(xRot), originDistance * cos(yRot) * cos(xRot)) + vec3(0.0, 4.0, 0.0);
    vec2 offset = vec2(0.0);

#if (AA > 1)
    for(int x = 0; x < AA; ++x)
    {
    	for(int y = 0; y < AA; ++y)
        {
    		offset = vec2(float(x), float(y)) / float(AA) - 0.5; 	 
#endif // (AA > 1)
            vec2 screenCoord = ((fragCoord.xy + offset) / iResolution.xy);
            vec2 aspectRatioAdjustedUVs = vec2((screenCoord.x * 2.0 - 1.0) * (iResolution.x / iResolution.y), (screenCoord.y * 2.0 - 1.0));

            // Determine our camera info          
            vec3 cameraDirection = normalize(yRotMatrix * xRotMatrix * normalize(vec3(aspectRatioAdjustedUVs, -1.0)));                   
            finalColor += DisplayScene(gCameraPosition, cameraDirection, ((fragCoord.xy + offset) / iResolution.yy) * 8.0);
#if (AA > 1)
    	}
    }
	finalColor /= float(AA * AA);        
#endif // (AA > 1)     
	fragColor = finalColor;
}

const int ox = 0x4F58;
const int cs = 0x4353;
const vec3 light_blue = vec3(0.8, 0.84, 0.88);
//const vec3 inv_lb = 1.0 - light_blue;
const vec3 dark_blue = vec3(0.13, 0.3, 0.53);
//const vec3 inv_db = 1.0 - dark_blue;
const vec4 white = vec4(1.0, 1.0, 1.0, 1.0);

/**
 * Draw a circle at vec2 `pos` with radius `rad` and
 * color `color`.
 */
vec4 circle(vec2 uv, vec2 pos, float rad, vec3 color) {
	float d = length(pos - uv) - rad;
	float t = clamp(d, 0.0, 1.0);
	return vec4(color, 1.0 - t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ssize = min(iResolution.x, iResolution.y) / 4.0;
    vec2 a = vec2(fragCoord.x, ssize * 4.0 - fragCoord.y);
	vec2 gloc = floor(a.xy / ssize);
	vec2 rloc = mod(a.xy, ssize);

	int pos = int(floor(gloc.y + 4.0*gloc.x + 0.5));
    float dist = length(rloc - vec2(ssize/2.0, ssize/2.0));

    if (gloc.x < 4.0) {
        vec3 oxcolor, cscolor;    
        if (pos == 1 || pos / 4 == 1 || pos == 9 || pos == 11 || pos == 12) {
            oxcolor = dark_blue;
        } else {
            oxcolor = light_blue;
        }

        if (pos == 1 || pos == 6 || pos == 7 || pos == 9 || pos == 11 || pos == 14 || pos == 15) {
            cscolor = dark_blue;
        } else {
            cscolor = light_blue;
        }

        vec4 ox_circle_layer = circle(rloc, vec2(ssize/2.0, ssize/2.0), ssize/3.0, oxcolor);
        vec4 cs_circle_layer = circle(rloc, vec2(ssize/2.0, ssize/2.0), ssize/3.0, cscolor);

        vec4 oxpart = mix(white, ox_circle_layer, ox_circle_layer.a);
        vec4 cspart = mix(white, cs_circle_layer, cs_circle_layer.a);
        fragColor = mix(oxpart, cspart, abs(sin(iTime/3.0)));
    } else {
        fragColor = vec4(0.0);
    }
}

// Created by Stephane Cuillerdier - @Aiekick/2016
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// Tuned via XShade (http://www.funparadigm.com/xshade/)

// count radial section. 
// with this you can have more section. 
// more sections need more id check, but the tech is exactly the same
const float sections = 4.; 	

// some varas
float time = 0.;				// time
float cid = 0., lid = 0.; 	// current id, last id
mat3 m1;						// matrix used by the pattern merging
mat3 m2;						// matrix used by the pattern merging

// Matrix operations
mat3 getRotXMat(float a){return mat3(1.,0.,0.,0.,cos(a),-sin(a),0.,sin(a),cos(a));}
mat3 getRotYMat(float a){return mat3(cos(a),0.,sin(a),0.,1.,0.,-sin(a),0.,cos(a));}
mat3 getRotZMat(float a){return mat3(cos(a),-sin(a),0.,sin(a),cos(a),0.,0.,0.,1.);}

// tunnel and cam path
vec2 path(float t)
{
    return vec2(2., 5.1) * 1.;
}

// continuous angle from atan
float cAtan(vec2 uv)
{
	float a = 0.;
	if (uv.x >= 0.) a = atan(uv.x, uv.y);
    if (uv.x < 0.) a = 3.14159 - atan(uv.x, -uv.y);
    return a;
}

// return id of region 
float GetID(vec2 uv) 
{
    return cAtan(uv) * floor(sections) * .5 / 3.14159;
}

// one pettern for each ID
// here id is from range 0 to 1
// so i use Id > section numbers to have continuity
float pattern(vec3 p, mat3 m, float s, float id)
{
	float r = 0.;
	p = abs(fract(p*m*s) - 0.5);
	if (id > 3.) r= max(min(abs(p.x),abs(p.z)),abs(p.y));
    else if (id > 2.) r= max(p.x,abs(p.y)+p.z);
	else if (id > 1.) r= length(p);
    else if (id > 0.) r= max(p.x,-p.y);
	return r;
}

// redirect dispalce func according to ID
// here id is from range 0 to 1
// so i use Id > section numbers to have continuity
float displace(vec3 p, float id)
{
	vec3 a = vec3(0), b = a;
	if (id > 3.) a = vec3(-0.32,0.5,.5), b = vec3(0);
    else if (id > 2.) a = vec3(0.46,0.42,-1.5), b = vec3(0);
    else if (id > 1.) a = vec3(0.36,0.2,-2.28), b = vec3(0.36,0.24,1.62);
	else if (id > 0.) a = vec3(0.62,0.62,-1.02), b = vec3(0);

    return 
        (1.-min(pattern(p, m1, a.x, id), pattern(p, m2, a.y, id))) * a.z + 
    	(1.-min(pattern(p, m1, b.x, id), pattern(p, m2, b.y, id))) * b.z;
}

float smin( float a, float b )
{
	float k = 1.;
	float h = clamp( 0.5 + 0.5*(b-a)/k, 0.0, 1.0 );
	return mix( b, a, h ) - k*h*(1.0-h);
}

vec4 map(vec3 p)
{
	p.xy -= path(p.z);													// tunnel path

    // mix from displace of last section id with displace of current section id accroding to id range 
    float r = mix(displace(p, lid), displace(p, cid), fract(cid)); 	// id range [0-1]

    p *= getRotZMat(p.z*0.05);

    p = mod(p, 10.) - 5.;

    return vec4(abs(p.y)+2. - 1. + r, p);
}

vec3 nor( vec3 pos, float k)
{
	vec3 eps = vec3( k, 0., 0. );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}

// return color from temperature 
//http://www.physics.sfasu.edu/astro/color/blackbody.html
//http://www.vendian.org/mncharity/dir3/blackbody/
//http://www.vendian.org/mncharity/dir3/blackbody/UnstableURLs/bbr_color.html
vec3 blackbody(float Temp)
{
	vec3 col = vec3(255.);
    col.x = 56100000. * pow(Temp,(-3. / 2.)) + 148.;
   	col.y = 100.04 * log(Temp) - 623.6;
   	if (Temp > 6500.) col.y = 35200000. * pow(Temp,(-3. / 2.)) + 184.;
   	col.z = 194.18 * log(Temp) - 1448.6;
   	col = clamp(col, 0., 255.)/255.;
    if (Temp < 1000.) col *= Temp/1000.;
   	return col;
}

// get density of the df at surfPoint
// ratio between constant step and df value
float SubDensity(vec3 surfPoint, float prec, float ms) 
{
	vec3 n;
	float s = 0.;
    const int iter = 8;
	for (int i=0;i<iter;i++)
	{
		n = nor(surfPoint,prec); 
		surfPoint = surfPoint - n * ms; 
		s += map(surfPoint).x;
	}
	return 1.-s/(ms*float(iter)); // s < 0. => inside df
}

float SubDensity(vec3 p, float s) 
{
	vec3 n = nor(p,s); 							// precise normale at surf point
	return map(p - n * s).x;						// ratio between df step and constant step
}

// from shane sahders
// Tri-Planar blending function. Based on an old Nvidia writeup:
// GPU Gems 3 - Ryan Geiss: http://http.developer.nvidia.com/GPUGems3/gpugems3_ch01.html
vec3 tex3D( sampler2D tex, in vec3 p, in vec3 n )
{
    n = max((abs(n) - .2)*7., .001);
    n /= (n.x + n.y + n.z );  
	p = (texture(tex, p.yz)*n.x + texture(tex, p.zx)*n.y + texture(tex, p.xy)*n.z).xyz;
    return p*p;
}

// from shane sahders
// Texture bump mapping. Four tri-planar lookups, or 12 texture lookups in total. I tried to 
// make it as concise as possible. Whether that translates to speed, or not, I couldn't say.
vec3 doBumpMap( sampler2D tx, in vec3 p, in vec3 n, float bf)
{
    const vec2 e = vec2(0.001, 0);
    // Three gradient vectors rolled into a matrix, constructed with offset greyscale texture values.    
    mat3 m = mat3( tex3D(tx, p - e.xyy, n), tex3D(tx, p - e.yxy, n), tex3D(tx, p - e.yyx, n));
    vec3 g = vec3(0.299, 0.587, 0.114)*m; // Converting to greyscale.
    g = (g - dot(tex3D(tx,  p , n), vec3(0.299, 0.587, 0.114)) )/e.x; g -= n*dot(n, g);
    return normalize( n + g*bf ); // Bumped normal. "bf" - bump factor.
}

// color arangement is what i choose
// its easy to have another coloration wothout modified the lighting.
// here is an easy way i choose, but you can do alterate more as you want
// here id is from range 0 to 1
// so i use Id > section numbers to have continuity
vec4 params(vec4 f, float id)
{
	vec4 c = f;
    if (id > 3.) c = f.zwyw; 
    else if (id > 2.) c = f.zyzw; 
    else if (id > 1.) c = f.yzyx; 
    else if (id > 0.) c = f.xyzw; 
    return c;
}

vec4 shade(vec3 ro, vec3 rd, float d, vec3 lp)
{
	vec3 p = ro + rd * d;												// surface point
	float sb = SubDensity(p, 0.001, 0.1);							// deep subdensity (10 iterations)
	vec3 bb = blackbody(100.*sb+100.);								// blackbody color according to the subdensity value
	vec3 ld = normalize(lp-p); 											// light dir
	vec3 n = nor(p, .01);												// normal at surface point
	n = doBumpMap(iChannel0, -p*0.5, n, 0.015);					// use bumpmap fnc of shane here
	vec3 refl = reflect(rd,n);											// reflected ray dir at surf point 
	float amb = 0.08; 													// ambiance factor
	float diff = clamp( dot( n, ld ), 0.0, 1.0 ); 					// diffuse
	float fre = pow( clamp( 1. + dot(n,rd),0.0,1.0), 16. ); 		// fresnel
	float spe = pow(clamp( dot( refl, ld ), 0.0, 1.0 ),25.);		// specular
	float sss = 1. - SubDensity(p*0.1, 5.) * 0.5; 					// one step sub density of df
	return vec4(															// some mix of WTF :) i tried many different things and this i choose :) but maybe there is other things to do
        (diff + fre + bb.x * sss) * amb + diff * 0.5, 
        (diff + fre + bb * sb + sss * 0.3) * amb + spe * 0.6 - diff * sss * 0.05	
    );
}

// get cam 
// g will be gl_FragCoord.xy or uMouse.xy
//  si is screensize
vec3 cam(vec2 g, vec2 si, vec3 ro, vec3 cv)
{
	vec2 uv = (g+g-si)/si.y;
	vec3 cu = normalize(vec3(0,1,0) * getRotZMat(ro.z*-0.03));
  	vec3 z = normalize(cv-ro);
    vec3 x = normalize(cross(cu,z));
  	vec3 y= cross(z,x);
  	return normalize(z + uv.x*x + uv.y*y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec4 f = vec4(0);

    vec2 g = fragCoord.xy;
    vec2 si = iResolution.xy;

    mat3 mx = getRotXMat(-7.);
	mat3 my = getRotYMat(-5.);
	mat3 mz = getRotZMat(-3.);

	// matrix used by the pattern
    m1 = mx * my * mz;
    m2 = m1 * m1;

    time = iTime * 8.;

    vec3 cu = vec3(0,1,0);											// Camera Up
  	vec3 cv = vec3(path(time + .1),time + .1); 					// Camera View
    vec3 ro = vec3(path(time),time);								// Camera Origin
    ro *= getRotZMat(ro.z*0.05);
	cv *= getRotZMat(ro.z*0.05);

	vec3 lp = vec3(path(ro.z + 7.),ro.z + 7.); 					// light point
    vec3 cp = vec3(path(ro.z + 25.2),ro.z + 25.2); 		// center point for Smooth RadClick
	vec3 rd = cam(g, si, ro, cv);									// Camera Direction

	// center point for calculate section id accroding to the tunnel path
	vec2 rdID = rd.xy - normalize(cp-ro).xy;

	rdID = cam(si*0.5, si, ro, cv).xy - normalize(cp-ro).xy;

	// radial section id
    float id = 0.;//GetID(rdID); // 0 to 4

    // circular sections id 
    cid = id; 									// current section id
	lid = cid - 1.; 							// last section id
	if (lid < 0.) lid = id + sections - 1.;	// circular 

	// Ray Marching
    float s = 1.;
    float d = 1.;
    for(int i=0;i<60;i++)
    {      
        if (log(d*d/s/1e5)>0.) break;
        d += s = map(ro+rd*d).x * .6;
    }

	// Color
    f = shade(ro, rd, d, lp);

	// mix Color Arangement according to Radial Section Range 
	// mix from params of last section id with params of current section id 
	// according to the range between the two sections with fract(cid) => range 0 to 1
	f = mix(params(f, lid), params(f, cid), fract(cid)); // id range [0-1]

	// improve light a little bit
	f = f + f.x*0.3;

	// fog for hide some ray marching artifact cause by the low count iteration (60 here )
    f = mix( f, vec4(0.8), 1.0-exp( -0.001*d*d ) );

	// gamma correction for add some contrast without saturation
   	fragColor = sqrt(f*f*f*2.); // gamma correction
}

