// wave effect using curves

#define C(x)   a*cos(x-t) 
#define S(x)   a*sin(x-t)
#define X0(x,xx)  x-C(xx)

float trochoid(float x, float t, float a) 
{
    return S(X0(x,X0(x,X0(x,X0(x,X0(x,x))))));
}

