#define RS(a,b,x) (smoothstep(a-1.0, a+1.0,x)*1.0-smoothstep(b-1.0, b+1.0, x))
#define blue4 vec3(0.953,0.969,0.89)
#define red   vec3(1.00,0.38,0.227)

vec3 triangleUP(vec2 uv, vec2 center, float radius, float height){
     vec2 d = uv - center;
    return RS(0.0, height, d.y+radius) * blue4
        +(1.0-smoothstep( height-1.0-d.y-radius,height-1.0-d.y-radius, abs(d.x)))*red;
    //replace plus with * to get triangle
}

vec3 triangleDOWN(vec2 uv, vec2 center, float radius, float height){
     vec2 d = uv - center;
    return RS(-height, 0.0, d.y-radius) * blue4
        +(1.0-smoothstep( height-1.0+d.y-radius,height-1.0+d.y-radius, abs(d.x)))*red;
    //replace plus with * to get triangle
}

vec3 triangleLEFT(vec2 uv, vec2 center, float radius, float height){
     vec2 d = uv - center;
    return RS(-height, 0.0, d.x-radius) * blue4
        +(1.0-smoothstep( height-1.0+d.x-radius,height-1.0+d.x-radius, abs(d.y)))*red;
    //replace plus with * to get triangle
}

vec3 triangleRIGHT(vec2 uv, vec2 center, float radius, float height){
     vec2 d = uv - center;
    return RS(0.0, height, d.x+radius) * blue4
        +(1.0-smoothstep( height-1.0-d.x-radius,height-1.0-d.x-radius, abs(d.y)))*red;
    //replace plus with * to get triangle
}


