vec3 diamond(vec2 center, float size, vec2 fragCoord)
{
    vec2 offs = (fragCoord.xy - center) / size;
    vec2 dist = abs(offs);
    float R = -0.5 * offs.x - 0.866 * offs.y + 0.3;
    float G = -0.5 * offs.x + 0.866 * offs.y + 0.3;
    float B =  0.9 * offs.x + 0.6 * offs.y + 0.7;
    return vec3(R, G, B) * (1.0 - smoothstep(0.45, 0.5, dist.x + dist.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 bgColor = vec3(0.2, 0.1, 0.6);

    vec3 c = vec3(0.0);
    for (float i = 0.0; i < 16.0; i += 1.0) {
        float size = iResolution.y / (5.4 + 2.0 * cos(i*23.3));
        float x = iResolution.x * 0.06 * (i + 1.02 * cos(i*24.3));
        float y = iResolution.y * (0.3 + 0.1 * sin(i*2.2) + 0.0002 * iTime * size);
        c += diamond(vec2(x, y), size, fragCoord.xy);    
    }

    fragColor.rgb = bgColor + c;
}

#define time iTime

float dist(vec2 x, vec3 y) {
    float a = abs(sqrt(pow((x.x - y.x),2.0) + pow((x.y - y.y),2.0)));
    return a;

}
vec3 position(vec3 deft, vec2 alternative, float speed){
    float move = abs(sin(time * speed));
    vec2 difference = deft.xy - alternative.xy;
    vec2 factor = difference * vec2(move);
    vec2 position = deft.xy - factor;
    return vec3(position,deft.z);
}

