            float totalRadius = point[i].z * borderSize;
            float totalRadiusFixedBorder = point[i].z + fixedBorder;
            float different = sqrt(pow((uv.x - point[i].x),2.0) + pow((uv.y - point[i].y),2.0));
            vec4 main = vec4(1.0,1.0,1.0,1.0);
            if(different < point[i].z){   
           		color = main;
            } else if(different < totalRadiusFixedBorder) {
            	float radius = point[i].z;
           		vec4 diff = abs(vec4(uv,0.5+0.5*sin(iTime),1.0) -  main);
                float a = dist(uv, point[i]) - point[i].z;
            	float b = a / (totalRadiusFixedBorder - point[i].z);
            	color = main - (diff *vec4(b));
            }
        }
    return color;
}


vec4 layer2(vec4 x, vec2 y) {
    vec3 post = position(vec3(0.9, 0.9, 1.0), vec2(0.1, 0.1), 1.0);
    float size = 0.09;
    float total = size + 0.3;
    vec4 bright = x + vec4(vec3(0.4), 1.0);
    vec4 dark = x - vec4(vec3(0.9), 1.0);
    if (dist(y, post) < size){   
		return bright;
    } else if (dist(y, post) < total) {
    	float percent = (dist(y, post) - size) / (total - size);
    	vec3 difference = bright.xyz - dark.xyz;
        vec3 color = bright.xyz - (difference * pow(percent, 1.4));
        return vec4( color ,1.0);
    } else {
    	return dark;
    }

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.x;
    fragColor = layer2(layer1(uv), uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
