void mainImage(out vec4 color, in vec2 pixCoords) {   
    /* == Background == */
    color = vec4(0.0, 0.0, 0.0, 1.0);

    /* == Camera == */
    float centerX = (iResolution.x / 2.0);
   	float centerY = (iResolution.y / 2.0);

    pixCoords.x = (pixCoords.x - centerX);
    pixCoords.y = (pixCoords.y - centerY);

    /* == Red circle == */
    vec2 center = vec2(0,0);
    float radius = (iResolution.y * 25.0) / 100.0;

    /* == In the circle.. == */
    if ( sqrt(pow(pixCoords.x - center.x, 2.0) + pow(pixCoords.y - center.y, 2.0)) < radius ) {

        float z = sqrt(pow(radius, 2.0) - pow(pixCoords.x, 2.0) - pow(pixCoords.y, 2.0));
        vec3 pixel = vec3(pixCoords.x, pixCoords.y, z);

        vec3 haut = pixel / radius;
        vec3 sun = vec3(cos(iTime * 3.0), 1.0, sin(iTime * 3.0));

        vec4 colorBase = vec4(0.7, 0.2, 0.2, 1.0);
        float lum = dot(haut, sun);

    	color = colorBase * max(lum, 0.2);
    } 
}

#define cc vec2(0.5,0.5)
#define smoothness 1.0
#define SNOW_COL vec4(1.0,1.0,1.0,1.0)
#define N_LAYERS 8.0
#define SNOW_ALPHA 1.0
#define SPEED 0.3
#define bg vec4(0.8,0.8,0.9,1.0)

float smoothCircle(vec2 position,float relativeSize){
    float d = distance(cc,position)*2./relativeSize;
    if (d > 1.0){
    	return 0.0;
    }
    else{
    	return clamp(smoothness/d-smoothness,-1.0,1.0);
    }
}

// fake random stolen from https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
float randF(float n){
    return fract(sin(n) * 43758.5453123);
}

// used to determine whether to draw a flake in the grid
// the magic number is just me mashing the keyboard
bool rand2d(float i, float j, float probability){
	return  (randF(i + j*7.8124861) > probability);
}


// create the grid of circles, with a bunch missing, and fanaggle the sizes a bit
float circleGrid(vec2 position, float spacing, float dotSize){

    // idx => which dot we are showing in the grid.

    // check x and y index to see if we should draw it or not.
    float idx = floor(1./spacing * position.x);
    float yIdx = floor(1./spacing * position.y);

    // much higher than .06 causes the grid to be obvious
    if (rand2d(idx,yIdx,0.06)){
    	return 0.0;
    }

    // modify the size of the flake a bit
    float relativeSize = (0.5 + 0.5*randF(yIdx))*dotSize / spacing;

    return smoothCircle(vec2(
        fract(1./spacing*position.x),
        fract(1./spacing*position.y + yIdx)
    ),relativeSize);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uvsq = vec2(uv.x* iResolution.x/iResolution.y,uv.y);
    float amnt = 0.0;

    float rotX = 0.0;
    float rotY = 0.0;
    rotX = -2.0*iMouse.x / iResolution.x;
    rotY = -2.0*iMouse.y / iResolution.y;


    for (float i = 0.0; i < N_LAYERS; i ++){
        float p = 0.5 + ((i+1.) / N_LAYERS)*0.4;
        // small flakes in bg drawn first with lower speed

        // the only reason we add "i" is so it tweaks the noise called on this position later.
        vec2 fallPosition = vec2(
            rotX * (1.0-p) + uvsq.x + i + p*sin(iTime/2.+i)/4.*SPEED,
            rotY * (1.0-p) + i * 3.0 + uvsq.y + iTime*p/1.*SPEED
        );
    	amnt = amnt + SNOW_ALPHA * circleGrid(fallPosition, 0.06* p, 0.04* p*p); 
    }

    fragColor = mix(SNOW_COL,bg,1.0-amnt);

}

vec3  v_cam_pos = vec3(0.0, 1.0, -6.0);
vec3  v_cam_forward = vec3(0.0, 0.0, 1.0);
vec3  v_cam_right = vec3(1.0, 0.0, 0.0);
vec3  v_cam_up = vec3(0.0, 1.0, 0.0);
vec3  v_sundir = normalize(vec3(0.0, 0.7, 1.0));
float f_near = 2.3;

#define FLOOR 999
#define INFINITY 99999.0

vec3 ToRay(in vec2 fragCoord, in mat3 m)
{
    float aspect = iResolution.x / iResolution.y;
    vec2 invResolution = 1.0 / iResolution.xy;
	vec2 ndc = (fragCoord.xy * invResolution) * 2.0 - 1.0; 
	vec3 ray = vec3(aspect * ndc.x, ndc.y, f_near);

	return m * normalize(ray);
}

vec3 IdToColor(int id)
{
	if (id == FLOOR)
        return vec3(3.4, 3.4, 5.0);

    float f = float(id);

    float sf = sin(f * 2.0) * 0.5 + 0.5;
    float cf = cos(f * 3.0) * 0.5 + 0.5;

    return vec3(sf, cf, fract(sf+cf + 0.5)) * 2.0;
}

float Scene(vec3 point, out int id)
{
    vec3 offset = vec3(0.0, 3.4, 0.0);
    float distance = INFINITY;

    id = -1;

    #define ITER 6  
    for (int i = 0; i < ITER*ITER; ++i)
    {
        int h = ITER / 2;
        int a = i / ITER;
        int b = i % ITER;

        vec3 position = vec3(float(a - h), 0.0, float(b - h)) * 5.0;
        float radius = 0.9 + sin(float(a) + float(b)) * 0.5 + 0.5;

        position.y = 4.0; 


        float l2c = length(point - position) - radius;
        l2c = max(0.0, l2c);

        if (l2c < distance)
        {
        	distance = l2c;
            id = i;
        }
    }

    float t = iTime;
    float H = sin(length(point.xz) - iTime * 2.0) * 0.3;

    float h = point.y - H;

    if (h < distance)
    {
        id = FLOOR;
    	distance = h;
    }


	return distance;
}

vec4 March(vec3 start, vec3 ray, out int id)
{   
    float step = 0.0;

	for (int i = 0; i < 100; ++i)
    {
        start = start + (step * ray);
        step = Scene( start, id );

        if (step < 0.0005)
        {
            return vec4(start, float(i));
        }  

        if (step > 30.0)
        {
        	break;
        }

    }

    return vec4(0,0,0,-1);
}


float ShadowSample(vec3 pos)
{
    int id = -1;
    float shadow = 1.0;
    vec4 shadow_pos = March(pos + v_sundir * 80.0, -v_sundir, id);

    if (shadow_pos.w < 0.0)
        return 1.0;

    float distToShadowCaster = length(shadow_pos.xyz - pos.xyz);

    shadow = min(1.0, max(0.0, 1.0 - distToShadowCaster));


    return shadow;
}

float Shadow(vec3 pos)
{
    return ShadowSample(pos);
}

vec3 Ambient()
{
	return vec3(1.0, 1.0, 1.0) * 0.3;
}

vec3 Diffuse(vec3 nrm)
{
	return dot(nrm, v_sundir) * vec3(0.9, 0.2, 0.2) * 6.0;
}

vec3 Opq(vec3 pos, int id)
{
    vec2 uv = pos.xz;
    uv.x += fract(pos.y);
    uv.y += fract(pos.y);

    uv = fract(uv);
    uv *= 0.2;

    vec3 color = vec3(1,1,1);

    if (id == FLOOR)
    {
        uv *= 0.04;
    	color = texture(iChannel0, uv).xyz;
    }

    color *= IdToColor(id);

	return color;
}

vec3 Reflection(vec3 ray, vec4 pos, vec3 nrm, int surface_id)
{  
    vec3 reflected = reflect(ray, nrm);

    int id = -1;
    vec4 hit = March(pos.xyz + nrm*0.01, reflected, id);

    if (hit.w < 0.0)
		return vec3(0, 0, 0);


    return Opq(hit.xyz, id);
}

vec3 Fog(vec4 pos)
{
    if (pos.w < 0.0)
        return vec3(0.0, 0.0, 0.0);

    float distance = length(pos.xyz - v_cam_pos);

	return max(0.0, exp(-distance * 0.04 )) * vec3(1.0, 1.0, 1.0);
}

vec3 Specular(vec3 pos, vec3 nrm)
{
    vec3 VertexToEye = normalize(v_cam_pos - pos);
    vec3 LightReflect  = normalize(reflect(-v_sundir, nrm));

    float str = max(0.0, dot(VertexToEye, LightReflect));

	vec3 spc = pow(str, 30.0) * vec3(1.0, 1.0, 1.0);

    return spc;
}

float AmbientOcclusion(vec4 pos)
{
	return (1.0-clamp(pow(pos.w / 100.0, 0.2), 0.0, 1.0));
}

mat3 CameraMatrix()
{
	float radius = 30.0;
    float param = iMouse.x * 0.03;//obalTime * 0.3;

    v_cam_pos.x = sin(param) * radius;
    v_cam_pos.y = 1.0 + iMouse.y * 0.01;
    v_cam_pos.z = cos(param) * radius;

    v_cam_forward = -normalize(v_cam_pos);
    v_cam_forward.y = 0.0;
    v_cam_forward = normalize(v_cam_forward);

    v_cam_forward = -normalize(v_cam_pos);

    v_cam_right   = cross(v_cam_forward, v_cam_up);

    mat3 m;
    m[0] = v_cam_right;
    m[1] = v_cam_up;
    m[2] = v_cam_forward;

    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	mat3 cam = CameraMatrix();

    int id = -1;

    vec3 ray   = ToRay(fragCoord, cam);
    vec4 pos   = March(v_cam_pos, ray, id);



    int id_alt = -1;
    vec3 ray_A = ToRay(fragCoord + vec2(0.2, 0.0), cam);
    vec3 ray_B = ToRay(fragCoord + vec2(0.0, 0.2), cam);
    vec3 pos_A = March(v_cam_pos, ray_A, id_alt).xyz;
    vec3 pos_B = March(v_cam_pos, ray_B, id_alt).xyz;
    vec3 diff1 = normalize(pos.xyz - pos_A);
    vec3 diff2 = normalize(pos.xyz - pos_B);
    vec3 nrm = normalize(cross(diff1, diff2));

    vec3  opq = Opq(pos.xyz, id);
    vec3  rfl = Reflection(ray, pos, nrm, id);
    vec3  spc = Specular(pos.xyz, nrm);
    vec3  amb = Ambient();
    vec3  dif = Diffuse(nrm);
    vec3  fog = Fog(pos);
    float shd = Shadow(pos.xyz);

    vec3 color;

    color = (amb + dif * shd + (spc + rfl)) * opq;

    color *= AmbientOcclusion(pos);
    fragColor.xyz = color * fog;


    fragColor.w = 1.0;  

}

#define res_     iResolution
#define time_    iTime
#define pi_      3.14159265

#define crot(a)  mat2(cos(a),-sin(a),sin(a),cos(a))

vec2  domain(vec2 uv, float s);

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    // Initial calculations
    // ---------------------------------------------------------------------------
    vec2 p = domain(fragCoord, 2.);
    vec2 np = fragCoord / res_.xy;
    float sound = texture(iChannel0, vec2(-.2)).x;
    float sound_movement = 2.*smoothstep(0., 3., sound);
    // ---------------------------------------------------------------------------

    // Scene multiplication, depending on sound
    // ---------------------------------------------------------------------------
    if(sound_movement > .22) {
        p.x = mod(p.x, 3.6) - .1;
        p.y *= 2.1;
        p.x *= 1.05;
    	p.y = mod(p.y, 4.2) - 2.3;
    }
	// ---------------------------------------------------------------------------

    // Diagonal line
    // --------------------------------------------------------------------------
    float d_diagonal_line = abs(p.x - p.y) + .1*sound_movement*sin(21.*sound*p.x);
    // --------------------------------------------------------------------------

    float fq = 1.;

    // Pendulum string
    // --------------------------------------------------------------------------
    vec2 pendulum_string_domain = p;
    pendulum_string_domain -= vec2(2., 2.);
    pendulum_string_domain *= crot(sound_movement * sin(fq*time_));
    float d_vertical_line = abs(pendulum_string_domain.x) + .02*sin(11.*p.y);
    // --------------------------------------------------------------------------

    // Circle
    // --------------------------------------------------------------------------
    float movment_angle = (-3.*pi_/2. - 1. * sound_movement*sin(fq*time_));
    vec2 circle_domain = p;
    circle_domain  -= vec2(2., 1.7 + sound_movement * 2.);  
    circle_domain  += vec2(3.2 * cos(movment_angle), 3.2 * sin(movment_angle));
    float d_circle  = abs(length(circle_domain) - .5);
	float d_disk    = (length(circle_domain) - .5);
    // --------------------------------------------------------------------------

    // Light
    // ---------------------------------------------------------------------
    vec2 light = vec2(6., 2.); 
    float diffuse = 1. + max(0., dot(p, light));
    // ---------------------------------------------------------------------

    // Shading
    // ---------------------------------------------------------------------
    vec3 color = vec3(0.);

    vec3 lb = vec3(0.1, 0.4, 1.7)*.5 + .3;
    vec3 lg = vec3(0.1, 0.7, 0.1);

    color += fract(mix(lg, vec3(sin(14.*time_)*.5, .2, .4), p.x))*smoothstep(.06, .01, d_diagonal_line);

    if(pendulum_string_domain.y > -3. + sound_movement * 2.) 
        color += mix(lg, vec3(sin(14.*time_)*.5, .2, .4), pendulum_string_domain.y)*smoothstep(.06, .01, d_vertical_line);

    color += mix(lg, vec3(sin(14.*time_)*.5, .2, .4), p.x) * smoothstep(.04, .01, d_circle);
    color += mix(lb, vec3(sin(14.*time_))*.5, 2.5*length(circle_domain + vec2(-.2)) - .001)
             * smoothstep(.02, .01, d_disk);

    if(p.y > 2.01) color= vec3(0.);
    // ---------------------------------------------------------------------

    color *= .12*diffuse * (vec3(250., 250., 175.) / 255.);

    fragColor = vec4(color, 1.);
}

vec2 domain(vec2 uv, float s) {
    return (2.*uv.xy-res_.xy) / res_.y*s;
}

float circle (vec2 uv, vec2 pos, float rad, float blur) {

    float dist = length(uv-pos);    
    float circle = smoothstep(rad, rad-blur, dist);

    //if (dist < 0.2) circle = 0.4; //else circle = 0.0;

    return circle;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv - 0.5;
    uv.x = uv.x * iResolution.x / iResolution.y;

    float x = 0.2;

    float c0 = circle(uv, vec2(0.0, 0.0), 0.1, 0.01);
    float c1 = circle(uv, vec2(sin(iTime)*0.3, 0.0), 0.1, 0.01);
    float c2 = circle(uv, vec2(0.0, sin(iTime)*0.3), 0.1, 0.01);
    float c3 = circle(uv, vec2(sin(iTime)*-0.3, 0.0), 0.1, 0.01);
    float c4 = circle(uv, vec2(0.0, sin(iTime)*-0.3), 0.1, 0.01);

    float c5 = circle(uv, vec2(0.0, 0.0), 0.1, 0.01);
    float c6 = circle(uv, vec2(sin(iTime)*-x, 0.0), 0.1, 0.01);
    float c7 = circle(uv, vec2(0.0, sin(iTime)*-x), 0.1, 0.01);
    float c8 = circle(uv, vec2(sin(iTime)*x, 0.0), 0.1, 0.01);
    float c9 = circle(uv, vec2(0.0, sin(iTime)*x), 0.1, 0.01);

    vec3 c = vec3 (0.0, 1.0, 0.0)*c5;
    vec3 col = vec3(0.0, 1.0, 0.0)*((c0+c1+c2+c3+c4)-(c6+c7+c8+c9));

    fragColor = vec4(col+c, 1.0);
}

vec3 hsv2rgb(float h, float s, float v) {
	float c = v*s;
	float x = c*(1.0 - abs(mod(h/60.0, 2.0) - 1.0));
	float m = v - c;

	vec3 color;
	if(h < 60.0) {
		color = vec3(c + m, x + m, 0.0);
	}
	else if(h < 120.0) {
		color = vec3(x + m, c + m, 0.0);
	}
	else if(h < 180.0) {
		color = vec3(0.0, c + m, x + m);
	}
	else if(h < 240.0) {
		color = vec3(0.0, x + m, c + m);
	}
	else if(h < 300.0) {
		color = vec3(x + m, 0.0, c + m);
	}
	else if(h < 360.0) {
		color = vec3(c + m, 0.0, x + m);
	}
	return color;
}


//math
vec2 cmult(vec2 z1, vec2 z2) {
    return vec2(z1.x*z2.x - z1.y*z2.y, z1.x*z2.y + z1.y*z2.x);
}
vec2 cpow(vec2 z, int n) {
    vec2 ret = vec2(1.0, 0);
    int count = 0;
    for(int i = 0; i < 10; i++) {
        ret = cmult(ret, z);
        count++;
        if(count == n) break;
    }
    return ret;
}
float carg(vec2 z) {
    return atan(z.y, z.x);
}


//float smooth(vec2 z, int iter) {
	//return float(iter + 1) - log(log(length(z)))/log(2.0);
//}
float smooth2(vec2 z, int iter) {
    float s = float(iter) - log(log(length(z))/log(7.0))/log(7.0);
    return s;
}


const int maxIter = 100;
//complex system
vec3 system(vec2 c) {
    vec2 z = vec2(0);
    vec2 zp = vec2(0);
    int iter = 0;
    for(int i = 0; i < maxIter; i++) {
        zp = z;
        z = cmult(cpow(z, 7), c) + cmult(vec2(length(z)*cos(iTime*3.2), carg(z)*sin(iTime*3.3)) - c, cpow(z, 3)) + c;
        //z = cmult(z, z) + c;
        if(length(z) > 2.0) {
            iter = i;
            break;
        }
    }
    //vec4 color = vec4(hsv2rgb(240.0 - smooth2(z, iter)/float(maxIter)*240.0, 1.0, 1.0), 1.0);
    vec4 color = vec4(vec3(5.0*smooth2(z, iter)/float(maxIter)), 1.0);
    return color.xyz;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.0*fragCoord.xy - iResolution.xy) / iResolution.y;
	fragColor = vec4(system(uv), 1.0);
}

// rewrote 2018/02/01
vec3  hsv(float h,float s,float v)
{
    return((clamp(abs(fract(h+vec3(0,2,1)/3.)*6.-3.)-1.,0.,1.)-1.)*s+1.)*v;
}

mat2 rotate(float a)
{
	return mat2(cos(a), sin(a), -sin(a), cos(a));	
}

float hash(float n){return fract(sin(n) * 43758.5453123);}

float de(in vec2 p)
{
    float de1 = length(p)-0.1;
    float a = radians(72.);    
    p *= rotate(floor(0.5 - atan(p.x, p.y) /a) *a);
    float de2 = p.y + abs(p.x) * 2.5- 0.3;
    return mix(de1, de2, sin(iTime*0.5 +sin(iTime))*0.5+0.5);
}

float noise( in vec2 p)
{
    float t = iTime*0.5;
    p += sin(t + sin(3.0*t +sin(6.0*t +sin(12.0*t))));
    vec2 i = floor(p);
    float n = dot(i,vec2(1,9))+ floor(t);
    return mix(hash(n),hash(n+1.0),smoothstep(0.0,1.0,fract(t))) * smoothstep(0.9,0.1,de(fract(p)-0.5));
}

float fbm( vec2 p)
{
    float t = iTime*0.02;
    float n=0.0;
    for (int i=1;i<5;i++){
        p *= rotate(t);t*=2.0;
        n += noise(p)/pow(2.0,float(i)); p*=2.0;
    }
    return n;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*gl_FragCoord.xy-iResolution.xy) / iResolution.y;
    p *= 1.5/(1.5-pow(p.y-2.5, 2.)); // perspective    
    float t = floor(iTime*0.5);
