    // WOW! WOW! WOW! WOW! WOW! WOW! WOW! WOW! WOW! WOW! WOW! WOW! 
    // WOW! WOW! WOW! WOW! WOW! WOW! WOW! WOW! WOW! WOW! WOW! WOW! 


}

vec3 hash( vec3 p ) 
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    return mix( mix( mix( dot( hash( i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ), 
                          dot( hash( i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ), 
                          dot( hash( i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y),
                mix( mix( dot( hash( i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ), 
                          dot( hash( i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ), 
                          dot( hash( i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy *2. - 1.;

    float len = length((iMouse.xy / iResolution.xy) * 2. - 1. - uv);
    float len2 = len * len;

    vec3 v = vec3(uv * 4.3 * len2, iTime * 3.61 );

    float n=0.;

    float z = 1. + 1. / (5.+len *  115.);

    float max = 6.;

    float m = iMouse.x * 1. + iResolution.x /2.;


    for(float i = 0.01;i<1.;i+=.009) {

        v.z += 7112.*(log(1.+i*0.00003));
        v.xy *= z;
        float f = noise(v);
        if(f > .009 * (28.-len2)) {
            n = 1.0- i;
            break;
        }
    }


    fragColor = vec4(n*n*0.6*len,0.,n*n*1.0,1.);
}

// Fabrice's 2nd optomization
void mainImage( out vec4 O,  vec2 p )
{
	p *= 5. / iResolution.xy;

    vec4 a = vec4(.1,.4,.222,0) + iTime + atan(p.y, p.x), 
         b = a; b.y+=.4;
    a = cos( sin(p.x)-cos(p.y) +a ),
    b = sin( a*p.x*p.y - p.y   +b );

    a =  abs(b*b-a*a);

    O =  1.6 * pow(1.-a+a*a,  16.+a-a);
}

